// === PART 1: Basic Properties ===
var health: Int = 100
var mana: Int = 50
var gold: Int = 0
var currentLevel: Int = 1
var experience: Double = 0.0

let playerID: String = "player_" + UUID().uuidString.prefix(8)
let maximumHealth: Int = 200
let maximumMana: Int = 100

// === PART 2: Computed Properties ===
var healthPercentage: Double {
    return Double(health) / Double(maximumHealth)
}

var isAlive: Bool {
    return health > 0
}

var playerStatus: String {
    return "Level \(currentLevel) Player - \(health)/\(maximumHealth) HP (\(Int(healthPercentage * 100))%)"
}

// === PART 3: Property Observers ===
var observedHealth: Int = 100 {
    didSet {
        if observedHealth < 0 {
            observedHealth = 0
            print("Player has been defeated!")
        } else if observedHealth > maximumHealth {
            observedHealth = maximumHealth
        }

        if observedHealth > oldValue {
            print("Healed for \(observedHealth - oldValue) HP")
        } else if observedHealth < oldValue {
            print("Took \(oldValue - observedHealth) damage")
        }
    }
}

var observedGold: Int = 0 {
    didSet {
        if observedGold > oldValue {
            print("Gained \(observedGold - oldValue) gold! Total: \(observedGold)")
        } else if observedGold < oldValue {
            print("Spent \(oldValue - observedGold) gold. Remaining: \(observedGold)")
        }
    }
}

// === PART 4: Type Conversions ===
var goldDisplay: String {
    return "\(gold) gold"
}

var experienceNeeded: Double {
    return Double(currentLevel) * 1000.0
}

// === PART 5: Advanced Challenge ===
func takeDamage(amount: Int) {
    let newHealth = health - amount

    if newHealth <= 0 {
        health = 0
        print("Critical hit! Player has been defeated.")
    } else {
        health = newHealth
        print("Player took \(amount) damage. Health: \(health)/\(maximumHealth)")
    }
}

func gainExperience(amount: Double) {
    experience += amount
    print("Gained \(amount) experience. Total: \(experience)/\(experienceNeeded)")

    if experience >= experienceNeeded {
        levelUp()
    }
}

func levelUp() {
    currentLevel += 1
    experience = 0.0
    maximumHealth += 20
    health = maximumHealth
    print("Level up! Reached level \(currentLevel). Health restored to full!")
}

