// === PART 1: Basic Optional Handling ===
var username: String? = "SwiftMaster"
var email: String? = "user@example.com"
var age: Int? = 25
var phoneNumber: String? = nil

func printUserInfo() {
    if let name = username {
        print("Username: \(name)")
    } else {
        print("Username: Not set")
    }

    if let userEmail = email {
        print("Email: \(userEmail)")
    } else {
        print("Email: Not provided")
    }

    if let userAge = age {
        print("Age: \(userAge)")
    } else {
        print("Age: Unknown")
    }
}

func getUserBio() -> String? {
    guard let name = username else {
        return nil
    }

    var bio = "User: \(name)"

    if let userAge = age {
        bio += ", Age: \(userAge)"
    }

    if let userEmail = email {
        bio += ", Email: \(userEmail)"
    }

    return bio
}

// === PART 2: Optional Chaining & Collections ===
struct Address {
    var street: String?
    var city: String?
    var country: String? = "USA"
}

struct Preferences {
    var theme: String?
    var notifications: Bool? = true
}

struct UserProfile {
    var username: String?
    var address: Address?
    var preferences: Preferences?
    var friends: [String]?
}

var userProfile = UserProfile(
    username: "JohnDoe",
    address: Address(street: "123 Main St", city: nil, country: "USA"),
    preferences: Preferences(theme: "dark", notifications: true),
    friends: ["Alice", "Bob", "Charlie"]
)

func getFullAddress() -> String? {
    guard let address = userProfile.address else {
        return nil
    }

    var addressParts: [String] = []

    if let street = address.street {
        addressParts.append(street)
    }

    if let city = address.city {
        addressParts.append(city)
    }

    if let country = address.country {
        addressParts.append(country)
    }

    return addressParts.isEmpty ? nil : addressParts.joined(separator: ", ")
}

func getFirstFriend() -> String? {
    return userProfile.friends?.first
}

// === PART 3: Nil Coalescing & Default Values ===
func getDisplayName() -> String {
    return username ?? "Anonymous User"
}

func getDisplayAge() -> String {
    return age.map { "\($0) years old" } ?? "Age not specified"
}

func getUserLevel() -> Int {
    let baseLevel = 1
    let ageBonus = age.map { $0 / 10 } ?? 0  // 1 level per 10 years
    return baseLevel + ageBonus
}

struct UserSettings {
    var theme: String?
    var language: String?
    var volume: Int?

    var effectiveTheme: String {
        return theme ?? "light"
    }

    var effectiveLanguage: String {
        return language ?? "en"
    }

    var effectiveVolume: Int {
        return volume ?? 50
    }
}

// === PART 4: Advanced Optional Patterns ===
func validateEmail() -> Bool {
    return email?.contains("@") == true
}

func getDomainFromEmail() -> String? {
    return email?.split(separator: "@").last.map(String.init)
}

func createWelcomeMessage() -> String? {
    return username.map { name in
        let greeting = "Welcome, \(name)!"
        let emailPart = email.map { " We sent a confirmation to \($0)." } ?? ""
        return greeting + emailPart
    }
}

func processUserInput(_ input: String?) -> String? {
    return input?
        .trimmingCharacters(in: .whitespaces)
        .isEmpty == false ? input : nil
}

// === PART 5: Real-World Application ===
struct ValidationError {
    let message: String
}

func validateRegistration(username: String?, email: String?, password: String?) -> ValidationError? {
    guard let username = username, !username.isEmpty else {
        return ValidationError(message: "Username is required")
    }

    guard let email = email, email.contains("@") else {
        return ValidationError(message: "Valid email is required")
    }

    guard let password = password, password.count >= 8 else {
        return ValidationError(message: "Password must be at least 8 characters")
    }

    return nil
}

func registerUser(username: String?, email: String?, password: String?) -> Bool {
    if let error = validateRegistration(username: username, email: email, password: password) {
        print("Registration failed: \(error.message)")
        return false
    }

    print("User \(username!) registered successfully with email \(email!)")
    return true
}

func safeDataProcessingPipeline() -> String? {
    return username?
        .trimmingCharacters(in: .whitespaces)
        .uppercased()
        .appending(" - VERIFIED")
}

