// === IF/ELSE STATEMENTS ===
let temperature = 25
let isRaining = false

// Basic if/else
if temperature > 30 {
    print("It's hot! Stay hydrated.")
} else if temperature > 20 && !isRaining {
    print("Perfect weather for a walk!")
} else {
    print("Might want to stay indoors.")
}

// Nested conditionals
let age = 25
let hasLicense = true

if age >= 18 {
    if hasLicense {
        print("You can drive a car!")
    } else {
        print("You're old enough but need a license.")
    }
} else {
    print("You're too young to drive.")
}

// === SWITCH STATEMENTS ===
let direction = "north"

// Basic switch
switch direction {
case "north":
    print("Heading north")
case "south":
    print("Heading south")
case "east":
    print("Heading east")
case "west":
    print("Heading west")
default:
    print("Unknown direction")
}

// Switch with ranges
let examScore = 87

switch examScore {
case 90...100:
    print("Grade: A - Excellent!")
case 80..<90:
    print("Grade: B - Good job!")
case 70..<80:
    print("Grade: C - Satisfactory")
case 60..<70:
    print("Grade: D - Needs improvement")
case 0..<60:
    print("Grade: F - Failed")
default:
    print("Invalid score")
}

// Switch with tuples
let coordinates = (1, 0)

switch coordinates {
case (0, 0):
    print("At the origin")
case (_, 0):
    print("On the x-axis")
case (0, _):
    print("On the y-axis")
case (-2...2, -2...2):
    print("Inside the 5x5 box")
case let (x, y):
    print("At coordinates (\(x), \(y))")
}

// === FOR-IN LOOPS ===
// Loop through range
print("\nCounting to 5:")
for number in 1...5 {
    print(number)
}

// Loop through array
let fruits = ["Apple", "Banana", "Cherry", "Date"]
print("\nFruits:")
for fruit in fruits {
    print("- \(fruit)")
}

// Loop with index
print("\nFruits with index:")
for (index, fruit) in fruits.enumerated() {
    print("\(index + 1). \(fruit)")
}

// Loop through dictionary
let inventory = ["Sword": 5, "Shield": 3, "Potion": 10]
print("\nInventory:")
for (item, quantity) in inventory {
    print("\(item): \(quantity)")
}

// === WHILE LOOPS ===
var countdown = 3
print("\nCountdown:")
while countdown > 0 {
    print("\(countdown)...")
    countdown -= 1
}
print("Go!")

// === REPEAT-WHILE LOOPS ===
var userInput = ""
print("\nRepeat-while example:")
repeat {
    print("Type 'exit' to quit: ")
    // Simulate user input
    userInput = "exit"
} while userInput != "exit"

// === LOOP CONTROL ===
print("\nBreak and continue:")
for number in 1...10 {
    if number == 3 {
        continue // Skip 3
    }
    if number == 8 {
        break // Stop at 8
    }
    print(number)
}

// === PRACTICAL EXAMPLE: GAME MECHANICS ===
struct Player {
    var health: Int
    var mana: Int
    var level: Int
}

var player = Player(health: 100, mana: 50, level: 1)
let enemies = ["Goblin", "Orc", "Dragon"]
let enemyHealth = ["Goblin": 30, "Orc": 50, "Dragon": 100]

print("\n=== BATTLE SIMULATION ===")

// Simulate battles with different enemies
for enemy in enemies {
    print("\nA wild \(enemy) appears!")

    var currentEnemyHealth = enemyHealth[enemy] ?? 0

    while currentEnemyHealth > 0 && player.health > 0 {
        // Player attacks
        let damage = Int.random(in: 5...15) * player.level
        currentEnemyHealth -= damage
        print("You hit the \(enemy) for \(damage) damage!")

        // Enemy attacks (sometimes)
        if currentEnemyHealth > 0 && Bool.random() {
            let enemyDamage = Int.random(in: 3...8)
            player.health -= enemyDamage
            print("The \(enemy) hits you for \(enemyDamage) damage!")
        }

        // Check battle status
        switch (player.health, currentEnemyHealth) {
        case (..., 0):
            print("You defeated the \(enemy)!")
            player.level += 1
        case (0, _):
            print("You were defeated by the \(enemy)!")
        case (1...30, _):
            print("Warning: Low health!")
        default:
            break
        }
    }

    if player.health <= 0 {
        print("Game Over!")
        break
    }
}

