// === PART 1: Grade Calculator ===
func calculateLetterGrade(score: Int) -> String {
    switch score {
    case 93...100:
        return "A"
    case 90...92:
        return "A-"
    case 87...89:
        return "B+"
    case 83...86:
        return "B"
    case 80...82:
        return "B-"
    case 77...79:
        return "C+"
    case 73...76:
        return "C"
    case 70...72:
        return "C-"
    case 67...69:
        return "D+"
    case 63...66:
        return "D"
    case 60...62:
        return "D-"
    case 0...59:
        return "F"
    default:
        return "Invalid Score"
    }
}

// === PART 2: Student Analysis ===
let studentScores = [85, 92, 78, 45, 67, 95, 88, 72, 60, 81, 53, 98, 75, 64, 89]

// Calculate average
var totalScore = 0
for score in studentScores {
    totalScore += score
}
let averageScore = totalScore / studentScores.count
print("Class Average: \(averageScore)")

// Count grades
var gradeCounts = ["A": 0, "B": 0, "C": 0, "D": 0, "F": 0]

for score in studentScores {
    let grade = calculateLetterGrade(score: score)

    switch grade.first {
    case "A":
        gradeCounts["A"]! += 1
    case "B":
        gradeCounts["B"]! += 1
    case "C":
        gradeCounts["C"]! += 1
    case "D":
        gradeCounts["D"]! += 1
    case "F":
        gradeCounts["F"]! += 1
    default:
        break
    }
}

print("Grade Distribution: \(gradeCounts)")

// Find highest and lowest
var highestScore = studentScores[0]
var lowestScore = studentScores[0]

for score in studentScores {
    if score > highestScore {
        highestScore = score
    }
    if score < lowestScore {
        lowestScore = score
    }
}

print("Highest Score: \(highestScore), Lowest Score: \(lowestScore)")

// === PART 3: Grade Improvement ===
var failingStudentScore = 45
var studySessions = 0

print("\n=== STUDENT IMPROVEMENT TRACKER ===")

while failingStudentScore < 60 {
    studySessions += 1
    failingStudentScore += 5

    let currentGrade = calculateLetterGrade(score: failingStudentScore)
    print("After session \(studySessions): Score = \(failingStudentScore), Grade = \(currentGrade)")

    if failingStudentScore >= 60 {
        print("ðŸŽ‰ Student passed after \(studySessions) study sessions!")
    }
}

// === PART 4: Advanced Reporting ===
func categorizePerformance(score: Int) -> String {
    if score >= 90 {
        return "Excellent"
    } else if score >= 75 {
        return "Good"
    } else if score >= 60 {
        return "Needs Improvement"
    } else {
        return "Failing"
    }
}

func findStudentsInRange(scores: [Int], minScore: Int, maxScore: Int) -> [Int] {
    var results: [Int] = []

    for score in scores {
        if score >= minScore && score <= maxScore {
            results.append(score)
        }
    }

    return results
}

print("\n=== PERFORMANCE CATEGORIZATION ===")
for score in studentScores {
    let category = categorizePerformance(score: score)
    let grade = calculateLetterGrade(score: score)
    print("Score \(score): \(grade) - \(category)")
}

let midRangeScores = findStudentsInRange(scores: studentScores, minScore: 70, maxScore: 85)
print("\nScores between 70-85: \(midRangeScores)")

// === BONUS: Menu System ===
func displayMenu() {
    print("""
    \n=== GRADEBOOK MENU ===
    1. View All Scores
    2. Calculate Average
    3. Grade Distribution
    4. Find Students in Range
    5. Exit
    """)
}

var menuRunning = true

while menuRunning {
    displayMenu()
    let userChoice = 5 // Simulated user input - would be input in real app

    switch userChoice {
    case 1:
        print("All Scores: \(studentScores)")
    case 2:
        print("Class Average: \(averageScore)")
    case 3:
        print("Grade Distribution: \(gradeCounts)")
    case 4:
        let rangeScores = findStudentsInRange(scores: studentScores, minScore: 70, maxScore: 85)
        print("Scores between 70-85: \(rangeScores)")
    case 5:
        print("Goodbye!")
        menuRunning = false
    default:
        print("Invalid choice. Please try again.")
    }

    if userChoice != 5 {
        // In real app, you'd have a way to continue
        menuRunning = false // Simulate exit for this example
    }
}

