var book2 = book1  // This creates a COPY
book2.title = "Book B"
print(book1.title) // "Book A" - unchanged!
print(book2.title) // "Book B" - changed copy

// === CLASS EXAMPLES ===

// Basic class with inheritance
class Animal {
    var name: String
    var age: Int

    init(name: String, age: Int) {
        self.name = name
        self.age = age
    }

    func makeSound() {
        print("Some animal sound")
    }
}

class Dog: Animal {
    var breed: String

    init(name: String, age: Int, breed: String) {
        self.breed = breed
        super.init(name: name, age: age)
    }

    override func makeSound() {
        print("Woof!")
    }

    func bark() {
        print("\(name) the \(breed) is barking!")
    }
}

// Using the class
let myDog = Dog(name: "Buddy", age: 3, breed: "Golden Retriever")
myDog.makeSound() // "Woof!"
myDog.bark()      // "Buddy the Golden Retriever is barking!"

// Reference semantics demonstration
let dog1 = Dog(name: "Max", age: 2, breed: "Labrador")
let dog2 = dog1  // Both reference SAME object
dog2.name = "Charlie"
print(dog1.name) // "Charlie" - both changed!

// Identity comparison
print(dog1 === dog2) // true - same instance

// === PRACTICAL COMPARISON ===

// Struct for value semantics (good for data)
struct Coordinate {
    var latitude: Double
    var longitude: Double

    // Computed property
    var isInNorthernHemisphere: Bool {
        return latitude > 0
    }
}

// Class for reference semantics (good for shared state)
class Player {
    var name: String
    var score: Int
    var position: Coordinate

    init(name: String, score: Int = 0, position: Coordinate) {
        self.name = name
        self.score = score
        self.position = position
    }

    func moveTo(newPosition: Coordinate) {
        position = newPosition
        print("\(name) moved to (\(position.latitude), \(position.longitude))")
    }
}

// Usage example
let startPosition = Coordinate(latitude: 37.7749, longitude: -122.4194)
let player1 = Player(name: "Hero", position: startPosition)

// Copy the coordinate (value type)
var newPosition = player1.position
newPosition.latitude = 40.7128

// Player's position unchanged (value semantics)
print(player1.position.latitude) // 37.7749

// Share player reference
let player2 = player1
player2.score = 100
print(player1.score) // 100 - both changed (reference semantics)

