// === PART 1: Book Struct ===
struct Book {
    let title: String
    let author: String
    let isbn: String
    let pageCount: Int
    var isCheckedOut: Bool = false

    // Computed properties
    var description: String {
        return "\"\(title)\" by \(author) (ISBN: \(isbn))"
    }

    var canBeBorrowed: Bool {
        return !isCheckedOut
    }

    // Mutating methods
    mutating func checkOut() {
        if !isCheckedOut {
            isCheckedOut = true
            print("Book '\(title)' has been checked out")
        } else {
            print("Book '\(title)' is already checked out")
        }
    }

    mutating func returnBook() {
        if isCheckedOut {
            isCheckedOut = false
            print("Book '\(title)' has been returned")
        } else {
            print("Book '\(title)' was not checked out")
        }
    }
}

// Value semantics demonstration
var book1 = Book(title: "Swift Basics", author: "Apple", isbn: "123", pageCount: 300)
var book2 = book1  // Copy created
book2.checkOut()   // Only affects book2
print(book1.isCheckedOut) // false
print(book2.isCheckedOut) // true

// === PART 2: LibraryMember Class ===
class LibraryMember {
    let memberID: String
    var name: String
    private var borrowedBooks: [Book] = []

    init(memberID: String, name: String) {
        self.memberID = memberID
        self.name = name
    }

    func borrowBook(_ book: inout Book) {
        if book.canBeBorrowed {
            book.checkOut()
            borrowedBooks.append(book)
            print("\(name) borrowed '\(book.title)'")
        } else {
            print("Cannot borrow '\(book.title)' - already checked out")
        }
    }

    func returnBook(isbn: String) {
        if let index = borrowedBooks.firstIndex(where: { $0.isbn == isbn }) {
            // Note: We can't modify the original book through this reference
            // In a real app, you'd have a way to update the original
            borrowedBooks.remove(at: index)
            print("\(name) returned book with ISBN \(isbn)")
        } else {
            print("No book with ISBN \(isbn) found in \(name)'s borrowed list")
        }
    }

    func listBorrowedBooks() {
        if borrowedBooks.isEmpty {
            print("\(name) has no borrowed books")
        } else {
            print("\(name)'s borrowed books:")
            for book in borrowedBooks {
                print("  - \(book.description)")
            }
        }
    }
}

// Reference semantics demonstration
let member1 = LibraryMember(memberID: "M001", name: "Alice")
let member2 = member1  // Same instance!
member2.name = "Bob"
print(member1.name) // "Bob" - both changed

// === PART 3: Inheritance Hierarchy ===
class LibraryItem {
    let id: String
    let title: String
    var isAvailable: Bool = true

    init(id: String, title: String) {
        self.id = id
        self.title = title
    }

    func calculateDueDate() -> Date {
        let calendar = Calendar.current
        return calendar.date(byAdding: .day, value: 14, to: Date()) ?? Date()
    }

    func checkOut() {
        if isAvailable {
            isAvailable = false
            let dueDate = calculateDueDate()
            print("'\(title)' checked out. Due: \(dueDate)")
        } else {
            print("'\(title)' is not available")
        }
    }

    func returnItem() {
        isAvailable = true
        print("'\(title)' has been returned")
    }
}

class PrintedBook: LibraryItem {
    let author: String
    let pageCount: Int

    init(id: String, title: String, author: String, pageCount: Int) {
        self.author = author
        self.pageCount = pageCount
        super.init(id: id, title: title)
    }

    override func calculateDueDate() -> Date {
        let calendar = Calendar.current
        return calendar.date(byAdding: .day, value: 21, to: Date()) ?? Date()
    }
}

class EBook: LibraryItem {
    let fileSize: Double // in MB

    init(id: String, title: String, fileSize: Double) {
        self.fileSize = fileSize
        super.init(id: id, title: title)
    }

    override func calculateDueDate() -> Date {
        let calendar = Calendar.current
        return calendar.date(byAdding: .day, value: 7, to: Date()) ?? Date()
    }
}

// Polymorphism demonstration
let items: [LibraryItem] = [
    PrintedBook(id: "B001", title: "Swift Guide", author: "Apple", pageCount: 400),
    EBook(id: "E001", title: "Digital Swift", fileSize: 5.2)
]

for item in items {
    item.checkOut() // Each calls its own calculateDueDate implementation
}

// === PART 4: Advanced Features ===
class LibrarySystem {
    static let shared = LibrarySystem()
    private init() {}

    private var allItems: [LibraryItem] = []

    func addItem(_ item: LibraryItem) {
        allItems.append(item)
    }

    func findAvailableItems() -> [LibraryItem] {
        return allItems.filter { $0.isAvailable }
    }
}

// Singleton usage
LibrarySystem.shared.addItem(PrintedBook(id: "B002", title: "Advanced Swift", author: "Apple", pageCount: 600))

// Identity operators
let bookA = PrintedBook(id: "B003", title: "Book A", author: "Author A", pageCount: 100)
let bookB = bookA
let bookC = PrintedBook(id: "B003", title: "Book A", author: "Author A", pageCount: 100)

print(bookA === bookB) // true - same instance
print(bookA === bookC) // false - different instances

// Deinitializer example
class ResourceHandler {
    let resourceName: String

    init(resourceName: String) {
        self.resourceName = resourceName
        print("Acquired resource: \(resourceName)")
    }

    deinit {
        print("Released resource: \(resourceName)")
    }
}

// Test deinitializer
func testResource() {
    let handler = ResourceHandler(resourceName: "Database Connection")
    // handler will be deinitialized when this function ends
}

testResource() // Prints: "Released resource: Database Connection"

