// === PART 1: AuthState Enum ===
enum AuthState {
    case authenticated(user: User, token: String)
    case unauthenticated
    case loading(message: String)
    case error(AuthError)

    // Computed properties
    var isLoggedIn: Bool {
        switch self {
        case .authenticated:
            return true
        default:
            return false
        }
    }

    var currentUser: User? {
        switch self {
        case .authenticated(let user, _):
            return user
        default:
            return nil
        }
    }

    var errorMessage: String? {
        switch self {
        case .error(let error):
            return error.localizedDescription
        default:
            return nil
        }
    }

    // Mutating methods for state transitions
    mutating func login(user: User, token: String) {
        self = .authenticated(user: user, token: token)
    }

    mutating func logout() {
        self = .unauthenticated
    }

    mutating func setLoading(_ message: String) {
        self = .loading(message: message)
    }

    mutating func setError(_ error: AuthError) {
        self = .error(error)
    }
}

// Error enum with associated values
enum AuthError: Error {
    case invalidCredentials
    case networkError(underlyingError: Error)
    case validationFailed(field: String, reason: String)
    case accountLocked(until: Date)
    case unknownError(message: String)

    var localizedDescription: String {
        switch self {
        case .invalidCredentials:
            return "Invalid username or password"
        case .networkError(let underlyingError):
            return "Network error: \(underlyingError.localizedDescription)"
        case .validationFailed(let field, let reason):
            return "\(field) validation failed: \(reason)"
        case .accountLocked(let until):
            let formatter = DateFormatter()
            formatter.dateFormat = "HH:mm"
            return "Account locked until \(formatter.string(from: until))"
        case .unknownError(let message):
            return "Unknown error: \(message)"
        }
    }
}

// === PART 2: Authentication Protocol ===
protocol Authenticatable {
    var username: String { get }
    var email: String { get }
    var password: String { get }
}

protocol Validatable {
    func validate() throws
}

// Protocol extension with default implementations
extension Authenticatable {
    var isValidEmail: Bool {
        let emailRegex = "[A-Z0-9a-z._%+-]+@[A-Za-z0-9.-]+\\.[A-Za-z]{2,}"
        return NSPredicate(format: "SELF MATCHES %@", emailRegex).evaluate(with: email)
    }

    var isValidUsername: Bool {
        return username.count >= 3 && username.count <= 20
    }

    var isValidPassword: Bool {
        return password.count >= 8
    }
}

// User struct implementing protocols
struct User: Authenticatable, Validatable {
    let username: String
    let email: String
    let password: String
    let id: String
    let createdAt: Date

    init(username: String, email: String, password: String) throws {
        self.username = username
        self.email = email
        self.password = password
        self.id = UUID().uuidString
        self.createdAt = Date()

        try validate()
    }

    func validate() throws {
        if !isValidUsername {
            throw AuthError.validationFailed(field: "username", reason: "Must be 3-20 characters")
        }

        if !isValidEmail {
            throw AuthError.validationFailed(field: "email", reason: "Invalid email format")
        }

        if !isValidPassword {
            throw AuthError.validationFailed(field: "password", reason: "Must be at least 8 characters")
        }
    }
}

// === PART 3: Authentication Service ===
// Result enum with associated values
enum AuthResult {
    case success(user: User, token: String)
    case failure(AuthError)
    case loading(progress: Double)
}

protocol AuthService {
    func login(username: String, password: String) async -> AuthResult
    func logout() async -> Bool
    func register(user: User) async -> AuthResult
}

class MockAuthService: AuthService {
    private var currentState: AuthState = .unauthenticated

    func login(username: String, password: String) async -> AuthResult {
        // Simulate network delay
        await Task.sleep(1_000_000_000) // 1 second

        // Mock validation
        if username == "admin" && password == "password" {
            do {
                let user = try User(username: username, email: "admin@example.com", password: password)
                let token = "mock-jwt-token-\(UUID().uuidString)"
                return .success(user: user, token: token)
            } catch {
                return .failure(error as? AuthError ?? .unknownError(message: error.localizedDescription))
            }
        } else {
            return .failure(.invalidCredentials)
        }
    }

    func logout() async -> Bool {
        await Task.sleep(500_000_000) // 0.5 second
        currentState = .unauthenticated
        return true
    }

    func register(user: User) async -> AuthResult {
        await Task.sleep(1_500_000_000) // 1.5 seconds

        do {
            try user.validate()
            let token = "mock-jwt-token-\(UUID().uuidString)"
            return .success(user: user, token: token)
        } catch {
            return .failure(error as? AuthError ?? .unknownError(message: error.localizedDescription))
        }
    }
}

// === PART 4: Advanced Protocol Composition ===
// Provider protocols
protocol EmailAuth {
    func loginWithEmail(_ email: String, password: String) async -> AuthResult
}

protocol SocialAuth {
    func loginWithSocialProvider(_ provider: SocialProvider) async -> AuthResult
}

protocol BiometricAuth {
    func loginWithBiometrics() async -> AuthResult
}

// Social provider enum
enum SocialProvider {
    case google
    case facebook
    case apple
    case github

    var name: String {
        switch self {
        case .google: return "Google"
        case .facebook: return "Facebook"
        case .apple: return "Apple"
        case .github: return "GitHub"
        }
    }
}

// Protocol composition for multi-provider auth
protocol MultiProviderAuth: EmailAuth, SocialAuth, BiometricAuth {
    var availableProviders: [SocialProvider] { get }
    func switchProvider(_ provider: SocialProvider)
}

// Protocol with associated type
protocol AuthResponse {
    associatedtype UserType: Authenticatable
    func getUser() -> UserType?
    func getToken() -> String?
}

// Concrete implementation
struct TypedAuthResponse<UserType: Authenticatable>: AuthResponse {
    let user: UserType?
    let token: String?

    func getUser() -> UserType? {
        return user
    }

    func getToken() -> String? {
        return token
    }
}

// Complete auth manager using all patterns
class AuthManager: MultiProviderAuth {
    private var authService: AuthService
    private(set) var currentState: AuthState = .unauthenticated
    private(set) var availableProviders: [SocialProvider] = [.google, .apple, .github]

    init(authService: AuthService = MockAuthService()) {
        self.authService = authService
    }

    // EmailAuth implementation
    func loginWithEmail(_ email: String, password: String) async -> AuthResult {
        currentState.setLoading("Logging in with email...")
        return await authService.login(username: email, password: password)
    }

    // SocialAuth implementation
    func loginWithSocialProvider(_ provider: SocialProvider) async -> AuthResult {
        currentState.setLoading("Logging in with \(provider.name)...")

        await Task.sleep(2_000_000_000) // 2 seconds

        // Mock social login
        do {
            let user = try User(username: "social_user", email: "user@example.com", password: "social")
            let token = "social-token-\(provider.name.lowercased())-\(UUID().uuidString)"
            return .success(user: user, token: token)
        } catch {
            return .failure(error as? AuthError ?? .unknownError(message: error.localizedDescription))
        }
    }

    // BiometricAuth implementation
    func loginWithBiometrics() async -> AuthResult {
        currentState.setLoading("Authenticating with biometrics...")

        await Task.sleep(1_000_000_000) // 1 second

        // Mock biometric auth (80% success rate)
        if Bool.random() {
            do {
                let user = try User(username: "biometric_user", email: "bio@example.com", password: "bio")
                let token = "bio-token-\(UUID().uuidString)"
                return .success(user: user, token: token)
            } catch {
                return .failure(error as? AuthError ?? .unknownError(message: error.localizedDescription))
            }
        } else {
            return .failure(.unknownError(message: "Biometric authentication failed"))
        }
    }

    func switchProvider(_ provider: SocialProvider) {
        print("Switched to \(provider.name) provider")
    }

    // Update state based on auth result
    func handleAuthResult(_ result: AuthResult) {
        switch result {
        case .success(let user, let token):
            currentState.login(user: user, token: token)
            print("Login successful for user: \(user.username)")
        case .failure(let error):
            currentState.setError(error)
            print("Login failed: \(error.localizedDescription)")
        case .loading(let progress):
            currentState.setLoading("Loading... \(Int(progress * 100))%")
        }
    }
}

// Usage example
func demonstrateAuthSystem() async {
    let authManager = AuthManager()

    // Test email login
    let emailResult = await authManager.loginWithEmail("admin", password: "password")
    authManager.handleAuthResult(emailResult)

    // Test social login
    let socialResult = await authManager.loginWithSocialProvider(.google)
    authManager.handleAuthResult(socialResult)

    // Test biometric login
    let bioResult = await authManager.loginWithBiometrics()
    authManager.handleAuthResult(bioResult)
}

// Run the demonstration
// await demonstrateAuthSystem()

