func validateAge(_ age: Int) throws {
    guard age >= 0 else {
        throw ValidationError.valueTooLow(fieldName: "age", minValue: 0)
    }

    guard age <= 150 else {
        throw ValidationError.valueTooHigh(fieldName: "age", maxValue: 150)
    }

    guard age >= 13 else {
        throw ValidationError.valueTooLow(fieldName: "age", minValue: 13)
    }

    print("Age validation passed: \(age)")
}

// Function that processes user registration
func registerUser(name: String, email: String, age: Int, password: String) throws -> String {
    // Validate name
    guard !name.isEmpty else {
        throw ValidationError.emptyField(fieldName: "name")
    }

    guard name.count >= 2 else {
        throw ValidationError.tooShort(fieldName: "name", minLength: 2)
    }

    // Validate email
    guard !email.isEmpty else {
        throw ValidationError.emptyField(fieldName: "email")
    }

    guard email.contains("@") && email.contains(".") else {
        throw ValidationError.invalidFormat(fieldName: "email")
    }

    // Validate age
    try validateAge(age)

    // Validate password
    guard !password.isEmpty else {
        throw ValidationError.emptyField(fieldName: "password")
    }

    guard password.count >= 8 else {
        throw ValidationError.tooShort(fieldName: "password", minLength: 8)
    }

