// === PART 1: Define Error Types ===
enum RegistrationError: Error, CustomStringConvertible {
    case invalidUsername(reason: String)
    case invalidEmail
    case weakPassword(strengthIssue: String)
    case ageRestriction(minAge: Int)
    case duplicateUser(username: String)

    var description: String {
        switch self {
        case .invalidUsername(let reason):
            return "Invalid username: \(reason)"
        case .invalidEmail:
            return "Invalid email format"
        case .weakPassword(let issue):
            return "Weak password: \(issue)"
        case .ageRestriction(let minAge):
            return "Must be at least \(minAge) years old"
        case .duplicateUser(let username):
            return "Username '\(username)' is already taken"
        }
    }
}

enum NetworkError: Error, CustomStringConvertible {
    case noConnection
    case serverDown
    case timeout
    case invalidResponse

    var description: String {
        switch self {
        case .noConnection:
            return "No internet connection"
        case .serverDown:
            return "Server is temporarily unavailable"
        case .timeout:
            return "Request timed out"
        case .invalidResponse:
            return "Received invalid response from server"
        }
    }
}

// === PART 2: Validation Functions ===
func validateUsername(_ username: String) throws {
    guard !username.isEmpty else {
        throw RegistrationError.invalidUsername(reason: "Username cannot be empty")
    }

    guard username.count >= 3 else {
        throw RegistrationError.invalidUsername(reason: "Username must be at least 3 characters")
    }

    guard username.count <= 20 else {
        throw RegistrationError.invalidUsername(reason: "Username cannot exceed 20 characters")
    }

    let invalidCharacters = CharacterSet.alphanumerics.inverted
    guard username.rangeOfCharacter(from: invalidCharacters) == nil else {
        throw RegistrationError.invalidUsername(reason: "Username can only contain letters and numbers")
    }
}

func validateEmail(_ email: String) throws {
    guard !email.isEmpty else {
        throw RegistrationError.invalidEmail
    }

    let emailRegex = "[A-Z0-9a-z._%+-]+@[A-Za-z0-9.-]+\\.[A-Za-z]{2,}"
    let emailPredicate = NSPredicate(format: "SELF MATCHES %@", emailRegex)

    guard emailPredicate.evaluate(with: email) else {
        throw RegistrationError.invalidEmail
    }
}

func validatePassword(_ password: String) throws {
    guard !password.isEmpty else {
        throw RegistrationError.weakPassword(strengthIssue: "Password cannot be empty")
    }

    guard password.count >= 8 else {
        throw RegistrationError.weakPassword(strengthIssue: "Password must be at least 8 characters")
    }

    let numberRegex = ".*[0-9].*"
    let numberPredicate = NSPredicate(format: "SELF MATCHES %@", numberRegex)
    guard numberPredicate.evaluate(with: password) else {
        throw RegistrationError.weakPassword(strengthIssue: "Password must contain at least one number")
    }

    let uppercaseRegex = ".*[A-Z].*"
    let uppercasePredicate = NSPredicate(format: "SELF MATCHES %@", uppercaseRegex)
    guard uppercasePredicate.evaluate(with: password) else {
        throw RegistrationError.weakPassword(strengthIssue: "Password must contain at least one uppercase letter")
    }
}

func validateAge(_ age: Int) throws {
    guard age >= 13 else {
        throw RegistrationError.ageRestriction(minAge: 13)
    }

    guard age <= 150 else {
        throw RegistrationError.invalidUsername(reason: "Age must be 150 or less")
    }
}

// === PART 3: Registration System ===
func checkNetworkAvailability() throws {
    // Simulate network check
    let isOnline = Bool.random()
    if !isOnline {
        throw NetworkError.noConnection
    }

    let serverIsUp = Bool.random()
    if !serverIsUp {
        throw NetworkError.serverDown
    }
}

func checkDuplicateUser(username: String) throws {
    // Simulate database check
    let existingUsers = ["alice", "bob", "charlie"]
    if existingUsers.contains(username.lowercased()) {
        throw RegistrationError.duplicateUser(username: username)
    }
}

func registerUser(username: String, email: String, password: String, age: Int) throws -> String {
    // Validate all inputs
    try validateUsername(username)
    try validateEmail(email)
    try validatePassword(password)
    try validateAge(age)

    // Check network
    try checkNetworkAvailability()

    // Check for duplicates
    try checkDuplicateUser(username: username)

    // Simulate successful registration
    return "User '\(username)' registered successfully!"
}

// === PART 4: Advanced Error Handling ===
func completeRegistration(username: String, email: String, password: String, age: Int) {
    print("Starting registration for: \(username)")

    let maxRetries = 3
    var attempts = 0

    while attempts < maxRetries {
        do {
            attempts += 1
            print("Attempt \(attempts) of \(maxRetries)")

            let result = try registerUser(username: username, email: email, password: password, age: age)
            print("âœ… SUCCESS: \(result)")
            return

        } catch let error as RegistrationError {
            // Validation errors - no retry needed
            print("âŒ Registration Error: \(error.description)")
            return

        } catch let error as NetworkError {
            // Network errors - can retry
            print("ðŸŒ Network Error: \(error.description)")

            if attempts >= maxRetries {
                print("ðŸš« Maximum retry attempts reached. Please try again later.")
                return
            } else {
                print("ðŸ”„ Retrying in 2 seconds...")
                // In real app, you'd use proper async delay
                Thread.sleep(forTimeInterval: 2.0)
            }

        } catch {
            // Unexpected errors
            print("ðŸ’¥ Unexpected error: \(error)")
            return
        }
    }
}

// User-friendly error handler
func handleRegistrationError(_ error: Error) -> String {
    if let registrationError = error as? RegistrationError {
        switch registrationError {
        case .invalidUsername(let reason):
            return "Please choose a different username. \(reason)"
        case .invalidEmail:
            return "Please enter a valid email address"
        case .weakPassword(let issue):
            return "Please strengthen your password. \(issue)"
        case .ageRestriction(let minAge):
            return "You must be at least \(minAge) years old to register"
        case .duplicateUser(let username):
            return "The username '\(username)' is already taken. Please choose another."
        }
    } else if let networkError = error as? NetworkError {
        switch networkError {
        case .noConnection:
            return "Please check your internet connection and try again"
        case .serverDown:
            return "Our servers are temporarily busy. Please try again in a few minutes"
        case .timeout:
            return "The request took too long. Please try again"
        case .invalidResponse:
            return "We encountered an issue. Please try again"
        }
    } else {
        return "An unexpected error occurred. Please try again"
    }
}

