struct ProfileView: View {
    @State private var isFollowing = false
    @State private var selectedTab = 0
    @State private var showMessageAlert = false

    let userStats = [
        ("12K", "Followers"),
        ("563", "Following"), 
        ("128", "Posts")
    ]

    let tabs = ["Posts", "Photos", "Likes"]

    var body: some View {
