                            .foregroundColor(.secondary)
                    }

                    HStack(spacing: 16) {
                        Button(action: {
                            isFollowing.toggle()
                        }) {
                            HStack {
                                Image(systemName: isFollowing ? "checkmark" : "person.badge.plus")
                                Text(isFollowing ? "Following" : "Follow")
                            }
                            .primaryButton()
                        }

                        Button(action: {
                            showMessageAlert = true
                        }) {
                            HStack {
                                Image(systemName: "message")
                                Text("Message")
                            }
                            .secondaryButton()
                        }
                    }
                }
                .padding()
                .cardStyle()

                // Stats Row
                HStack {
                    ForEach(userStats, id: \.1) { stat in
                        VStack {
                            Text(stat.0)
                                .font(.title2)
                                .fontWeight(.bold)
                                .foregroundColor(.primary)

                            Text(stat.1)
                                .font(.caption)
                                .foregroundColor(.secondary)
                        }
                        .frame(maxWidth: .infinity)
                    }
                }
                .padding()
                .cardStyle()

                // Bio Section
                VStack(alignment: .leading, spacing: 12) {
                    Text("About")
                        .font(.headline)
                        .fontWeight(.semibold)

                    Text("Passionate iOS developer with 5+ years of experience building beautiful, user-friendly applications. Love working with SwiftUI and exploring new technologies.")
                        .font(.body)
                        .foregroundColor(.secondary)
                        .lineSpacing(4)
                }
                .frame(maxWidth: .infinity, alignment: .leading)
                .padding()
                .cardStyle()

                // Tabbed Content
                VStack(spacing: 16) {
                    // Tab Selector
                    HStack {
                        ForEach(0..<tabs.count, id: \.self) { index in
                            Button(action: {
                                selectedTab = index
                            }) {
                                Text(tabs[index])
                                    .font(.headline)
                                    .foregroundColor(selectedTab == index ? .blue : .secondary)
                                    .padding(.vertical, 8)
                                    .frame(maxWidth: .infinity)
                                    .overlay(
                                        Rectangle()
                                            .fill(selectedTab == index ? Color.blue : Color.clear)
                                            .frame(height: 2)
                                            .offset(y: 16),
                                        alignment: .bottom
                                    )
                            }
                        }
                    }

                    // Tab Content
                    Group {
                        switch selectedTab {
                        case 0:
                            PostsTabView()
                        case 1:
                            PhotosTabView()
                        case 2:
                            LikesTabView()
                        default:
                            EmptyView()
                        }
                    }
                    .animation(.easeInOut, value: selectedTab)
                }
                .padding()
                .cardStyle()
            }
            .padding()
        }
        .background(Color(.systemGroupedBackground))
        .alert("Message Sent!", isPresented: $showMessageAlert) {
            Button("OK", role: .cancel) { }
        } message: {
            Text("Your message has been sent to Sarah.")
