import SwiftUI

struct SocialMediaPost: View {
    let post: Post
    @State private var isLiked = false
    @State private var isExpanded = false
    @State private var showShareSheet = false
    @State private var profileScale: CGFloat = 1.0

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            // Header
            HStack(alignment: .top, spacing: 12) {
                // Profile picture with verified badge
                ZStack(alignment: .bottomTrailing) {
                    Image(systemName: "person.circle.fill")
                        .font(.system(size: 44))
                        .foregroundColor(.blue)
                        .scaleEffect(profileScale)
                        .onTapGesture {
                            withAnimation(.spring(response: 0.3, dampingFraction: 0.6)) {
                                profileScale = 0.8
                            }
                            withAnimation(.spring(response: 0.3, dampingFraction: 0.6).delay(0.1)) {
                                profileScale = 1.0
                            }
                        }

                    // Verified badge
                    if post.isVerified {
                        Image(systemName: "checkmark.seal.fill")
                            .font(.system(size: 12))
                            .foregroundColor(.blue)
                            .background(Circle().fill(Color.white))
                    }
                }

                VStack(alignment: .leading, spacing: 2) {
                    HStack {
                        Text(post.authorName)
                            .font(.headline)
                            .fontWeight(.semibold)

                        if post.isPremium {
                            Image(systemName: "crown.fill")
                                .font(.caption)
                                .foregroundColor(.yellow)
                        }
                    }

                    Text(post.timestamp)
                        .font(.caption)
                        .foregroundColor(.secondary)
                }

                Spacer()

                Image(systemName: "ellipsis")
                    .foregroundColor(.secondary)
            }

            // Post content
            VStack(alignment: .leading, spacing: 8) {
                Text(post.content)
                    .font(.body)
                    .lineLimit(isExpanded ? nil : 3)

                if post.content.count > 150 {
                    Button(isExpanded ? "Show less" : "Show more") {
                        withAnimation(.easeInOut(duration: 0.3)) {
                            isExpanded.toggle()
                        }
                    }
                    .font(.caption)
                    .fontWeight(.medium)
                    .foregroundColor(.blue)
                }
            }

            // Post image with overlay
            if let imageName = post.imageName {
                ZStack(alignment: .bottomTrailing) {
                    Rectangle()
                        .fill(Color.gray.opacity(0.3))
                        .aspectRatio(1.5, contentMode: .fit)
                        .overlay(
                            Image(systemName: imageName)
                                .font(.system(size: 50))
                                .foregroundColor(.gray)
                        )

                    // Read time indicator
                    if post.hasReadTime {
                        Text("5 min read")
                            .font(.caption2)
                            .fontWeight(.medium)
                            .foregroundColor(.white)
                            .padding(.horizontal, 8)
                            .padding(.vertical, 4)
                            .background(Color.black.opacity(0.7))
                            .cornerRadius(4)
                            .padding(8)
                    }
                }
                .cornerRadius(12)
            }

            // Engagement stats
            HStack {
                HStack(spacing: 4) {
                    // Like count with animation
                    ZStack {
                        Image(systemName: "heart.fill")
                            .font(.caption)
                            .foregroundColor(isLiked ? .red : .clear)

                        Image(systemName: "heart")
                            .font(.caption)
                            .foregroundColor(.secondary)
                    }

                    Text("\(post.likeCount + (isLiked ? 1 : 0))")
                        .font(.caption)
                        .foregroundColor(.secondary)
                }

                Text("\(post.commentCount) comments")
                    .font(.caption)
                    .foregroundColor(.secondary)

                Spacer()

                Text("\(post.shareCount) shares")
                    .font(.caption)
                    .foregroundColor(.secondary)
            }

            // Engagement buttons
            HStack(spacing: 0) {
                Button(action: {
                    withAnimation(.spring(response: 0.3, dampingFraction: 0.6)) {
                        isLiked.toggle()
                    }
                }) {
                    HStack {
                        Image(systemName: isLiked ? "heart.fill" : "heart")
                            .foregroundColor(isLiked ? .red : .secondary)
                        Text("Like")
                            .foregroundColor(isLiked ? .red : .secondary)
                    }
                    .frame(maxWidth: .infinity)
                    .padding(.vertical, 8)
                }

                Button(action: {
                    // Comment action
                }) {
                    HStack {
                        Image(systemName: "bubble.right")
                        Text("Comment")
                    }
                    .frame(maxWidth: .infinity)
                    .padding(.vertical, 8)
                    .foregroundColor(.secondary)
                }

                Button(action: {
                    showShareSheet = true
                }) {
                    HStack {
                        Image(systemName: "arrowshape.turn.up.right")
                        Text("Share")
                    }
                    .frame(maxWidth: .infinity)
                    .padding(.vertical, 8)
                    .foregroundColor(.secondary)
                }
            }
            .font(.subheadline)
            .fontWeight(.medium)

            Divider()
        }
        .padding(.horizontal)
        .sheet(isPresented: $showShareSheet) {
            Text("Share Sheet")
                .presentationDetents([.medium])
        }
    }
}

struct SocialMediaFeed: View {
    let posts: [Post] = Post.mockPosts
    @State private var isRefreshing = false

    var body: some View {
        ZStack(alignment: .top) {
            ScrollView {
                LazyVStack(spacing: 0) {
                    // Pull to refresh indicator
                    if isRefreshing {
                        ProgressView()
                            .padding()
                    }

                    ForEach(posts) { post in
                        SocialMediaPost(post: post)
                    }
                }
                .background(
                    GeometryReader { geometry in
                        Color.clear
                            .onChange(of: geometry.frame(in: .global).minY) { value in
                                // Simple pull-to-refresh logic
                                if value > 50 && !isRefreshing {
                                    isRefreshing = true
                                    // Simulate refresh
                                    DispatchQueue.main.asyncAfter(deadline: .now() + 2) {
                                        isRefreshing = false
                                    }
                                }
                            }
                    }
                )
            }

            // Sticky header
            VStack(spacing: 0) {
                HStack {
                    Text("Social Feed")
