// === PART 1: @State for Local UI ===
struct ProductListView: View {
    let products: [Product]

    // @State for local UI state
    @State private var searchQuery = ""
    @State private var selectedCategory = "All"
    @State private var sortOption = "name"
    @State private var showFilters = false

    // Computed properties for filtered and sorted products
    private var filteredProducts: [Product] {
        let filtered = products.filter { product in
            (selectedCategory == "All" || product.category == selectedCategory) &&
            (searchQuery.isEmpty || product.name.localizedCaseInsensitiveContains(searchQuery))
        }

        switch sortOption {
        case "name":
            return filtered.sorted { $0.name < $1.name }
        case "priceLow":
            return filtered.sorted { $0.price < $1.price }
        case "priceHigh":
            return filtered.sorted { $0.price > $1.price }
        default:
            return filtered
        }
    }

    private var categories: [String] {
        ["All"] + Array(Set(products.map { $0.category })).sorted()
    }

    var body: some View {
        NavigationView {
            VStack {
                // Search bar
                HStack {
                    Image(systemName: "magnifyingglass")
                        .foregroundColor(.secondary)
                    TextField("Search products...", text: $searchQuery)

                    Button("Filters") {
                        showFilters.toggle()
                    }
                    .buttonStyle(.bordered)
                }
                .padding()
                .background(Color(.systemGray6))
                .cornerRadius(10)
                .padding(.horizontal)

                // Filters sheet
                .sheet(isPresented: $showFilters) {
                    FilterView(
                        selectedCategory: $selectedCategory,
                        sortOption: $sortOption,
                        categories: categories
                    )
                }

                // Product list
                List(filteredProducts) { product in
                    ProductRow(product: product)
                }
                .listStyle(.plain)
            }
            .navigationTitle("Products")
        }
    }
}

// === PART 2: @Binding for Components ===
struct ProductRow: View {
    let product: Product
    @Binding var cart: [CartItem]

    private var quantity: Int {
        cart.first { $0.product.id == product.id }?.quantity ?? 0
    }

    var body: some View {
        HStack {
            // Product info
            VStack(alignment: .leading) {
                Text(product.name)
                    .font(.headline)
                Text(product.category)
                    .font(.caption)
                    .foregroundColor(.secondary)
                Text("$\(product.price, specifier: "%.2f")")
                    .font(.subheadline)
                    .bold()
            }

            Spacer()

            // Quantity controls
            if quantity > 0 {
                QuantityStepper(quantity: Binding(
                    get: { quantity },
                    set: { newValue in
                        if let index = cart.firstIndex(where: { $0.product.id == product.id }) {
                            if newValue > 0 {
                                cart[index].quantity = newValue
                            } else {
                                cart.remove(at: index)
                            }
                        } else if newValue > 0 {
                            cart.append(CartItem(product: product, quantity: newValue))
                        }
                    }
                ))
            } else {
                Button("Add to Cart") {
                    if let index = cart.firstIndex(where: { $0.product.id == product.id }) {
                        cart[index].quantity += 1
                    } else {
                        cart.append(CartItem(product: product, quantity: 1))
                    }
                }
                .buttonStyle(.bordered)
            }
        }
        .padding(.vertical, 8)
    }
}

struct QuantityStepper: View {
    @Binding var quantity: Int

    var body: some View {
        HStack {
            Button("-") {
                if quantity > 0 {
                    quantity -= 1
                }
            }
            .buttonStyle(.bordered)

            Text("\(quantity)")
                .frame(minWidth: 30)

            Button("+") {
                quantity += 1
            }
            .buttonStyle(.bordered)
        }
    }
}

struct FilterView: View {
    @Binding var selectedCategory: String
    @Binding var sortOption: String
    let categories: [String]
    @Environment(\.dismiss) private var dismiss

    var body: some View {
        NavigationView {
            Form {
                Section("Category") {
                    Picker("Category", selection: $selectedCategory) {
                        ForEach(categories, id: \.self) { category in
                            Text(category).tag(category)
                        }
                    }
                    .pickerStyle(.segmented)
                }

                Section("Sort By") {
                    Picker("Sort By", selection: $sortOption) {
                        Text("Name").tag("name")
                        Text("Price: Low to High").tag("priceLow")
                        Text("Price: High to Low").tag("priceHigh")
                    }
                    .pickerStyle(.segmented)
                }
            }
            .navigationTitle("Filters")
            .navigationBarItems(trailing: Button("Done") { dismiss() })
        }
    }
}

// === PART 3: @ObservedObject for Shared Data ===
class ShoppingCart: ObservableObject {
    @Published var items: [CartItem] = []
    @Published var lastUpdate = Date()

    var total: Double {
        items.reduce(0) { $0 + ($1.product.price * Double($1.quantity)) }
    }

    var itemCount: Int {
        items.reduce(0) { $0 + $1.quantity }
    }

    func addProduct(_ product: Product, quantity: Int = 1) {
        if let index = items.firstIndex(where: { $0.product.id == product.id }) {
            items[index].quantity += quantity
        } else {
            items.append(CartItem(product: product, quantity: quantity))
        }
        lastUpdate = Date()
    }

    func removeProduct(_ product: Product) {
        items.removeAll { $0.product.id == product.id }
        lastUpdate = Date()
    }

    func updateQuantity(for product: Product, to quantity: Int) {
        if quantity <= 0 {
            removeProduct(product)
        } else if let index = items.firstIndex(where: { $0.product.id == product.id }) {
            items[index].quantity = quantity
            lastUpdate = Date()
        }
    }

    func clear() {
        items.removeAll()
        lastUpdate = Date()
    }
}

class InventoryManager: ObservableObject {
    @Published var products: [Product] = [
        Product(id: 1, name: "iPhone", category: "Electronics", price: 999.99),
        Product(id: 2, name: "MacBook", category: "Electronics", price: 1299.99),
        Product(id: 3, name: "Coffee Mug", category: "Home", price: 12.99),
        Product(id: 4, name: "Desk Lamp", category: "Home", price: 45.99),
        Product(id: 5, name: "T-shirt", category: "Clothing", price: 24.99),
        Product(id: 6, name: "Jeans", category: "Clothing", price: 59.99)
    ]
}

// === PART 4: Integration ===
struct ShoppingApp: View {
    @StateObject private var inventory = InventoryManager()
    @StateObject private var cart = ShoppingCart()
    @State private var selectedTab = 0

    var body: some View {
        TabView(selection: $selectedTab) {
            // Products Tab
            ProductListView(products: inventory.products)
                .tabItem {
                    Image(systemName: "list.bullet")
                    Text("Products")
                }
                .tag(0)

            // Cart Tab
            CartView()
                .tabItem {
                    Image(systemName: "cart")
                    Text("Cart")
                }
                .badge(cart.itemCount)
                .tag(1)
        }
        .environmentObject(cart)
        .environmentObject(inventory)
    }
}

struct CartView: View {
    @EnvironmentObject var cart: ShoppingCart

    var body: some View {
        NavigationView {
            Group {
                if cart.items.isEmpty {
                    VStack {
                        Image(systemName: "cart")
                            .font(.system(size: 60))
                            .foregroundColor(.secondary)
                        Text("Your cart is empty")
                            .font(.title2)
                            .foregroundColor(.secondary)
                    }
                } else {
                    List {
                        ForEach(cart.items) { item in
                            HStack {
                                VStack(alignment: .leading) {
                                    Text(item.product.name)
                                        .font(.headline)
                                    Text("$\(item.product.price, specifier: "%.2f") each")
                                        .font(.caption)
                                        .foregroundColor(.secondary)
                                }

                                Spacer()

                                QuantityStepper(quantity: Binding(
                                    get: { item.quantity },
                                    set: { newValue in
                                        cart.updateQuantity(for: item.product, to: newValue)
                                    }
                                ))

                                Text("$\(item.total, specifier: "%.2f")")
                                    .font(.headline)
                                    .frame(width: 80, alignment: .trailing)
                            }
                        }
                        .onDelete { indexSet in
                            for index in indexSet {
                                cart.removeProduct(cart.items[index].product)
                            }
                        }

                        // Total section
                        Section {
                            HStack {
                                Text("Total")
                                    .font(.title2)
                                    .bold()
                                Spacer()
                                Text("$\(cart.total, specifier: "%.2f")")
                                    .font(.title2)
                                    .bold()
                            }
                        }
                    }
                    .listStyle(.insetGrouped)
                }
            }
            .navigationTitle("Shopping Cart")
            .toolbar {
                if !cart.items.isEmpty {
                    Button("Clear All") {
                        cart.clear()
                    }
                }
            }
        }
    }
}

// Data Models
struct Product: Identifiable {
    let id: Int
    let name: String
    let category: String
    let price: Double
}

struct CartItem: Identifiable {
    let id = UUID()
    let product: Product
