import SwiftUI

// === PART 1: Data Models ===
struct Restaurant: Identifiable {
    let id = UUID()
    let name: String
    let cuisine: String
    let priceRange: Int // 1-3 ($, $$, $$$)
    let rating: Double // 1-5
    let imageName: String
    let description: String
    let address: String
    var isFavorite: Bool = false
}

// Sample data
let sampleRestaurants = [
    Restaurant(name: "Italian Bistro", cuisine: "Italian", priceRange: 2, rating: 4.5, imageName: "italian", description: "Authentic Italian cuisine with fresh ingredients", address: "123 Main St"),
    Restaurant(name: "Sushi Heaven", cuisine: "Japanese", priceRange: 3, rating: 4.8, imageName: "sushi", description: "Premium sushi and sashimi", address: "456 Oak Ave"),
    Restaurant(name: "Burger Palace", cuisine: "American", priceRange: 1, rating: 4.2, imageName: "burger", description: "Classic American burgers and fries", address: "789 Elm St"),
    Restaurant(name: "Taco Fiesta", cuisine: "Mexican", priceRange: 1, rating: 4.0, imageName: "mexican", description: "Fresh Mexican street food", address: "321 Pine Rd"),
    Restaurant(name: "French Delight", cuisine: "French", priceRange: 3, rating: 4.7, imageName: "french", description: "Elegant French dining experience", address: "654 Maple Dr"),
    Restaurant(name: "Dragon Palace", cuisine: "Chinese", priceRange: 2, rating: 4.3, imageName: "chinese", description: "Traditional Chinese dishes", address: "987 Cedar Ln"),
    Restaurant(name: "Spice Garden", cuisine: "Indian", priceRange: 2, rating: 4.6, imageName: "indian", description: "Aromatic Indian curries and breads", address: "147 Birch St"),
    Restaurant(name: "Mediterranean Grill", cuisine: "Mediterranean", priceRange: 2, rating: 4.4, imageName: "mediterranean", description: "Healthy Mediterranean flavors", address: "258 Willow Ave")
]

// === PART 2: Restaurant List ===
struct RestaurantListView: View {
    let restaurants: [Restaurant]
    @State private var searchText = ""
    @State private var isRefreshing = false

    var filteredRestaurants: [Restaurant] {
        if searchText.isEmpty {
            return restaurants
        } else {
            return restaurants.filter {
                $0.name.localizedCaseInsensitiveContains(searchText) ||
                $0.cuisine.localizedCaseInsensitiveContains(searchText)
            }
        }
    }

    var groupedRestaurants: [String: [Restaurant]] {
        Dictionary(grouping: filteredRestaurants) { $0.cuisine }
    }

    var body: some View {
        NavigationView {
            List {
                ForEach(Array(groupedRestaurants.keys.sorted()), id: \.self) { cuisine in
                    Section(header: Text(cuisine).font(.headline)) {
                        ForEach(groupedRestaurants[cuisine]!) { restaurant in
                            NavigationLink(destination: RestaurantDetailView(restaurant: restaurant)) {
                                RestaurantRow(restaurant: restaurant)
                            }
                            .swipeActions(edge: .trailing) {
                                Button {
                                    toggleFavorite(for: restaurant)
                                } label: {
                                    Label("Favorite", systemImage: restaurant.isFavorite ? "heart.slash" : "heart")
                                }
                                .tint(restaurant.isFavorite ? .gray : .red)
                            }
                        }
                    }
                }
            }
            .searchable(text: $searchText, prompt: "Search restaurants or cuisines")
            .refreshable {
                await simulateRefresh()
            }
            .navigationTitle("Restaurants")
            .listStyle(InsetGroupedListStyle())
        }
    }

    private func toggleFavorite(for restaurant: Restaurant) {
        // In a real app, you'd update this in your data model
        print("Toggled favorite for \(restaurant.name)")
    }

    private func simulateRefresh() async {
        isRefreshing = true
        try? await Task.sleep(nanoseconds: 1_000_000_000) // 1 second
        isRefreshing = false
    }
}

struct RestaurantRow: View {
    let restaurant: Restaurant

    var body: some View {
        HStack {
            // Restaurant icon
            Circle()
                .fill(Color.blue.opacity(0.2))
                .frame(width: 50, height: 50)
                .overlay(
                    Text(String(restaurant.name.prefix(1)))
                        .font(.title2)
                        .foregroundColor(.blue)
                )

            VStack(alignment: .leading, spacing: 4) {
                Text(restaurant.name)
                    .font(.headline)

                Text(restaurant.cuisine)
                    .font(.subheadline)
                    .foregroundColor(.secondary)

                HStack {
                    // Rating stars
                    HStack(spacing: 2) {
                        ForEach(1...5, id: \.self) { star in
                            Image(systemName: star <= Int(restaurant.rating) ? "star.fill" : "star")
                                .foregroundColor(.yellow)
                                .font(.caption)
                        }
                        Text("(\(restaurant.rating, specifier: "%.1f"))")
                            .font(.caption)
                            .foregroundColor(.secondary)
                    }
                }
            }

            Spacer()

            // Price range
            VStack(alignment: .trailing) {
                Text(String(repeating: "$", count: restaurant.priceRange))
                    .font(.subheadline)
                    .foregroundColor(.green)

                if restaurant.isFavorite {
                    Image(systemName: "heart.fill")
                        .foregroundColor(.red)
                        .font(.caption)
                }
            }
        }
        .padding(.vertical, 4)
    }
}

// === PART 3: Detail View ===
struct RestaurantDetailView: View {
    let restaurant: Restaurant
    @Environment(\.presentationMode) var presentationMode
    @State private var isShowingMap = false

    var body: some View {
        ScrollView {
            VStack(alignment: .leading, spacing: 16) {
                // Header
                ZStack(alignment: .bottomLeading) {
                    Rectangle()
                        .fill(Color.blue.opacity(0.3))
                        .frame(height: 200)
                        .overlay(
                            Text(restaurant.name)
                                .font(.largeTitle)
                                .bold()
                                .foregroundColor(.white)
                                .shadow(radius: 5)
                        )

                    VStack(alignment: .leading) {
                        Text(restaurant.cuisine)
                            .font(.title3)
                            .foregroundColor(.white)

                        HStack {
                            ForEach(1...5, id: \.self) { star in
                                Image(systemName: star <= Int(restaurant.rating) ? "star.fill" : "star")
                                    .foregroundColor(.yellow)
                            }
                            Text("\(restaurant.rating, specifier: "%.1f")")
                                .foregroundColor(.white)
                        }
                    }
                    .padding()
                    .background(Color.black.opacity(0.5))
                }

                // Info Section
                VStack(alignment: .leading, spacing: 12) {
                    InfoRow(icon: "mappin.circle", text: restaurant.address)
                    InfoRow(icon: "dollarsign.circle", text: "Price: \(String(repeating: "$", count: restaurant.priceRange))")
                }
                .padding(.horizontal)

                // Actions
                LazyVGrid(columns: [GridItem(.flexible()), GridItem(.flexible())], spacing: 12) {
                    ActionButton(title: "Call", icon: "phone", color: .green) {
                        print("Call \(restaurant.name)")
                    }
                    ActionButton(title: "Directions", icon: "map", color: .blue) {
                        isShowingMap = true
                    }
                    ActionButton(title: "Website", icon: "globe", color: .orange) {
                        print("Open website")
                    }
                    ActionButton(title: "Share", icon: "square.and.arrow.up", color: .purple) {
                        print("Share restaurant")
                    }
                }
                .padding(.horizontal)

                // Description
                VStack(alignment: .leading, spacing: 8) {
                    Text("About")
                        .font(.headline)

                    Text(restaurant.description)
                        .foregroundColor(.secondary)
                }
                .padding(.horizontal)

                Spacer()
            }
        }
        .navigationTitle(restaurant.name)
        .navigationBarTitleDisplayMode(.inline)
        .toolbar {
            ToolbarItem(placement: .navigationBarTrailing) {
                Button(action: { toggleFavorite() }) {
                    Image(systemName: restaurant.isFavorite ? "heart.fill" : "heart")
                        .foregroundColor(restaurant.isFavorite ? .red : .primary)
                }
            }
        }
        .sheet(isPresented: $isShowingMap) {
            MapView(restaurant: restaurant)
        }
    }

    private func toggleFavorite() {
        print("Toggled favorite for \(restaurant.name)")
    }
}

struct InfoRow: View {
    let icon: String
    let text: String

    var body: some View {
        HStack {
            Image(systemName: icon)
                .foregroundColor(.blue)
                .frame(width: 30)
