import SwiftUI

struct RegistrationChallenge: View {
    // Form State
    @State private var firstName = ""
    @State private var lastName = ""
    @State private var email = ""
    @State private var password = ""
    @State private var confirmPassword = ""
    @State private var birthDate = Date()
    @State private var subscribeNewsletter = true
    @State private var selectedTheme = "System"
    @State private var experienceLevel = 5.0
    @State private var languagesKnown = 1
    @State private var bio = ""
    @State private var agreedToTerms = false

    // UI State
    @State private var showAlert = false
    @State private var alertMessage = ""
    @FocusState private var focusedField: Field?

    // Validation State
    @State private var emailError = ""
    @State private var passwordError = ""

    let themes = ["Light", "Dark", "System"]

    enum Field {
        case firstName, lastName, email, password, confirmPassword, bio
    }

    var body: some View {
        NavigationView {
            Form {
                // Section 1: Personal Information
                Section(header: Text("Personal Information")) {
                    CustomTextField("First Name", text: $firstName)
                        .focused($focusedField, equals: .firstName)
                        .submitLabel(.next)
                        .onSubmit { focusedField = .lastName }

                    CustomTextField("Last Name", text: $lastName)
                        .focused($focusedField, equals: .lastName)
                        .submitLabel(.next)
                        .onSubmit { focusedField = .email }

                    CustomTextField("Email", text: $email)
                        .focused($focusedField, equals: .email)
                        .keyboardType(.emailAddress)
                        .textContentType(.emailAddress)
                        .autocapitalization(.none)
                        .onChange(of: email) { _ in validateEmail() }
                        .overlay(validationOverlay(for: emailError))
                        .submitLabel(.next)
                        .onSubmit { focusedField = .password }

                    DatePicker("Birth Date", selection: $birthDate, in: ...Date(), displayedComponents: .date)

                    if !isAdult {
                        Text("Must be 18 years or older to register")
                            .font(.caption)
                            .foregroundColor(.red)
                    }
                }

                // Section 2: Account Security
                Section(header: Text("Account Security")) {
                    SecureField("Password", text: $password)
                        .focused($focusedField, equals: .password)
                        .textContentType(.newPassword)
                        .onChange(of: password) { _ in validatePassword() }
                        .overlay(validationOverlay(for: passwordError))
                        .submitLabel(.next)
                        .onSubmit { focusedField = .confirmPassword }

                    SecureField("Confirm Password", text: $confirmPassword)
                        .focused($focusedField, equals: .confirmPassword)
                        .textContentType(.newPassword)
                        .overlay(validationOverlay(for: passwordsMatch ? "" : "Passwords do not match"))
                        .submitLabel(.next)
                        .onSubmit { focusedField = .bio }

                    PasswordStrengthView(password: password)
                }

                // Section 3: Preferences
                Section(header: Text("Preferences")) {
                    Picker("Theme", selection: $selectedTheme) {
                        ForEach(themes, id: \.self) { theme in
                            Text(theme)
                        }
                    }
                    .pickerStyle(.segmented)

                    VStack {
                        HStack {
                            Text("Experience Level")
                            Spacer()
                            Text("\(Int(experienceLevel))/10")
                                .foregroundColor(.secondary)
                        }
                        Slider(value: $experienceLevel, in: 1...10, step: 1)
                    }

                    Stepper("Programming Languages: \(languagesKnown)", value: $languagesKnown, in: 0...20)

                    Toggle("Subscribe to Newsletter", isOn: $subscribeNewsletter)
                }

                // Section 4: Additional Info
                Section(header: Text("About You")) {
                    VStack(alignment: .leading) {
                        Text("Bio")
                            .font(.caption)
                            .foregroundColor(.secondary)
                        TextEditor(text: $bio)
                            .focused($focusedField, equals: .bio)
                            .frame(height: 100)
                            .overlay(
                                RoundedRectangle(cornerRadius: 8)
                                    .stroke(bioColor, lineWidth: 1)
                            )
                        HStack {
                            Spacer()
                            Text("\(bio.count)/200")
                                .font(.caption)
                                .foregroundColor(bioCountColor)
                        }
                    }

                    Toggle("I agree to the Terms and Conditions", isOn: $agreedToTerms)
                }

                // Section 5: Actions
                Section {
                    Button("Create Account") {
                        submitRegistration()
                    }
                    .disabled(!canSubmit)
                    .frame(maxWidth: .infinity)

                    Button("Clear Form", role: .destructive) {
                        resetForm()
                    }
                    .frame(maxWidth: .infinity)
                }
            }
            .navigationTitle("Complete Registration")
            .alert("Registration", isPresented: $showAlert) {
                Button("OK") { }
            } message: {
                Text(alertMessage)
            }
            .onAppear {
                focusedField = .firstName
            }
        }
    }

    // MARK: - Validation Computed Properties

    var isAdult: Bool {
        let calendar = Calendar.current
        let age = calendar.dateComponents([.year], from: birthDate, to: Date()).year ?? 0
        return age >= 18
    }

    var passwordsMatch: Bool {
        !password.isEmpty && password == confirmPassword
    }

    var isBioValid: Bool {
        bio.count <= 200
    }

    var bioColor: Color {
        isBioValid ? .gray.opacity(0.3) : .red
    }

    var bioCountColor: Color {
        if bio.isEmpty {
            return .secondary
        } else if isBioValid {
            return .green
        } else {
            return .red
        }
    }

    var canSubmit: Bool {
        !firstName.isEmpty &&
        !lastName.isEmpty &&
        emailError.isEmpty &&
        passwordError.isEmpty &&
        passwordsMatch &&
        isAdult &&
        isBioValid &&
        agreedToTerms
    }

    // MARK: - Methods

    func validateEmail() {
        let emailRegex = #"^[A-Z0-9a-z._%+-]+@[A-Za-z0-9.-]+\\.[A-Za-z]{2,}$"#
        let isValid = NSPredicate(format: "SELF MATCHES %@", emailRegex).evaluate(with: email)
        emailError = isValid || email.isEmpty ? "" : "Please enter a valid email address"
    }

    func validatePassword() {
        if password.count < 8 {
            passwordError = "Password must be at least 8 characters"
        } else if !password.contains(where: { $0.isUppercase }) {
            passwordError = "Password must contain an uppercase letter"
        } else if !password.contains(where: { $0.isLowercase }) {
            passwordError = "Password must contain a lowercase letter"
        } else if !password.contains(where: { $0.isNumber }) {
            passwordError = "Password must contain a number"
        } else {
            passwordError = ""
        }
    }

    func submitRegistration() {
        // In real app, this would call an API
        alertMessage = "Welcome, \(firstName)! Your account has been created successfully."
        showAlert = true
    }

    func resetForm() {
        firstName = ""
        lastName = ""
        email = ""
        password = ""
        confirmPassword = ""
        birthDate = Date()
        subscribeNewsletter = true
        selectedTheme = "System"
        experienceLevel = 5.0
        languagesKnown = 1
        bio = ""
        agreedToTerms = false
        emailError = ""
        passwordError = ""
        focusedField = .firstName
    }

    func validationOverlay(for error: String) -> some View {
        Group {
            if !error.isEmpty {
                HStack {
                    Spacer()
                    Image(systemName: "exclamationmark.triangle.fill")
                        .foregroundColor(.red)
                        .padding(.trailing, 8)
                }
            }
        }
    }
}

// MARK: - Custom Components

struct CustomTextField: View {
    let title: String
    @Binding var text: String

    var body: some View {
        VStack(alignment: .leading) {
            Text(title)
                .font(.caption)
                .foregroundColor(.secondary)

            TextField(title, text: $text)
                .textFieldStyle(RoundedBorderTextFieldStyle())
        }
    }
}

struct PasswordStrengthView: View {
    let password: String

    var strength: (color: Color, text: String) {
        if password.isEmpty {
            return (.secondary, "Enter a password")
        } else if password.count < 8 {
            return (.red, "Weak")
        } else if password.count < 12 {
            return (.orange, "Medium")
        } else {
            return (.green, "Strong")
        }
    }

    var body: some View {
        VStack(alignment: .leading) {
            HStack {
                Text("Password Strength")
                    .font(.caption)
                    .foregroundColor(.secondary)
                Spacer()
                Text(strength.text)
                    .font(.caption)
                    .foregroundColor(strength.color)
            }

            GeometryReader { geometry in
                ZStack(alignment: .leading) {
                    Rectangle()
                        .fill(Color.gray.opacity(0.3))
                        .frame(height: 4)

                    Rectangle()
                        .fill(strength.color)
                        .frame(width: progressWidth(in: geometry.size.width), height: 4)
                }
            }
            .frame(height: 4)
        }
    }

    func progressWidth(in totalWidth: CGFloat) -> CGFloat {
        if password.isEmpty {
            return 0
        } else if password.count < 8 {
            return totalWidth * 0.33
        } else if password.count < 12 {
            return totalWidth * 0.66
        } else {
            return totalWidth
        }
    }
}

