// === PART 1: Implicit Animations ===
struct OnboardingView: View {
    @State private var isVisible = false
    @State private var featureOpacity = 0.0
    @State private var progress: Double = 0.0

    var body: some View {
        VStack(spacing: 30) {
            // Welcome card with implicit animation
            RoundedRectangle(cornerRadius: 20)
                .fill(LinearGradient(
                    colors: [.blue, .purple],
                    startPoint: .topLeading,
                    endPoint: .bottomTrailing
                ))
                .frame(width: isVisible ? 300 : 10, height: 200)
                .opacity(isVisible ? 1.0 : 0.0)
                .overlay(
                    Text("Welcome!")
                        .font(.largeTitle)
                        .foregroundColor(.white)
                        .opacity(isVisible ? 1.0 : 0.0)
                )
                .animation(.spring(response: 0.6, dampingFraction: 0.8), value: isVisible)

            // Feature list with sequential implicit animations
            VStack(alignment: .leading, spacing: 10) {
                FeatureRow(text: "Easy to use", delay: 0.0, opacity: featureOpacity)
                FeatureRow(text: "Powerful features", delay: 0.2, opacity: featureOpacity)
                FeatureRow(text: "Fast performance", delay: 0.4, opacity: featureOpacity)
            }

            // Animated progress indicator
            Circle()
                .trim(from: 0.0, to: progress)
                .stroke(Color.blue, lineWidth: 8)
                .frame(width: 60, height: 60)
                .rotationEffect(.degrees(-90))
                .animation(.easeInOut(duration: 1.0), value: progress)
        }
        .onAppear {
            isVisible = true

            // Sequence the animations
            DispatchQueue.main.asyncAfter(deadline: .now() + 0.5) {
                withAnimation(.easeInOut(duration: 0.5)) {
                    featureOpacity = 1.0
                }
            }

            DispatchQueue.main.asyncAfter(deadline: .now() + 1.0) {
                withAnimation(.easeInOut(duration: 2.0)) {
                    progress = 0.75
                }
            }
        }
    }
}

struct FeatureRow: View {
    let text: String
    let delay: Double
    let opacity: Double

    var body: some View {
        HStack {
            Image(systemName: "checkmark.circle.fill")
                .foregroundColor(.green)
                .scaleEffect(opacity > 0 ? 1.0 : 0.1)

            Text(text)
                .opacity(opacity)
        }
        .animation(.spring().delay(delay), value: opacity)
    }
}

// === PART 2: Explicit Animations ===
struct AnimatedButton: View {
    @State private var isPressed = false
    @State private var isSuccess = false

    var body: some View {
        Button(action: {
            withAnimation(.spring(response: 0.3, dampingFraction: 0.6)) {
                isPressed = true
                isSuccess.toggle()
            }

            // Reset press state
            DispatchQueue.main.asyncAfter(deadline: .now() + 0.1) {
                withAnimation(.spring()) {
                    isPressed = false
                }
            }
        }) {
            HStack {
                if isSuccess {
                    Image(systemName: "checkmark")
                        .transition(.scale.combined(with: .opacity))
                }

                Text(isSuccess ? "Completed!" : "Get Started")
            }
            .foregroundColor(.white)
            .padding()
            .frame(width: isPressed ? 170 : 160)
            .background(isSuccess ? Color.green : Color.blue)
            .cornerRadius(isPressed ? 12 : 8)
        }
    }
}

// Card flip with explicit animation
struct FlipCard: View {
    @State private var isFlipped = false
    @State private var rotation = 0.0

    var body: some View {
        VStack {
            RoundedRectangle(cornerRadius: 20)
                .fill(isFlipped ? Color.green : Color.blue)
                .frame(width: 200, height: 150)
                .overlay(
                    Group {
                        if isFlipped {
                            Text("Back Side")
                                .foregroundColor(.white)
                                .font(.title2)
                        } else {
                            Text("Front Side")
                                .foregroundColor(.white)
                                .font(.title2)
                        }
                    }
                )
                .rotation3DEffect(
                    .degrees(rotation),
                    axis: (x: 0.0, y: 1.0, z: 0.0)
                )

            Button("Flip Card") {
                withAnimation(.easeInOut(duration: 0.6)) {
                    rotation += 180
                    if rotation.truncatingRemainder(dividingBy: 360) == 0 {
                        isFlipped = false
                    } else {
                        isFlipped = true
                    }
                }
            }
        }
    }
}

// === PART 3: Combined System ===
struct OnboardingFlow: View {
    @State private var currentPage = 0
    @State private var isAnimating = false

    var body: some View {
        ZStack {
            // Background color transition
            Rectangle()
                .fill(backgroundGradient)
                .ignoresSafeArea()
                .animation(.easeInOut(duration: 0.5), value: currentPage)

            VStack {
                // Page content with transition
                Group {
                    switch currentPage {
                    case 0:
                        WelcomePage()
                    case 1:
                        FeaturesPage()
                    case 2:
                        GetStartedPage()
                    default:
                        WelcomePage()
                    }
                }
                .transition(.asymmetric(
                    insertion: .move(edge: .trailing).combined(with: .opacity),
                    removal: .move(edge: .leading).combined(with: .opacity)
                ))

                Spacer()

                // Page indicators with implicit animation
                HStack(spacing: 8) {
                    ForEach(0..<3, id: \.self) { index in
                        Circle()
                            .fill(index == currentPage ? Color.white : Color.white.opacity(0.3))
                            .frame(width: 8, height: 8)
                            .scaleEffect(index == currentPage ? 1.2 : 1.0)
                            .animation(.spring(response: 0.3, dampingFraction: 0.6), value: currentPage)
                    }
                }

                // Navigation buttons
                HStack {
                    if currentPage > 0 {
                        Button("Previous") {
                            withAnimation(.easeInOut(duration: 0.4)) {
                                currentPage -= 1
                            }
                        }
                    }

                    Spacer()

                    Button(currentPage == 2 ? "Get Started" : "Next") {
                        withAnimation(.easeInOut(duration: 0.4)) {
                            if currentPage < 2 {
                                currentPage += 1
                            }
                        }
                    }
                }
                .padding()
            }
        }
    }

    private var backgroundGradient: LinearGradient {
        let colors: [Color] = [
            [.blue, .purple],
            [.purple, .pink],
            [.orange, .red]
        ][currentPage]

        return LinearGradient(
            colors: colors,
            startPoint: .topLeading,
            endPoint: .bottomTrailing
        )
    }
}

// === PART 4: Advanced Patterns ===
struct StaggeredGrid: View {
    @State private var isVisible = false
    let items = Array(1...6)

    var body: some View {
        LazyVGrid(columns: [GridItem(.flexible()), GridItem(.flexible())], spacing: 10) {
            ForEach(Array(items.enumerated()), id: \.offset) { index, item in
                RoundedRectangle(cornerRadius: 12)
                    .fill(Color.blue)
                    .frame(height: 80)
                    .overlay(Text("Item \(item)").foregroundColor(.white))
                    .scaleEffect(isVisible ? 1.0 : 0.1)
                    .opacity(isVisible ? 1.0 : 0.0)
                    .animation(
                        .spring(response: 0.6, dampingFraction: 0.8)
                        .delay(Double(index) * 0.1),
                        value: isVisible
                    )
            }
        }
        .padding()
        .onAppear {
            withAnimation {
                isVisible = true
            }
        }
    }
}

