// TodoManager.swift
import Foundation

class TodoManager {
    static let shared = TodoManager()
    private init() { loadTodos() }

    private var todos: [TodoItem] = []
    private let saveKey = "userTodos"

    // MARK: - Public API
    func addTodo(_ title: String, priority: Priority = .medium) -> TodoItem {
        let newTodo = TodoItem(title: title, priority: priority)
        todos.append(newTodo)
        saveTodos()
        return newTodo
    }

    func toggleComplete(_ id: UUID) {
        if let index = todos.firstIndex(where: { $0.id == id }) {
            todos[index].isCompleted.toggle()
            saveTodos()
        }
    }

    func deleteTodo(_ id: UUID) {
        todos.removeAll { $0.id == id }
        saveTodos()
    }

    func updateTodo(_ id: UUID, newTitle: String? = nil, newPriority: Priority? = nil) {
        if let index = todos.firstIndex(where: { $0.id == id }) {
            if let newTitle = newTitle {
                todos[index].title = newTitle
            }
            if let newPriority = newPriority {
                todos[index].priority = newPriority
            }
            saveTodos()
        }
    }

    func getTodos() -> [TodoItem] { return todos }
    func getIncomplete() -> [TodoItem] { return todos.filter { !$0.isCompleted } }
    func getCompleted() -> [TodoItem] { return todos.filter { $0.isCompleted } }

    // MARK: - Persistence
    private func saveTodos() {
        if let encoded = try? JSONEncoder().encode(todos) {
            UserDefaults.standard.set(encoded, forKey: saveKey)
        }
    }

    private func loadTodos() {
        if let data = UserDefaults.standard.data(forKey: saveKey),
           let decoded = try? JSONDecoder().decode([TodoItem].self, from: data) {
            todos = decoded
        }
    }
