import UIKit

class TodoViewController: UIViewController {
    @IBOutlet weak var tableView: UITableView!
    @IBOutlet weak var addButton: UIButton!
    @IBOutlet weak var textField: UITextField!
    @IBOutlet weak var priorityControl: UISegmentedControl!
    @IBOutlet weak var emptyStateView: UIView!
    @IBOutlet weak var emptyStateLabel: UILabel!

    private var todos: [TodoItem] = []

    override func viewDidLoad() {
        super.viewDidLoad()
        setupUI()
        loadData()
    }

    override func viewWillAppear(_ animated: Bool) {
        super.viewWillAppear(animated)
        loadData()
    }

    private func setupUI() {
        title = "My Todos"

        // Table View
        tableView.dataSource = self
        tableView.delegate = self
        tableView.rowHeight = UITableView.automaticDimension
        tableView.estimatedRowHeight = 60

        // Text Field
        textField.delegate = self
        textField.placeholder = "Add a new todo..."

        // Add Button
        addButton.layer.cornerRadius = 8
        addButton.backgroundColor = .systemBlue
        addButton.setTitleColor(.white, for: .normal)

        // Priority Control
        priorityControl.removeAllSegments()
        for (index, priority) in Priority.allCases.enumerated() {
            priorityControl.insertSegment(withTitle: priority.rawValue, at: index, animated: false)
        }
        priorityControl.selectedSegmentIndex = 1 // Medium

        // Empty State
        emptyStateLabel.text = "No todos yet!\nTap the + button to add your first todo."
        emptyStateLabel.textAlignment = .center
        emptyStateLabel.numberOfLines = 0

        // Register cell
        let nib = UINib(nibName: "TodoCell", bundle: nil)
        tableView.register(nib, forCellReuseIdentifier: "TodoCell")
    }

    private func loadData() {
        todos = TodoManager.shared.getTodos()
        updateEmptyState()
        tableView.reloadData()
    }

    private func updateEmptyState() {
        let isEmpty = todos.isEmpty
        emptyStateView.isHidden = !isEmpty
        tableView.isHidden = isEmpty
    }

    @IBAction func addButtonTapped(_ sender: UIButton) {
        addNewTodo()
    }

    private func addNewTodo() {
        guard let title = textField.text?.trimmingCharacters(in: .whitespaces), !title.isEmpty else {
            showAlert(title: "Oops!", message: "Please enter a todo title")
            return
        }

        let priority = Priority.allCases[priorityControl.selectedSegmentIndex]
        _ = TodoManager.shared.addTodo(title, priority: priority)

        // Reset UI
        textField.text = ""
        textField.resignFirstResponder()

        // Update data
        loadData()

        // Haptic feedback
        UIImpactFeedbackGenerator(style: .medium).impactOccurred()

        // Scroll to bottom
        if !todos.isEmpty {
            let indexPath = IndexPath(row: todos.count - 1, section: 0)
            tableView.scrollToRow(at: indexPath, at: .bottom, animated: true)
        }
    }

    private func showAlert(title: String, message: String) {
        let alert = UIAlertController(title: title, message: message, preferredStyle: .alert)
        alert.addAction(UIAlertAction(title: "OK", style: .default))
        present(alert, animated: true)
    }
}

// MARK: - TableView DataSource & Delegate
extension TodoViewController: UITableViewDataSource, UITableViewDelegate {
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return todos.count
    }

    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCell(withIdentifier: "TodoCell", for: indexPath) as! TodoCell
        let todo = todos[indexPath.row]
        cell.configure(with: todo)

        cell.onToggle = { [weak self] id in
            TodoManager.shared.toggleComplete(id)
            self?.loadData()
            UISelectionFeedbackGenerator().selectionChanged()
        }

        return cell
    }

    func tableView(_ tableView: UITableView, trailingSwipeActionsConfigurationForRowAt indexPath: IndexPath) -> UISwipeActionsConfiguration? {
        let delete = UIContextualAction(style: .destructive, title: "Delete") { [weak self] _, _, completion in
            let todo = self?.todos[indexPath.row]
            if let id = todo?.id {
                TodoManager.shared.deleteTodo(id)
                self?.loadData()
                UINotificationFeedbackGenerator().notificationOccurred(.success)
            }
            completion(true)
        }

        delete.image = UIImage(systemName: "trash")
        return UISwipeActionsConfiguration(actions: [delete])
    }
}

// MARK: - TextField Delegate
extension TodoViewController: UITextFieldDelegate {
    func textFieldShouldReturn(_ textField: UITextField) -> Bool {
        addNewTodo()
        return true
    }
}

