// MARK: - Model
struct Task: Identifiable, Codable {
    let id: UUID
    var title: String
    var description: String
    var isCompleted: Bool
    var dueDate: Date?
    var priority: Priority

    enum Priority: String, CaseIterable, Codable {
        case low, medium, high
    }

    init(id: UUID = UUID(), title: String, description: String = "", isCompleted: Bool = false, dueDate: Date? = nil, priority: Priority = .medium) {
        self.id = id
        self.title = title
        self.description = description
        self.isCompleted = isCompleted
        self.dueDate = dueDate
        self.priority = priority
    }

    var isOverdue: Bool {
        guard let dueDate = dueDate else { return false }
        return !isCompleted && dueDate < Date()
    }
}

// MARK: - ViewModel
class TaskViewModel: ObservableObject {
    @Published var tasks: [Task] = []
    @Published var isLoading = false
    @Published var errorMessage: String?
    @Published var searchText = ""
    @Published var selectedPriority: Task.Priority?

    var filteredTasks: [Task] {
        tasks.filter { task in
            let matchesSearch = searchText.isEmpty || 
                task.title.localizedCaseInsensitiveContains(searchText) ||
                task.description.localizedCaseInsensitiveContains(searchText)

            let matchesPriority = selectedPriority == nil || task.priority == selectedPriority

            return matchesSearch && matchesPriority
        }
    }

    var completedTasks: [Task] {
        filteredTasks.filter { $0.isCompleted }
    }

    var incompleteTasks: [Task] {
        filteredTasks.filter { !$0.isCompleted }
    }

    var overdueTasks: [Task] {
        filteredTasks.filter { $0.isOverdue }
    }

    // MARK: - Intent Methods

    @MainActor
    func loadTasks() async {
        isLoading = true
        errorMessage = nil

        // Simulate network request
        do {
            try await Task.sleep(nanoseconds: 1_000_000_000) // 1 second

            // Sample data
            tasks = [
                Task(title: "Learn SwiftUI", description: "Complete MVVM tutorial", priority: .high),
                Task(title: "Build Todo App", isCompleted: true, priority: .medium),
                Task(title: "Study Combine", description: "Reactive programming", dueDate: Date().addingTimeInterval(86400), priority: .high),
                Task(title: "Read documentation", dueDate: Date().addingTimeInterval(-86400), priority: .low)
            ]
        } catch {
            errorMessage = "Failed to load tasks: \(error.localizedDescription)"
        }

        isLoading = false
    }

    func addTask(title: String, description: String = "", dueDate: Date? = nil, priority: Task.Priority = .medium) {
        let newTask = Task(title: title, description: description, dueDate: dueDate, priority: priority)
        tasks.append(newTask)
    }

    func toggleTaskCompletion(_ task: Task) {
        if let index = tasks.firstIndex(where: { $0.id == task.id }) {
            tasks[index].isCompleted.toggle()
        }
    }

    func deleteTask(_ task: Task) {
        tasks.removeAll { $0.id == task.id }
    }

    func deleteTasks(at offsets: IndexSet) {
        tasks.remove(atOffsets: offsets)
    }

    func updateTask(_ task: Task) {
        if let index = tasks.firstIndex(where: { $0.id == task.id }) {
            tasks[index] = task
        }
    }
}

// MARK: - View
struct TaskListView: View {
    @StateObject private var viewModel = TaskViewModel()
    @State private var showingAddTask = false

    var body: some View {
        NavigationView {
            VStack {
                // Search and Filter
                VStack {
                    TextField("Search tasks...", text: $viewModel.searchText)
                        .textFieldStyle(RoundedBorderTextFieldStyle())
                        .padding(.horizontal)

                    ScrollView(.horizontal, showsIndicators: false) {
                        HStack {
                            FilterButton(title: "All", isSelected: viewModel.selectedPriority == nil) {
                                viewModel.selectedPriority = nil
                            }

                            ForEach(Task.Priority.allCases, id: \.self) { priority in
                                FilterButton(
                                    title: priority.rawValue.capitalized,
                                    isSelected: viewModel.selectedPriority == priority
                                ) {
                                    viewModel.selectedPriority = priority
                                }
                            }
                        }
                        .padding(.horizontal)
                    }
                }

                // Content
                if viewModel.isLoading {
                    ProgressView("Loading tasks...")
                        .frame(maxHeight: .infinity)
                } else if let error = viewModel.errorMessage {
                    VStack {
                        Image(systemName: "exclamationmark.triangle")
                            .font(.largeTitle)
                            .foregroundColor(.orange)
                        Text("Error loading tasks")
                        Text(error)
                            .font(.caption)
                            .foregroundColor(.secondary)
                        Button("Try Again") {
                            Task {
                                await viewModel.loadTasks()
                            }
                        }
                        .buttonStyle(.bordered)
                    }
                    .frame(maxHeight: .infinity)
                } else {
                    List {
                        // Overdue Section
                        if !viewModel.overdueTasks.isEmpty {
                            Section("Overdue") {
                                ForEach(viewModel.overdueTasks) { task in
                                    TaskRowView(task: task, viewModel: viewModel)
                                        .foregroundColor(.red)
                                }
                            }
                        }

                        // Incomplete Tasks
                        Section("To Do (\(viewModel.incompleteTasks.count))" ) {
                            ForEach(viewModel.incompleteTasks) { task in
                                TaskRowView(task: task, viewModel: viewModel)
                            }
                            .onDelete { offsets in
                                let incompleteTasks = viewModel.incompleteTasks
                                let tasksToDelete = offsets.map { incompleteTasks[$0] }
                                for task in tasksToDelete {
                                    viewModel.deleteTask(task)
                                }
                            }
                        }

                        // Completed Tasks
                        Section("Completed (\(viewModel.completedTasks.count))" ) {
                            ForEach(viewModel.completedTasks) { task in
                                TaskRowView(task: task, viewModel: viewModel)
                                    .foregroundColor(.green)
                            }
                        }
                    }
                    .listStyle(InsetGroupedListStyle())
                }
            }
            .navigationTitle("My Tasks")
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button(action: { showingAddTask = true }) {
                        Image(systemName: "plus")
                    }
                }
            }
            .sheet(isPresented: $showingAddTask) {
                AddTaskView(viewModel: viewModel)
            }
        }
        .task {
            await viewModel.loadTasks()
        }
    }
}

// MARK: - Supporting Views
struct TaskRowView: View {
    let task: Task
    let viewModel: TaskViewModel

    var body: some View {
        HStack {
            Button(action: {
                viewModel.toggleTaskCompletion(task)
            }) {
                Image(systemName: task.isCompleted ? "checkmark.circle.fill" : "circle")
                    .foregroundColor(task.isCompleted ? .green : .gray)
            }
            .buttonStyle(PlainButtonStyle())

            VStack(alignment: .leading) {
                Text(task.title)
                    .strikethrough(task.isCompleted)
                    .font(.headline)

                if !task.description.isEmpty {
                    Text(task.description)
                        .font(.caption)
                        .foregroundColor(.secondary)
                        .strikethrough(task.isCompleted)
                }

                if let dueDate = task.dueDate {
                    HStack {
                        Image(systemName: "calendar")
                            .font(.caption2)
                        Text(dueDate, style: .date)
                            .font(.caption2)
                    }
                    .foregroundColor(task.isOverdue ? .red : .secondary)
                }
            }

            Spacer()

            PriorityView(priority: task.priority)
        }
        .padding(.vertical, 4)
    }
}

struct PriorityView: View {
    let priority: Task.Priority

    var color: Color {
        switch priority {
        case .low: return .green
        case .medium: return .orange
        case .high: return .red
        }
    }

    var body: some View {
        Text(priority.rawValue.capitalized)
            .font(.caption)
            .padding(.horizontal, 8)
            .padding(.vertical, 4)
            .background(color.opacity(0.2))
            .foregroundColor(color)
            .cornerRadius(4)
    }
}

struct FilterButton: View {
    let title: String
    let isSelected: Bool
    let action: () -> Void

    var body: some View {
        Button(action: action) {
            Text(title)
                .font(.caption)
                .padding(.horizontal, 12)
                .padding(.vertical, 6)
                .background(isSelected ? Color.blue : Color.gray.opacity(0.2))
                .foregroundColor(isSelected ? .white : .primary)
                .cornerRadius(8)
        }
    }
}

struct AddTaskView: View {
    @Environment(\.dismiss) private var dismiss
    @ObservedObject var viewModel: TaskViewModel

    @State private var title = ""
    @State private var description = ""
    @State private var dueDate = Date()
    @State private var hasDueDate = false
    @State private var priority: Task.Priority = .medium

    var body: some View {
        NavigationView {
            Form {
                Section("Task Details") {
                    TextField("Title", text: $title)
                    TextField("Description (optional)", text: $description)
                }

                Section("Priority") {
                    Picker("Priority", selection: $priority) {
                        ForEach(Task.Priority.allCases, id: \.self) { priority in
                            Text(priority.rawValue.capitalized).tag(priority)
                        }
                    }
                    .pickerStyle(SegmentedPickerStyle())
                }

                Section("Due Date") {
                    Toggle("Add due date", isOn: $hasDueDate)

                    if hasDueDate {
                        DatePicker("Due Date", selection: $dueDate, in: Date()..., displayedComponents: .date)
                    }
                }
            }
            .navigationTitle("Add Task")
            .toolbar {
                ToolbarItem(placement: .cancellationAction) {
                    Button("Cancel") {
                        dismiss()
                    }
                }

                ToolbarItem(placement: .confirmationAction) {
                    Button("Add") {
                        viewModel.addTask(
                            title: title,
                            description: description,
                            dueDate: hasDueDate ? dueDate : nil,
                            priority: priority
                        )
                        dismiss()
                    }
                    .disabled(title.isEmpty)
                }
            }
        }
    }
}

