import Foundation

struct Weather: Identifiable, Codable {
    let id = UUID()
    let location: String
    let temperature: Double
    let condition: WeatherCondition
    let humidity: Int
    let windSpeed: Double
    let feelsLike: Double
    let sunrise: Date
    let sunset: Date

    var temperatureString: String {
        return "\(Int(temperature))Â°"
    }

    var feelsLikeString: String {
        return "\(Int(feelsLike))Â°"
    }

    var conditionIcon: String {
        switch condition {
        case .sunny: return "sun.max.fill"
        case .cloudy: return "cloud.fill"
        case .rainy: return "cloud.rain.fill"
        case .snowy: return "snow"
        case .thunderstorm: return "cloud.bolt.fill"
        }
    }

    var isDaytime: Bool {
        let now = Date()
        return now >= sunrise && now <= sunset
    }
}

enum WeatherCondition: String, Codable, CaseIterable {
    case sunny, cloudy, rainy, snowy, thunderstorm
}

enum TemperatureUnit: String, CaseIterable {
    case celsius, fahrenheit

    var symbol: String {
        switch self {
        case .celsius: return "Â°C"
        case .fahrenheit: return "Â°F"
        }
    }
}

enum WeatherError: LocalizedError {
    case networkError
    case locationNotFound
    case invalidData

    var errorDescription: String? {
        switch self {
        case .networkError: return "Unable to fetch weather data"
        case .locationNotFound: return "Location not found"
        case .invalidData: return "Invalid weather data received"
        }
    }
}

// Service Protocol for dependency injection
protocol WeatherServiceProtocol {
    func fetchWeather(for location: String) async throws -> Weather
    func fetchWeather(for coordinates: (Double, Double)) async throws -> Weather
}

// Mock implementation for testing
class MockWeatherService: WeatherServiceProtocol {
    func fetchWeather(for location: String) async throws -> Weather {
        // Simulate network delay
        try await Task.sleep(nanoseconds: 1_000_000_000)

        return Weather(
            location: location,
            temperature: 22.0,
            condition: .sunny,
            humidity: 65,
            windSpeed: 5.5,
            feelsLike: 24.0,
            sunrise: Date(),
            sunset: Date().addingTimeInterval(43200)
        )
    }

    func fetchWeather(for coordinates: (Double, Double)) async throws -> Weather {
        try await fetchWeather(for: "Current Location")
    }
}

// === PART 2: ViewModel Layer ===
import Combine
import SwiftUI

class WeatherViewModel: ObservableObject {
    @Published var currentWeather: Weather?
    @Published var isLoading = false
    @Published var error: WeatherError?
    @Published var searchText = ""
    @Published var temperatureUnit: TemperatureUnit = .celsius

    private let weatherService: WeatherServiceProtocol
    private var cancellables = Set<AnyCancellable>()

    init(weatherService: WeatherServiceProtocol = MockWeatherService()) {
        self.weatherService = weatherService

        // Debounce search
        $searchText
            .debounce(for: .seconds(0.5), scheduler: RunLoop.main)
            .removeDuplicates()
            .sink { [weak self] query in
                if !query.isEmpty {
                    self?.fetchWeather(for: query)
                }
            }
            .store(in: &cancellables)
    }

    @MainActor
    func fetchWeather(for location: String) {
        guard !location.isEmpty else { return }

        isLoading = true
        error = nil

        Task {
            do {
                let weather = try await weatherService.fetchWeather(for: location)
                currentWeather = weather
            } catch {
                self.error = WeatherError.networkError
            }
            isLoading = false
        }
    }

    func toggleTemperatureUnit() {
        temperatureUnit = temperatureUnit == .celsius ? .fahrenheit : .celsius
    }

    func convertTemperature(_ celsius: Double) -> Double {
        switch temperatureUnit {
        case .celsius:
            return celsius
        case .fahrenheit:
            return (celsius * 9/5) + 32
        }
    }

    func formattedTemperature(_ celsius: Double) -> String {
        let temp = convertTemperature(celsius)
        return "\(Int(temp))\(temperatureUnit.symbol)"
    }
}

// === PART 3: View Layer ===
struct WeatherView: View {
    @StateObject private var viewModel = WeatherViewModel()

    var body: some View {
        NavigationView {
            VStack(spacing: 20) {
                // Search Bar
                HStack {
                    Image(systemName: "magnifyingglass")
                        .foregroundColor(.secondary)
                    TextField("Search location...", text: $viewModel.searchText)
                }
                .padding()
                .background(Color(.systemGray6))
                .cornerRadius(10)
                .padding(.horizontal)

                // Content
                if viewModel.isLoading {
                    ProgressView("Fetching weather...")
                        .frame(maxHeight: .infinity)
                } else if let error = viewModel.error {
                    VStack {
                        Image(systemName: "exclamationmark.triangle")
                            .font(.largeTitle)
                            .foregroundColor(.orange)
                        Text(error.localizedDescription)
                            .multilineTextAlignment(.center)
                        Button("Try Again") {
                            if !viewModel.searchText.isEmpty {
                                viewModel.fetchWeather(for: viewModel.searchText)
                            }
                        }
                        .buttonStyle(.bordered)
                    }
                    .frame(maxHeight: .infinity)
                } else if let weather = viewModel.currentWeather {
                    ScrollView {
                        VStack(spacing: 20) {
                            // Current Weather
                            VStack(spacing: 10) {
                                Text(weather.location)
                                    .font(.title2)
                                    .fontWeight(.semibold)

                                HStack {
                                    Image(systemName: weather.conditionIcon)
                                        .font(.system(size: 60))
                                        .foregroundColor(weather.isDaytime ? .orange : .blue)

                                    Text(viewModel.formattedTemperature(weather.temperature))
                                        .font(.system(size: 50, weight: .thin))
                                }

                                Text(weather.condition.rawValue.capitalized)
                                    .font(.title3)
                                    .foregroundColor(.secondary)

                                Text("Feels like \(viewModel.formattedTemperature(weather.feelsLike))")
                                    .foregroundColor(.secondary)
                            }
                            .padding()
                            .background(Color(.systemGray6))
                            .cornerRadius(15)

                            // Weather Details
                            LazyVGrid(columns: [GridItem(.flexible()), GridItem(.flexible())], spacing: 10) {
                                WeatherDetailCard(title: "Humidity", value: "\(weather.humidity)%", icon: "humidity")
                                WeatherDetailCard(title: "Wind Speed", value: "\(weather.windSpeed) km/h", icon: "wind")
                            }
                        }
                        .padding()
                    }
                } else {
                    VStack {
                        Image(systemName: "cloud.sun")
