struct StopwatchView: View {
    @State private var seconds = 0
    @State private var timer: Timer?
    @State private var isRunning = false

    var body: some View {
        VStack(spacing: 20) {
            Text("\(seconds) seconds")
                .font(.system(size: 40, weight: .bold))

            HStack {
                Button(isRunning ? "Stop" : "Start") {
                    toggleTimer()
                }
                .buttonStyle(.borderedProminent)

                Button("Reset") {
                    resetTimer()
                }
                .buttonStyle(.bordered)
            }
        }
        .onAppear {
            print("â±ï¸ Stopwatch view appeared")
            // Optional: Restore previous state
            restoreTimerState()
        }
        .onDisappear {
            print("ðŸ›‘ Stopwatch view disappeared")
            // Critical: Always stop timer when view disappears
            timer?.invalidate()
            timer = nil
            saveTimerState()
        }
    }

    private func toggleTimer() {
        if isRunning {
            timer?.invalidate()
            timer = nil
        } else {
            timer = Timer.scheduledTimer(withTimeInterval: 1, repeats: true) { _ in
                seconds += 1
            }
        }
        isRunning.toggle()
    }

    private func resetTimer() {
        timer?.invalidate()
        timer = nil
        seconds = 0
        isRunning = false
    }

    private func saveTimerState() {
        UserDefaults.standard.set(seconds, forKey: "stopwatchSeconds")
        UserDefaults.standard.set(isRunning, forKey: "stopwatchRunning")
    }

    private func restoreTimerState() {
        seconds = UserDefaults.standard.integer(forKey: "stopwatchSeconds")
        isRunning = UserDefaults.standard.bool(forKey: "stopwatchRunning")

        if isRunning {
            toggleTimer() // Restart timer if it was running
        }
    }
}

