struct ArticleDetailView: View {
    let article: Article
    @State private var readingProgress: Double = 0.0
    @State private var readingTimer: Timer?
    @State private var timeSpent: Int = 0
    @Environment(\.dismiss) private var dismiss

    var body: some View {
        ScrollView {
            VStack(alignment: .leading, spacing: 16) {
                Text(article.title)
                    .font(.title)

                Text(article.content)
                    .font(.body)

                // Reading progress indicator
                VStack {
                    Text("Reading Progress: \(Int(readingProgress * 100))%")
                    ProgressView(value: readingProgress)
                        .progressViewStyle(.linear)
                    Text("Time spent: \(timeSpent) seconds")
                }
                .padding()
                .background(Color.gray.opacity(0.1))
                .cornerRadius(8)
            }
            .padding()
        }
        .navigationTitle(article.title)
        .navigationBarTitleDisplayMode(.inline)
        .onAppear {
            print("ðŸ“– Article detail appeared: \(article.title)")
            startReadingSession()
            loadReadingProgress()
        }
        .onDisappear {
            print("ðŸ“• Article detail disappeared: \(article.title)")
            endReadingSession()
            saveReadingProgress()
        }
    }

    private func startReadingSession() {
        // Start tracking reading time
        readingTimer = Timer.scheduledTimer(withTimeInterval: 1, repeats: true) { _ in
            timeSpent += 1
            // Simulate progress based on time (in real app, track scroll position)
            readingProgress = min(readingProgress + 0.01, 1.0)
        }
        print("â±ï¸ Started reading session")
    }

    private func endReadingSession() {
        readingTimer?.invalidate()
        readingTimer = nil
        print("ðŸ›‘ Ended reading session. Total time: \(timeSpent)s")
    }

    private func loadReadingProgress() {
        let savedProgress = UserDefaults.standard.double(forKey: "progress_\(article.id)")
        let savedTime = UserDefaults.standard.integer(forKey: "time_\(article.id)")

        if savedProgress > 0 {
            readingProgress = savedProgress
            timeSpent = savedTime
            print("ðŸ“Š Loaded saved progress: \(Int(savedProgress * 100))%")
        }
    }

    private func saveReadingProgress() {
        UserDefaults.standard.set(readingProgress, forKey: "progress_\(article.id)")
        UserDefaults.standard.set(timeSpent, forKey: "time_\(article.id)")
        print("ðŸ’¾ Saved progress: \(Int(readingProgress * 100))%")
    }
}

