import SwiftUI

// 1. PillBadge modifier
struct PillBadge: ViewModifier {
    var backgroundColor: Color = .blue
    var textColor: Color = .white

    func body(content: Content) -> some View {
        content
            .font(.caption)
            .fontWeight(.semibold)
            .foregroundColor(textColor)
            .padding(.horizontal, 12)
            .padding(.vertical, 6)
            .background(backgroundColor)
            .clipShape(Capsule())
    }
}

extension View {
    func pillBadge(backgroundColor: Color = .blue, textColor: Color = .white) -> some View {
        self.modifier(PillBadge(backgroundColor: backgroundColor, textColor: textColor))
    }
}

// 2. GradientBackground modifier
struct GradientBackgroundModifier: ViewModifier {
    let colors: [Color]
    let startPoint: UnitPoint
    let endPoint: UnitPoint

    func body(content: Content) -> some View {
        content
            .background(
                LinearGradient(
                    gradient: Gradient(colors: colors),
                    startPoint: startPoint,
                    endPoint: endPoint
                )
            )
    }
}

extension View {
    func gradientBackground(colors: [Color], startPoint: UnitPoint = .topLeading, endPoint: UnitPoint = .bottomTrailing) -> some View {
        self.modifier(GradientBackgroundModifier(colors: colors, startPoint: startPoint, endPoint: endPoint))
    }
}

// 3. BorderGlow modifier
struct BorderGlow: ViewModifier {
    var color: Color = .blue
    var radius: CGFloat = 10

    func body(content: Content) -> some View {
        content
            .overlay(
                RoundedRectangle(cornerRadius: 8)
                    .stroke(color, lineWidth: 1)
                    .shadow(color: color, radius: radius)
                    .shadow(color: color, radius: radius)
            )
    }
}

extension View {
    func borderGlow(color: Color = .blue, radius: CGFloat = 10) -> some View {
        self.modifier(BorderGlow(color: color, radius: radius))
    }
}

// 4. TextOutline modifier
struct TextOutline: ViewModifier {
    var outlineColor: Color = .black
    var outlineWidth: CGFloat = 2

    func body(content: Content) -> some View {
        content
            .shadow(color: outlineColor, radius: 0)
            .shadow(color: outlineColor, radius: 0)
            .shadow(color: outlineColor, radius: 0)
            .shadow(color: outlineColor, radius: 0)
    }
}

extension View {
    func textOutline(color: Color = .black, width: CGFloat = 2) -> some View {
        self.modifier(TextOutline(outlineColor: color, outlineWidth: width))
    }
}

