struct AppTheme {
    static let cornerRadius: CGFloat = 12
    static let padding: CGFloat = 16
    static let smallPadding: CGFloat = 8

    // Colors
    static let primary = Color.blue
    static let secondary = Color.orange
    static let success = Color.green
    static let danger = Color.red
    static let warning = Color.yellow

    // Gradients
    static let primaryGradient = [Color.blue, Color.purple]
    static let successGradient = [Color.green, Color.blue]

    // Fonts
    static let title = Font.title2.weight(.bold)
    static let headline = Font.headline.weight(.semibold)
    static let body = Font.body
}

// 10. Modifier variants
struct PrimaryButtonModifier: ViewModifier {
    var isDisabled: Bool = false

    func body(content: Content) -> some View {
        content
            .font(.headline)
            .foregroundColor(.white)
            .frame(maxWidth: .infinity)
            .padding()
            .background(isDisabled ? Color.gray : AppTheme.primary)
            .cornerRadius(AppTheme.cornerRadius)
    }
}

struct SecondaryButtonModifier: ViewModifier {
    func body(content: Content) -> some View {
        content
            .font(.headline)
            .foregroundColor(AppTheme.primary)
            .frame(maxWidth: .infinity)
            .padding()
            .background(Color.clear)
            .overlay(
                RoundedRectangle(cornerRadius: AppTheme.cornerRadius)
                    .stroke(AppTheme.primary, lineWidth: 2)
            )
    }
}

extension View {
    func primaryButtonStyle(disabled: Bool = false) -> some View {
        self.modifier(PrimaryButtonModifier(isDisabled: disabled))
    }

    func secondaryButtonStyle() -> some View {
        self.modifier(SecondaryButtonModifier())
    }
}

// 11. Conditional modifier
struct ConditionalScale: ViewModifier {
    let condition: Bool

    func body(content: Content) -> some View {
        content
            .scaleEffect(condition ? 1.05 : 1.0)
            .animation(.spring(response: 0.3), value: condition)
    }
}

extension View {
    func conditionalScale(_ condition: Bool) -> some View {
        self.modifier(ConditionalScale(condition: condition))
    }
}

// 12. Animated modifier
struct BounceOnAppear: ViewModifier {
    @State private var isVisible = false

    func body(content: Content) -> some View {
        content
            .scaleEffect(isVisible ? 1.0 : 0.5)
            .opacity(isVisible ? 1.0 : 0.0)
            .onAppear {
                withAnimation(.spring(response: 0.6, dampingFraction: 0.6)) {
                    isVisible = true
                }
            }
    }
}

extension View {
    func bounceOnAppear() -> some View {
        self.modifier(BounceOnAppear())
    }
}

