    let sunrise: Date
    let sunset: Date

    var temperatureFahrenheit: Double {
        return (temperature * 9/5) + 32
    }

    var windDirectionText: String {
        let directions = ["N", "NNE", "NE", "ENE", "E", "ESE", "SE", "SSE", 
                         "S", "SSW", "SW", "WSW", "W", "WNW", "NW", "NNW"]
        let index = Int((Double(windDirection) + 11.25) / 22.5) % 16
        return directions[index]
    }
}

// Weather condition with emoji and description
enum WeatherCondition: String, CaseIterable, Codable {
    case clear = "Clear"
    case cloudy = "Cloudy"
    case partlyCloudy = "Partly Cloudy"
    case rainy = "Rainy"
    case stormy = "Stormy"
    case snowy = "Snowy"
    case foggy = "Foggy"
    case windy = "Windy"

    var emoji: String {
        switch self {
        case .clear: return "â˜€ï¸"
        case .cloudy: return "â˜ï¸"
        case .partlyCloudy: return "â›…"
        case .rainy: return "ðŸŒ§ï¸"
        case .stormy: return "â›ˆï¸"
        case .snowy: return "â„ï¸"
        case .foggy: return "ðŸŒ«ï¸"
        case .windy: return "ðŸ’¨"
        }
    }

    var color: String {
        switch self {
        case .clear: return "FFD700" // Gold
        case .cloudy: return "A9A9A9" // Dark Gray
        case .partlyCloudy: return "87CEEB" // Sky Blue
        case .rainy: return "4682B4" // Steel Blue
        case .stormy: return "2F4F4F" // Dark Slate Gray
        case .snowy: return "F0F8FF" // Alice Blue
        case .foggy: return "D3D3D3" // Light Gray
        case .windy: return "B0C4DE" // Light Steel Blue
        }
    }
}

// Daily forecast
struct DailyForecast: Codable, Identifiable {
    let id = UUID()
    let date: Date
    let highTemperature: Double
    let lowTemperature: Double
    let condition: WeatherCondition
    let precipitationChance: Int
    let sunrise: Date
    let sunset: Date
    let moonPhase: String

    var dayOfWeek: String {
        let formatter = DateFormatter()
        formatter.dateFormat = "EEEE"
        return formatter.string(from: date)
    }
}

// Location information
struct WeatherLocation: Codable, Identifiable, Hashable {
    let id = UUID()
    let name: String
    let country: String
    let latitude: Double
    let longitude: Double
    let timezone: String

    var displayName: String {
        return "\(name), \(country)"
    }
}

// Complete weather data package
struct WeatherData: Codable {
    let location: WeatherLocation
    let current: CurrentWeather
    let forecast: [DailyForecast]
    let hourly: [HourlyWeather]
    let lastUpdated: Date
}

// Hourly weather for today
struct HourlyWeather: Codable, Identifiable {
    let id = UUID()
    let time: Date
    let temperature: Double
    let condition: WeatherCondition
    let precipitationChance: Int

    var hourString: String {
        let formatter = DateFormatter()
        formatter.dateFormat = "h a"
        return formatter.string(from: time)
    }
}

// === WEATHER VIEW MODEL ===

import SwiftUI
import Combine

class WeatherViewModel: ObservableObject {
    @Published var weatherData: WeatherData?
    @Published var isLoading = false
    @Published var errorMessage: String?
    @Published var selectedLocation: WeatherLocation?

    @AppStorage("temperatureUnit") var temperatureUnit: String = "celsius"
    @AppStorage("selectedLocationId") var selectedLocationId: String = ""

    private let weatherService = WeatherService()
    private var cancellables = Set<AnyCancellable>()

    // Mock locations for the app
    let mockLocations = [
        WeatherLocation(name: "New York", country: "US", latitude: 40.7128, longitude: -74.0060, timezone: "America/New_York"),
        WeatherLocation(name: "London", country: "UK", latitude: 51.5074, longitude: -0.1278, timezone: "Europe/London"),
        WeatherLocation(name: "Tokyo", country: "Japan", latitude: 35.6762, longitude: 139.6503, timezone: "Asia/Tokyo"),
        WeatherLocation(name: "Sydney", country: "Australia", latitude: -33.8688, longitude: 151.2093, timezone: "Australia/Sydney"),
        WeatherLocation(name: "Paris", country: "France", latitude: 48.8566, longitude: 2.3522, timezone: "Europe/Paris")
    ]

    init() {
        loadInitialData()
    }

    func loadInitialData() {
        // Set default location if none selected
        if selectedLocation == nil {
            selectedLocation = mockLocations.first
        }

        if let location = selectedLocation {
            loadWeather(for: location)
        }
    }

    func loadWeather(for location: WeatherLocation) {
        isLoading = true
        errorMessage = nil

        weatherService.fetchWeather(for: location)
            .receive(on: DispatchQueue.main)
            .sink { [weak self] completion in
                self?.isLoading = false
                if case .failure(let error) = completion {
                    self?.errorMessage = error.localizedDescription
                }
            } receiveValue: { [weak self] weatherData in
                self?.weatherData = weatherData
                self?.selectedLocation = location
                self?.selectedLocationId = location.id.uuidString
            }
            .store(in: &cancellables)
    }

    func searchLocations(query: String) -> [WeatherLocation] {
        if query.isEmpty {
            return mockLocations
        }

        return mockLocations.filter { location in
            location.name.localizedCaseInsensitiveContains(query) ||
            location.country.localizedCaseInsensitiveContains(query)
        }
    }

    // Temperature formatting based on user preference
    func formattedTemperature(_ temperature: Double) -> String {
        if temperatureUnit == "fahrenheit" {
            let fahrenheit = (temperature * 9/5) + 32
            return "\(Int(round(fahrenheit)))Â°F"
        } else {
            return "\(Int(round(temperature)))Â°C"
        }
    }

    // Toggle temperature unit
    func toggleTemperatureUnit() {
        temperatureUnit = temperatureUnit == "celsius" ? "fahrenheit" : "celsius"
        objectWillChange.send()
    }
}

// === WEATHER SERVICE (MOCK DATA) ===

class WeatherService {
    func fetchWeather(for location: WeatherLocation) -> AnyPublisher<WeatherData, Error> {
        // Generate mock weather data based on location and current time
        let mockWeatherData = generateMockWeatherData(for: location)

        return Just(mockWeatherData)
            .setFailureType(to: Error.self)
            .delay(for: .seconds(1), scheduler: RunLoop.main) // Simulate network delay
            .eraseToAnyPublisher()
    }

    private func generateMockWeatherData(for location: WeatherLocation) -> WeatherData {
        let currentDate = Date()
        let calendar = Calendar.current

        // Generate current weather
        let currentWeather = CurrentWeather(
            temperature: Double.random(in: -5...35), // Realistic temperature range
            feelsLike: Double.random(in: -8...38),
            condition: WeatherCondition.allCases.randomElement()!,
            humidity: Int.random(in: 30...90),
            windSpeed: Double.random(in: 0...25),
            windDirection: Int.random(in: 0...359),
            pressure: Int.random(in: 980...1040),
            visibility: Double.random(in: 2...20),
            uvIndex: Int.random(in: 0...11),
            sunrise: calendar.date(byAdding: .hour, value: -2, to: currentDate)!,
            sunset: calendar.date(byAdding: .hour, value: 10, to: currentDate)!
        )

        // Generate 5-day forecast
        var forecast: [DailyForecast] = []
        for day in 0..<5 {
            let forecastDate = calendar.date(byAdding: .day, value: day, to: currentDate)!
            let dailyForecast = DailyForecast(
                date: forecastDate,
                highTemperature: Double.random(in: currentWeather.temperature - 5...currentWeather.temperature + 8),
                lowTemperature: Double.random(in: currentWeather.temperature - 15...currentWeather.temperature - 2),
                condition: WeatherCondition.allCases.randomElement()!,
                precipitationChance: Int.random(in: 0...100),
                sunrise: calendar.date(byAdding: .hour, value: -2, to: forecastDate)!,
                sunset: calendar.date(byAdding: .hour, value: 10, to: forecastDate)!,
                moonPhase: ["New Moon", "Waxing Crescent", "First Quarter", "Waxing Gibbous", 
                           "Full Moon", "Waning Gibbous", "Last Quarter", "Waning Crescent"].randomElement()!
            )
            forecast.append(dailyForecast)
        }

        // Generate 24-hour forecast
        var hourly: [HourlyWeather] = []
        for hour in 0..<24 {
            let hourDate = calendar.date(byAdding: .hour, value: hour, to: currentDate)!
            let hourlyWeather = HourlyWeather(
                time: hourDate,
                temperature: Double.random(in: currentWeather.temperature - 8...currentWeather.temperature + 5),
                condition: currentWeather.condition, // Same condition for simplicity
                precipitationChance: Int.random(in: 0...30)
            )
            hourly.append(hourlyWeather)
        }

        return WeatherData(
            location: location,
            current: currentWeather,
            forecast: forecast,
            hourly: hourly,
            lastUpdated: currentDate
        )
    }
}

