import SwiftUI

struct Photo: Identifiable {
    let id = UUID()
    let name: String
    let category: String
    let color: Color
}

struct PhotoGalleryView: View {
    let samplePhotos = [
        Photo(name: "Sunset", category: "Nature", color: .orange),
        Photo(name: "Mountain", category: "Nature", color: .green),
        Photo(name: "Beach", category: "Travel", color: .blue),
        Photo(name: "City", category: "Urban", color: .gray),
        Photo(name: "Forest", category: "Nature", color: .green),
        Photo(name: "Food", category: "Lifestyle", color: .red),
        Photo(name: "Architecture", category: "Urban", color: .brown),
        Photo(name: "Portrait", category: "People", color: .purple),
        Photo(name: "Abstract", category: "Art", color: .pink),
        Photo(name: "Wildlife", category: "Nature", color: .yellow),
        Photo(name: "Night", category: "Urban", color: .black),
        Photo(name: "Macro", category: "Nature", color: .green),
        Photo(name: "Street", category: "Urban", color: .gray),
        Photo(name: "Sports", category: "Action", color: .red),
        Photo(name: "Concert", category: "Events", color: .purple),
        Photo(name: "Wedding", category: "Events", color: .white),
        Photo(name: "Product", category: "Commercial", color: .blue),
        Photo(name: "Fashion", category: "Lifestyle", color: .pink),
        Photo(name: "Aerial", category: "Travel", color: .cyan),
        Photo(name: "Black & White", category: "Art", color: .gray)
    ]

    @State private var selectedLayout: GridLayout = .adaptive
    @State private var selectedCategory: String? = nil
    @State private var searchText = ""
    @State private var isGridView = true
    @State private var isRefreshing = false
    @State private var selectedPhoto: Photo?

    enum GridLayout: CaseIterable {
        case twoColumn, threeColumn, adaptive

        var columns: [GridItem] {
            switch self {
            case .twoColumn:
                return [GridItem(.flexible()), GridItem(.flexible())]
            case .threeColumn:
                return [GridItem(.flexible()), GridItem(.flexible()), GridItem(.flexible())]
            case .adaptive:
                return [GridItem(.adaptive(minimum: 120))]
            }
        }

        var title: String {
            switch self {
            case .twoColumn: return "2 Columns"
            case .threeColumn: return "3 Columns"
            case .adaptive: return "Adaptive"
            }
        }
    }

    var categories: [String] {
        Array(Set(samplePhotos.map { $0.category })).sorted()
    }

    var filteredPhotos: [Photo] {
        var filtered = samplePhotos

        // Filter by category
        if let category = selectedCategory {
            filtered = filtered.filter { $0.category == category }
        }

        // Filter by search text
        if !searchText.isEmpty {
            filtered = filtered.filter { $0.name.localizedCaseInsensitiveContains(searchText) }
        }

        return filtered
    }

    var body: some View {
        NavigationView {
            VStack(spacing: 0) {
                // Search Bar
                HStack {
                    Image(systemName: "magnifyingglass")
                        .foregroundColor(.secondary)
                    TextField("Search photos...", text: $searchText)
                    if !searchText.isEmpty {
                        Button(action: { searchText = "" }) {
                            Image(systemName: "xmark.circle.fill")
                                .foregroundColor(.secondary)
                        }
                    }
                }
                .padding(8)
                .background(Color(.systemGray6))
                .cornerRadius(8)
                .padding(.horizontal)
                .padding(.top, 8)

                // Category Horizontal Grid
                ScrollView(.horizontal, showsIndicators: false) {
                    LazyHGrid(rows: [GridItem(.fixed(40))], spacing: 8) {
                        ForEach(categories, id: \.self) { category in
                            CategoryPill(
                                category: category,
                                isSelected: selectedCategory == category,
                                action: {
                                    selectedCategory = selectedCategory == category ? nil : category
                                }
                            )
                        }
                    }
                    .padding(.horizontal)
                }
                .padding(.vertical, 8)

                // Layout Controls
                HStack {
                    Picker("Layout", selection: $selectedLayout) {
                        ForEach(GridLayout.allCases, id: \.self) { layout in
                            Text(layout.title).tag(layout)
                        }
                    }
                    .pickerStyle(SegmentedPickerStyle())

                    Button(action: { isGridView.toggle() }) {
                        Image(systemName: isGridView ? "square.grid.2x2" : "list.bullet")
                            .font(.title3)
                    }
                }
                .padding(.horizontal)
                .padding(.bottom, 8)

                // Main Content
                if filteredPhotos.isEmpty {
                    VStack {
                        Image(systemName: "photo.on.rectangle")
                            .font(.system(size: 60))
                            .foregroundColor(.secondary)
                        Text("No photos found")
                            .font(.headline)
                            .foregroundColor(.secondary)
                    }
                    .frame(maxWidth: .infinity, maxHeight: .infinity)
                } else {
                    ScrollView {
                        if isGridView {
                            LazyVGrid(columns: selectedLayout.columns, spacing: 12) {
                                ForEach(filteredPhotos) { photo in
                                    PhotoGridItem(photo: photo)
                                        .onTapGesture {
                                            selectedPhoto = photo
                                        }
                                        .accessibilityLabel(photo.name)
                                        .accessibilityHint("Double tap to view details")
                                }
                            }
                            .padding()
                        } else {
                            LazyVStack(spacing: 12) {
                                ForEach(filteredPhotos) { photo in
                                    PhotoListItem(photo: photo)
                                        .onTapGesture {
                                            selectedPhoto = photo
                                        }
                                }
                            }
                            .padding()
                        }
                    }
                    .refreshable {
                        await refreshData()
                    }
                }
            }
            .navigationTitle("Photo Gallery")
            .sheet(item: $selectedPhoto) { photo in
                PhotoDetailView(photo: photo)
            }
        }
        .animation(.spring(response: 0.3), value: selectedLayout)
        .animation(.easeInOut, value: isGridView)
        .animation(.easeInOut, value: filteredPhotos)
    }

    private func refreshData() async {
        isRefreshing = true
        // Simulate network request
        try? await Task.sleep(nanoseconds: 1_000_000_000)
        isRefreshing = false
    }
}

