import SwiftUI

// Task model
struct Task: Identifiable {
    let id = UUID()
    var title: String
    var description: String
    var isCompleted: Bool
    var dueDate: Date?
    var priority: Priority

    enum Priority: String, CaseIterable {
        case low, medium, high

        var color: Color {
            switch self {
            case .low: return .green
            case .medium: return .orange
            case .high: return .red
            }
        }
    }
}

// Modal Manager
class ModalManager: ObservableObject {
    @Published var activeModal: ActiveModal?

    enum ActiveModal: Identifiable {
        case addTask, settings, filters

        var id: String {
            switch self {
            case .addTask: return "addTask"
            case .settings: return "settings"
            case .filters: return "filters"
            }
        }
    }
}

// Main Task Manager View
struct TaskManagerView: View {
    @StateObject private var modalManager = ModalManager()
    @State private var tasks: [Task] = []
    @State private var selectedTask: Task?
    @State private var showDeleteAlert = false
    @State private var taskToDelete: Task?
    @State private var showRenameAlert = false
    @State private var newTaskName = ""
    @State private var taskToRename: Task?
    @State private var showCompletionAlert = false
    @State private var taskToComplete: Task?
    @State private var showDetailView = false
    @State private var selectedTaskForDetail: Task?

    var body: some View {
        NavigationView {
            List {
                ForEach(tasks) { task in
                    TaskRow(task: task)
                        .onTapGesture {
                            selectedTask = task
                        }
                        .contextMenu {
                            Button("Mark Complete") {
                                taskToComplete = task
                                showCompletionAlert = true
                            }
                            Button("Rename") {
                                taskToRename = task
                                newTaskName = task.title
                                showRenameAlert = true
                            }
                            Button("View Details") {
                                selectedTaskForDetail = task
                                showDetailView = true
                            }
                            Button("Delete", role: .destructive) {
                                taskToDelete = task
                                showDeleteAlert = true
                            }
                        }
                        .swipeActions(edge: .trailing) {
                            Button(role: .destructive) {
                                taskToDelete = task
                                showDeleteAlert = true
                            } label: {
                                Label("Delete", systemImage: "trash")
                            }
                        }
                        .swipeActions(edge: .leading) {
                            Button {
                                taskToComplete = task
                                showCompletionAlert = true
                            } label: {
                                Label("Complete", systemImage: "checkmark")
                            }
                            .tint(.green)
                        }
                }
            }
            .navigationTitle("Task Manager")
            .toolbar {
                ToolbarItem(placement: .navigationBarLeading) {
                    Menu {
                        Button("Settings") {
                            modalManager.activeModal = .settings
                        }
                        Button("Filters") {
                            modalManager.activeModal = .filters
                        }
                    } label: {
                        Image(systemName: "ellipsis.circle")
                    }
                }

                ToolbarItem(placement: .navigationBarTrailing) {
                    Button("Add Task") {
                        modalManager.activeModal = .addTask
                    }
                }
            }
        }

        // Sheets
        .sheet(item: $modalManager.activeModal) { modal in
            switch modal {
            case .addTask:
                TaskFormView(onSave: { newTask in
                    tasks.append(newTask)
                    modalManager.activeModal = nil
                }, onCancel: {
                    modalManager.activeModal = nil
                })

            case .settings:
                SettingsView(isPresented: .init(
                    get: { modalManager.activeModal == .settings },
                    set: { if !$0 { modalManager.activeModal = nil } }
                ))

            case .filters:
                FilterView(isPresented: .init(
                    get: { modalManager.activeModal == .filters },
                    set: { if !$0 { modalManager.activeModal = nil } }
                ))
            }
        }

        .sheet(item: $selectedTask) { task in
            NavigationView {
                TaskFormView(task: task, onSave: { updatedTask in
                    if let index = tasks.firstIndex(where: { $0.id == updatedTask.id }) {
                        tasks[index] = updatedTask
                    }
                    selectedTask = nil
                }, onCancel: {
                    selectedTask = nil
                })
                .navigationTitle("Edit Task")
            }
        }

        // Full Screen Cover
        .fullScreenCover(item: $selectedTaskForDetail) { task in
            NavigationView {
                TaskDetailView(task: task) {
                    selectedTaskForDetail = nil
                }
                .navigationTitle("Task Details")
                .navigationBarItems(trailing: Button("Done") {
                    selectedTaskForDetail = nil
                })
            }
        }

        // Alerts
        .alert("Delete Task", isPresented: $showDeleteAlert) {
            Button("Delete", role: .destructive) {
                if let task = taskToDelete {
                    deleteTask(task)
                }
            }
            Button("Cancel", role: .cancel) {
                taskToDelete = nil
            }
        } message: {
            Text("Are you sure you want to delete this task? This action cannot be undone.")
        }

        .alert("Rename Task", isPresented: $showRenameAlert) {
            TextField("New name", text: $newTaskName)
            Button("Rename") {
                if let task = taskToRename {
                    renameTask(task, to: newTaskName)
                }
            }
            Button("Cancel", role: .cancel) {
                taskToRename = nil
                newTaskName = ""
            }
        } message: {
            Text("Enter a new name for this task")
        }

        .alert("Task Completed!", isPresented: $showCompletionAlert) {
            Button("Great!") {
                if let task = taskToComplete {
                    markTaskComplete(task)
                }
            }
            Button("Not Yet", role: .cancel) {
                taskToComplete = nil
            }
        } message: {
            Text("Congratulations on completing your task! ðŸŽ‰")
        }
    }

    // Task Management Functions
    private func deleteTask(_ task: Task) {
        tasks.removeAll { $0.id == task.id }
        taskToDelete = nil
        // Haptic feedback
        let impact = UIImpactFeedbackGenerator(style: .heavy)
        impact.impactOccurred()
    }

    private func renameTask(_ task: Task, to newName: String) {
        if let index = tasks.firstIndex(where: { $0.id == task.id }) {
            tasks[index].title = newName
        }
        taskToRename = nil
        newTaskName = ""
    }

    private func markTaskComplete(_ task: Task) {
        if let index = tasks.firstIndex(where: { $0.id == task.id }) {
            tasks[index].isCompleted = true
        }
        taskToComplete = nil
        // Haptic feedback
        let notification = UINotificationFeedbackGenerator()
        notification.notificationOccurred(.success)
    }
}

// Supporting Views (TaskFormView, SettingsView, FilterView, TaskDetailView, TaskRow)
// ... Additional view implementations would go here ...

struct ContentView: View {
    var body: some View {
        TaskManagerView()
    }
}

