import SwiftUI

// MARK: - Models
struct Workout: Identifiable, Codable {
    let id = UUID()
    var name: String
    var type: WorkoutType
    var duration: Int // in minutes
    var calories: Int
    var date: Date = Date()
    var isCompleted: Bool = false
}

struct FitnessGoal: Identifiable, Codable {
    let id = UUID()
    var title: String
    var targetWorkouts: Int
    var currentWorkouts: Int = 0
    var targetCalories: Int
    var currentCalories: Int = 0
    var deadline: Date?
}

struct UserProfile: Codable {
    var name: String = "Fitness User"
    var weight: Double = 70.0
    var height: Double = 170.0
    var fitnessLevel: FitnessLevel = .beginner
}

enum WorkoutType: String, CaseIterable, Codable {
    case running, cycling, swimming, strength, yoga, other
}

enum FitnessLevel: String, CaseIterable, Codable {
    case beginner, intermediate, advanced
}

// MARK: - App State
class AppState: ObservableObject {
    @Published var workouts: [Workout] = []
    @Published var goals: [FitnessGoal] = []
    @Published var userProfile = UserProfile()
    @Published var selectedWorkoutType: WorkoutType? = nil

    // Computed properties
    var totalWorkouts: Int { workouts.count }
    var completedWorkouts: Int { workouts.filter { $0.isCompleted }.count }
    var totalCalories: Int { workouts.reduce(0) { $0 + $1.calories } }
    var totalMinutes: Int { workouts.reduce(0) { $0 + $1.duration } }

    var filteredWorkouts: [Workout] {
        if let type = selectedWorkoutType {
            return workouts.filter { $0.type == type }
        }
        return workouts
    }

    func addWorkout(_ workout: Workout) {
        workouts.append(workout)
        updateGoals()
    }

    func completeWorkout(_ workout: Workout) {
        if let index = workouts.firstIndex(where: { $0.id == workout.id }) {
            workouts[index].isCompleted = true
            updateGoals()
        }
    }

    private func updateGoals() {
        for i in goals.indices {
            goals[i].currentWorkouts = completedWorkouts
            goals[i].currentCalories = totalCalories
        }
    }
}

// MARK: - Main App
@main
struct FitnessTrackerApp: App {
    @StateObject private var appState = AppState()

    var body: some Scene {
        WindowGroup {
            MainTabView()
                .environmentObject(appState)
