// === DATA MODELS ===
struct Recipe: Identifiable, Codable {
    let id = UUID()
    let title: String
    let description: String
    let ingredients: [Ingredient]
    let instructions: [String]
    let prepTime: Int // in minutes
    let cookTime: Int // in minutes
    let difficulty: Difficulty
    let category: Category
    let imageName: String
    var isFavorite: Bool = false

    var totalTime: Int {
        return prepTime + cookTime
    }
}

struct Ingredient: Identifiable, Codable {
    let id = UUID()
    let name: String
    let quantity: String
    let unit: String?
}

enum Difficulty: String, CaseIterable, Codable {
    case easy = "Easy"
    case medium = "Medium" 
    case hard = "Hard"
}

enum Category: String, CaseIterable, Codable {
    case breakfast = "Breakfast"
    case lunch = "Lunch"
    case dinner = "Dinner"
    case dessert = "Dessert"
    case vegetarian = "Vegetarian"
}

// === VIEWMODEL ===
class RecipeManager: ObservableObject {
    @Published var recipes: [Recipe] = []
    @Published var favorites: [Recipe] = []
    @Published var selectedCategory: Category? = nil

    init() {
        loadSampleData()
        loadFavorites()
    }

    private func loadSampleData() {
        recipes = [
            Recipe(
                title: "Avocado Toast",
                description: "Simple and delicious breakfast",
                ingredients: [
                    Ingredient(name: "Bread", quantity: "2", unit: "slices"),
                    Ingredient(name: "Avocado", quantity: "1", unit: "large"),
                    Ingredient(name: "Lemon juice", quantity: "1", unit: "tbsp")
                ],
                instructions: [
                    "Toast the bread until golden brown",
                    "Mash avocado with lemon juice and salt",
                    "Spread avocado mixture on toast",
                    "Add optional toppings like red pepper flakes"
                ],
