import Foundation

// === NETWORK ERROR TYPES ===
enum NetworkError: Error {
    case invalidURL
    case invalidResponse
    case statusCode(Int)
    case decodingError
    case noInternetConnection
    case timeout
    case serverError(String)
}

// === DATA MODELS ===
struct User: Codable, Identifiable {
    let id: Int
    let name: String
    let email: String
    let website: String?
}

struct Post: Codable, Identifiable {
    let id: Int
    let userId: Int
    let title: String
    let body: String
}

struct CreateUserRequest: Codable {
    let name: String
    let email: String
    let website: String?
}

struct OAuthResponse: Codable {
    let accessToken: String
    let tokenType: String
    let expiresIn: Int
}

// === NETWORK SERVICE ===
class NetworkManager {
    private let baseURL = "https://jsonplaceholder.typicode.com"
    private let session: URLSession

    init(configuration: URLSessionConfiguration = .default) {
        self.session = URLSession(configuration: configuration)
    }

    // === BASIC GET REQUEST ===
    func fetchUsers() async throws -> [User] {
        guard let url = URL(string: "\(baseURL)/users") else {
            throw NetworkError.invalidURL
        }

        let (data, response) = try await session.data(from: url)

        guard let httpResponse = response as? HTTPURLResponse else {
            throw NetworkError.invalidResponse
        }

        guard httpResponse.statusCode == 200 else {
            throw NetworkError.statusCode(httpResponse.statusCode)
        }

        do {
            return try JSONDecoder().decode([User].self, from: data)
        } catch {
            throw NetworkError.decodingError
        }
    }

    // === GET WITH QUERY PARAMETERS ===
    func fetchPosts(for userId: Int) async throws -> [Post] {
        var components = URLComponents(string: "\(baseURL)/posts")!
        components.queryItems = [
            URLQueryItem(name: "userId", value: "\(userId)")
        ]

        guard let url = components.url else {
            throw NetworkError.invalidURL
        }

        let (data, response) = try await session.data(from: url)

        guard let httpResponse = response as? HTTPURLResponse,
              httpResponse.statusCode == 200 else {
            throw NetworkError.invalidResponse
        }

        return try JSONDecoder().decode([Post].self, from: data)
    }

    // === POST REQUEST ===
    func createUser(_ userRequest: CreateUserRequest) async throws -> User {
        guard let url = URL(string: "\(baseURL)/users") else {
            throw NetworkError.invalidURL
        }

        var request = URLRequest(url: url)
        request.httpMethod = "POST"
        request.setValue("application/json", forHTTPHeaderField: "Content-Type")

        let encoder = JSONEncoder()
        request.httpBody = try encoder.encode(userRequest)

        let (data, response) = try await session.data(for: request)

        guard let httpResponse = response as? HTTPURLResponse else {
            throw NetworkError.invalidResponse
        }

        guard (200...299).contains(httpResponse.statusCode) else {
            throw NetworkError.statusCode(httpResponse.statusCode)
        }

        do {
            return try JSONDecoder().decode(User.self, from: data)
        } catch {
            throw NetworkError.decodingError
        }
    }

    // === REQUEST WITH RETRY LOGIC ===
    func fetchWithRetry<T: Decodable>(
        url: URL,
        maxRetries: Int = 3
    ) async throws -> T {
        for attempt in 1...maxRetries {
            do {
                let (data, response) = try await session.data(from: url)

                guard let httpResponse = response as? HTTPURLResponse else {
                    throw NetworkError.invalidResponse
                }

                switch httpResponse.statusCode {
                case 200...299:
                    return try JSONDecoder().decode(T.self, from: data)
                case 400...499:
                    throw NetworkError.statusCode(httpResponse.statusCode)
                case 500...599:
                    if attempt < maxRetries {
                        // Exponential backoff
                        let delay = UInt64(2_000_000_000 * attempt)
                        try await Task.sleep(nanoseconds: delay)
                        continue
                    } else {
                        throw NetworkError.serverError("Server error: \(httpResponse.statusCode)")
                    }
                default:
                    throw NetworkError.invalidResponse
                }
            } catch {
                if attempt == maxRetries {
                    throw error
                }
                let delay = UInt64(2_000_000_000 * attempt)
                try await Task.sleep(nanoseconds: delay)
            }
        }
        throw NetworkError.timeout
    }

    // === DOWNLOAD TASK ===
    func downloadImage(from url: URL) async throws -> Data {
        let (tempURL, response) = try await session.download(from: url)

        guard let httpResponse = response as? HTTPURLResponse,
              httpResponse.statusCode == 200 else {
            throw NetworkError.downloadFailed
        }

        return try Data(contentsOf: tempURL)
    }
}

// === USAGE EXAMPLES ===
class UserService {
    private let networkManager = NetworkManager()

    func loadAllUsers() async {
        do {
            let users = try await networkManager.fetchUsers()
            print("Loaded \(users.count) users")

