import Foundation
import SwiftUI

// === PART 1: Data Models ===
struct WeatherResponse: Codable {
    let coord: Coordinates
    let weather: [WeatherCondition]
    let main: MainWeather
    let visibility: Int
    let wind: Wind
    let clouds: Clouds
    let dt: TimeInterval
    let sys: System
    let timezone: Int
    let id: Int
    let name: String
    let cod: Int
}

struct ForecastResponse: Codable {
    let list: [ForecastItem]
    let city: City
}

struct Coordinates: Codable {
    let lon: Double
    let lat: Double
}

struct WeatherCondition: Codable {
    let id: Int
    let main: String
    let description: String
    let icon: String
}

struct MainWeather: Codable {
    let temp: Double
    let feelsLike: Double
    let tempMin: Double
    let tempMax: Double
    let pressure: Int
    let humidity: Int
    let seaLevel: Int?
    let grndLevel: Int?

    enum CodingKeys: String, CodingKey {
        case temp, pressure, humidity
        case feelsLike = "feels_like"
        case tempMin = "temp_min"
        case tempMax = "temp_max"
        case seaLevel = "sea_level"
        case grndLevel = "grnd_level"
    }
}

struct Wind: Codable {
    let speed: Double
    let deg: Int
    let gust: Double?
}

struct Clouds: Codable {
    let all: Int
}

struct System: Codable {
    let type: Int?
    let id: Int?
    let country: String
    let sunrise: TimeInterval
    let sunset: TimeInterval
}

struct ForecastItem: Codable {
    let dt: TimeInterval
    let main: MainWeather
    let weather: [WeatherCondition]
    let clouds: Clouds
    let wind: Wind
    let visibility: Int
    let pop: Double
    let dtTxt: String

    enum CodingKeys: String, CodingKey {
        case dt, main, weather, clouds, wind, visibility, pop
        case dtTxt = "dt_txt"
    }
}

struct City: Codable {
    let id: Int
    let name: String
    let coord: Coordinates
    let country: String
    let population: Int
    let timezone: Int
    let sunrise: TimeInterval
    let sunset: TimeInterval
}

// === PART 2: Network Error Handling ===
enum WeatherError: Error {
    case invalidURL
    case invalidResponse
    case statusCode(Int)
    case decodingError
    case locationUnavailable
    case apiKeyMissing
    case networkUnavailable
}

// === PART 3: Weather Service ===
class WeatherService {
    private let apiKey: String
    private let baseURL = "https://api.openweathermap.org/data/2.5"
    private let session: URLSession

    init(apiKey: String, session: URLSession = .shared) {
        self.apiKey = apiKey
        self.session = session
    }

    func fetchCurrentWeather(city: String, units: String = "metric") async throws -> WeatherResponse {
        guard let url = URL(string: "\(baseURL)/weather?q=\(city)&appid=\(apiKey)&units=\(units)") else {
            throw WeatherError.invalidURL
        }

        let (data, response) = try await session.data(from: url)

        guard let httpResponse = response as? HTTPURLResponse else {
            throw WeatherError.invalidResponse
        }

        guard httpResponse.statusCode == 200 else {
            throw WeatherError.statusCode(httpResponse.statusCode)
        }

        do {
            return try JSONDecoder().decode(WeatherResponse.self, from: data)
        } catch {
            throw WeatherError.decodingError
        }
    }

    func fetchFiveDayForecast(city: String, units: String = "metric") async throws -> ForecastResponse {
        guard let url = URL(string: "\(baseURL)/forecast?q=\(city)&appid=\(apiKey)&units=\(units)") else {
            throw WeatherError.invalidURL
        }

        let (data, response) = try await session.data(from: url)

        guard let httpResponse = response as? HTTPURLResponse else {
            throw WeatherError.invalidResponse
        }

        guard httpResponse.statusCode == 200 else {
            throw WeatherError.statusCode(httpResponse.statusCode)
        }

        do {
            return try JSONDecoder().decode(ForecastResponse.self, from: data)
        } catch {
            throw WeatherError.decodingError
        }
    }

    func fetchWithRetry<T: Decodable>(
        endpoint: String,
        parameters: [String: String],
        maxRetries: Int = 3
    ) async throws -> T {
        var components = URLComponents(string: "\(baseURL)/\(endpoint)")!
        var queryItems = parameters.map { URLQueryItem(name: $0.key, value: $0.value) }
        queryItems.append(URLQueryItem(name: "appid", value: apiKey))
        components.queryItems = queryItems

        guard let url = components.url else {
            throw WeatherError.invalidURL
        }

        for attempt in 1...maxRetries {
            do {
                let (data, response) = try await session.data(from: url)

                guard let httpResponse = response as? HTTPURLResponse else {
                    throw WeatherError.invalidResponse
                }

                switch httpResponse.statusCode {
                case 200...299:
                    return try JSONDecoder().decode(T.self, from: data)
                case 400...499:
                    throw WeatherError.statusCode(httpResponse.statusCode)
                case 500...599:
                    if attempt < maxRetries {
                        try await Task.sleep(nanoseconds: 2_000_000_000 * UInt64(attempt))
                        continue
                    } else {
                        throw WeatherError.statusCode(httpResponse.statusCode)
                    }
                default:
                    throw WeatherError.invalidResponse
                }
            } catch {
                if attempt == maxRetries {
                    throw error
                }
                try await Task.sleep(nanoseconds: 2_000_000_000 * UInt64(attempt))
            }
        }
        throw WeatherError.networkUnavailable
    }
}

// === PART 4: SwiftUI Views ===
struct WeatherView: View {
    @StateObject private var viewModel = WeatherViewModel()

    var body: some View {
        NavigationView {
            ScrollView {
                VStack(spacing: 20) {
                    if let weather = viewModel.currentWeather {
                        CurrentWeatherView(weather: weather)
                    }

                    if let forecast = viewModel.forecast {
                        ForecastView(forecast: forecast)
                    }

                    if viewModel.isLoading {
                        ProgressView("Loading weather...")
                    }

                    if let error = viewModel.error {
                        ErrorView(error: error, retryAction: {
                            Task { await viewModel.loadWeather() }
                        })
                    }
                }
                .padding()
            }
            .navigationTitle("Weather")
            .refreshable {
                await viewModel.loadWeather()
            }
        }
        .task {
            await viewModel.loadWeather()
        }
    }
}

@MainActor
class WeatherViewModel: ObservableObject {
    @Published var currentWeather: WeatherResponse?
    @Published var forecast: ForecastResponse?
    @Published var isLoading = false
    @Published var error: WeatherError?

    private let service = WeatherService(apiKey: "YOUR_API_KEY")

    func loadWeather() async {
        isLoading = true
        error = nil

        do {
            async let current = service.fetchCurrentWeather(city: "London")
            async let forecast = service.fetchFiveDayForecast(city: "London")

            let (currentResult, forecastResult) = try await (current, forecast)

            self.currentWeather = currentResult
            self.forecast = forecastResult
        } catch {
            self.error = error as? WeatherError
        }

        isLoading = false
    }
}

