        return images
    }
}

// === CONTINUATIONS ===
func legacyNetworkRequest() async throws -> Data {
    return try await withCheckedThrowingContinuation { continuation in
        OldNetworkManager.shared.requestData { result in
            switch result {
            case .success(let data):
                continuation.resume(returning: data)
            case .failure(let error):
                continuation.resume(throwing: error)
            }
        }
    }
}

// === ACTOR FOR THREAD SAFETY ===
actor BankAccount {
    private var balance: Double = 0
    private let accountNumber: String

    init(accountNumber: String, initialBalance: Double) {
        self.accountNumber = accountNumber
        self.balance = initialBalance
    }

    func deposit(amount: Double) {
        balance += amount
    }

    func withdraw(amount: Double) throws {
        guard balance >= amount else {
            throw BankError.insufficientFunds
        }
        balance -= amount
    }

    func getBalance() -> Double {
        return balance
    }

    func transfer(amount: Double, to otherAccount: BankAccount) async throws {
        try await withdraw(amount: amount)
        await otherAccount.deposit(amount: amount)
    }
}

// === ADVANCED ERROR HANDLING ===
enum NetworkError: Error {
    case invalidURL
    case timeout
    case unauthorized
    case serverError(Int)
}

func robustNetworkRequest() async {
    do {
        let data = try await fetchWithRetry()
        await processData(data)
    } catch NetworkError.timeout {
        await showTimeoutMessage()
    } catch NetworkError.unauthorized {
        await requestReauthentication()
    } catch {
        await showGenericError(error)
    }
}

func fetchWithRetry(maxAttempts: Int = 3) async throws -> Data {
    for attempt in 1...maxAttempts {
        do {
            return try await performNetworkRequest()
        } catch {
            if attempt == maxAttempts {
                throw error
            }
            // Exponential backoff
            let delay = UInt64(attempt * 2) * 1_000_000_000 // 2, 4, 6 seconds
            try await Task.sleep(nanoseconds: delay)
        }
    }
    throw NetworkError.timeout
}

// === REAL-WORLD EXAMPLE ===
