import Foundation

// Weather data models
struct WeatherData: Codable {
    let temperature: Double
    let condition: String
    let humidity: Int
}

struct Forecast: Codable {
    let daily: [DailyForecast]
}

struct AirQuality: Codable {
    let aqi: Int
    let pollutants: [String: Double]
}

struct DailyForecast: Codable {
    let date: Date
    let high: Double
    let low: Double
    let condition: String
}

struct CompleteWeather {
    let current: WeatherData
    let forecast: Forecast
    let airQuality: AirQuality
}

// Error types
enum WeatherError: Error {
    case invalidCity
    case networkError
    case parsingError
    case rateLimitExceeded
}

// === PART 1: Basic Async/Await ===
class WeatherService {

    func fetchCurrentWeather(city: String) async throws -> WeatherData {
        // Simulate network request
        try await Task.sleep(nanoseconds: 1_000_000_000) // 1 second

        guard !city.isEmpty else {
            throw WeatherError.invalidCity
        }

        // Simulate API response
        return WeatherData(
            temperature: 22.5,
            condition: "Sunny",
            humidity: 65
        )
    }

    func fetchForecast(city: String) async throws -> Forecast {
        try await Task.sleep(nanoseconds: 1_500_000_000) // 1.5 seconds

        let dailyForecasts = (1...5).map { day in
            DailyForecast(
                date: Calendar.current.date(byAdding: .day, value: day, to: Date())!,
                high: Double(20 + day * 2),
                low: Double(10 + day),
                condition: ["Sunny", "Cloudy", "Rainy"].randomElement()!
            )
        }

        return Forecast(daily: dailyForecasts)
    }

    func fetchAirQuality(city: String) async throws -> AirQuality {
        try await Task.sleep(nanoseconds: 800_000_000) // 0.8 seconds

        return AirQuality(
            aqi: Int.random(in: 1...100),
            pollutants: ["PM2.5": 12.5, "PM10": 20.0, "O3": 0.05]
        )
    }

    // Sequential implementation
    func fetchCompleteWeatherSequential(city: String) async throws -> CompleteWeather {
        let current = try await fetchCurrentWeather(city: city)
        let forecast = try await fetchForecast(city: city)
        let airQuality = try await fetchAirQuality(city: city)

        return CompleteWeather(
            current: current,
            forecast: forecast,
            airQuality: airQuality
        )
    }
}

// === PART 2: Parallel Execution ===
extension WeatherService {

    // Parallel implementation
    func fetchCompleteWeatherParallel(city: String) async throws -> CompleteWeather {
        async let current = fetchCurrentWeather(city: city)
        async let forecast = fetchForecast(city: city)
        async let airQuality = fetchAirQuality(city: city)

        return try await CompleteWeather(
            current: current,
            forecast: forecast,
            airQuality: airQuality
        )
    }

    // Multiple cities with task group
    func fetchWeatherForCities(_ cities: [String]) async throws -> [String: CompleteWeather] {
        try await withThrowingTaskGroup(of: (String, CompleteWeather).self) { group in
            var results: [String: CompleteWeather] = [:]

            for city in cities {
                group.addTask {
                    let weather = try await self.fetchCompleteWeatherParallel(city: city)
                    return (city, weather)
                }
            }

            for try await (city, weather) in group {
                results[city] = weather
            }

            return results
        }
    }

    // Cancellable version
    func fetchWeatherWithCancellation(city: String) async throws -> CompleteWeather {
        try await withTaskCancellationHandler {
            try await fetchCompleteWeatherParallel(city: city)
        } onCancel: {
            print("Weather fetch cancelled for \(city)")
        }
    }
}

// === PART 3: Advanced Patterns ===
actor WeatherCache {
    private var cache: [String: (CompleteWeather, Date)] = [:]
    private let cacheDuration: TimeInterval = 300 // 5 minutes

    func getWeather(for city: String) -> CompleteWeather? {
        guard let (weather, timestamp) = cache[city] else {
            return nil
        }

        // Check if cache is still valid
        if Date().timeIntervalSince(timestamp) < cacheDuration {
            return weather
        } else {
            cache.removeValue(forKey: city)
            return nil
        }
    }

    func setWeather(_ weather: CompleteWeather, for city: String) {
        cache[city] = (weather, Date())
    }

    func clear() {
        cache.removeAll()
    }
}

actor RateLimitedWeatherService {
    private let baseService = WeatherService()
    private var lastRequestTime: Date = .distantPast
    private let minInterval: TimeInterval = 1.0 // 1 second between requests

    func fetchWeather(city: String) async throws -> CompleteWeather {
        let now = Date()
        let timeSinceLast = now.timeIntervalSince(lastRequestTime)

        if timeSinceLast < minInterval {
            let delay = UInt64((minInterval - timeSinceLast) * 1_000_000_000)
            try await Task.sleep(nanoseconds: delay)
        }

        lastRequestTime = Date()
        return try await baseService.fetchCompleteWeatherParallel(city: city)
    }
}

// Continuation for legacy API
class LegacyWeatherAPI {
    func fetchWeather(city: String, completion: @escaping (Result<WeatherData, Error>) -> Void) {
        DispatchQueue.global().asyncAfter(deadline: .now() + 1.0) {
            completion(.success(WeatherData(temperature: 22.0, condition: "Sunny", humidity: 60)))
        }
    }
}

extension LegacyWeatherAPI {
    func fetchWeather(city: String) async throws -> WeatherData {
        return try await withCheckedThrowingContinuation { continuation in
            fetchWeather(city: city) { result in
                switch result {
                case .success(let weather):
                    continuation.resume(returning: weather)
                case .failure(let error):
                    continuation.resume(throwing: error)
                }
            }
        }
    }
}

// === PART 4: Real-World Application ===
class WeatherDashboard {
    private let service = WeatherService()
    private let cache = WeatherCache()

    func compareCities(_ cities: [String]) async throws -> [String: CompleteWeather] {
        // Try cache first
        var results: [String: CompleteWeather] = [:]

        for city in cities {
            if let cached = await cache.getWeather(for: city) {
                results[city] = cached
            }
        }

        // Fetch missing cities
        let missingCities = cities.filter { results[$0] == nil }
        if !missingCities.isEmpty {
            let fetched = try await service.fetchWeatherForCities(missingCities)

            // Update cache
            for (city, weather) in fetched {
                await cache.setWeather(weather, for: city)
                results[city] = weather
            }
        }

        return results
    }

    func fetchWithRetry(city: String, maxAttempts: Int = 3) async throws -> CompleteWeather {
        for attempt in 1...maxAttempts {
            do {
                return try await service.fetchCompleteWeatherParallel(city: city)
            } catch {
                if attempt == maxAttempts {
                    throw error
                }

                let delay = UInt64(attempt * 2) * 1_000_000_000
                try await Task.sleep(nanoseconds: delay)
                print("Retry attempt \(attempt) for \(city)")
            }
        }
        throw WeatherError.networkError
    }
}

// === PART 5: Error Handling ===
class WeatherErrorHandler {

    func handleWeatherError(_ error: Error) async -> String {
        switch error {
        case WeatherError.invalidCity:
            return "Please enter a valid city name"
        case WeatherError.networkError:
            return "Network connection failed. Please check your internet"
        case WeatherError.rateLimitExceeded:
            return "Too many requests. Please wait a moment"
        case WeatherError.parsingError:
            return "Failed to process weather data"
        default:
            return "An unexpected error occurred"
        }
    }

    func fetchWeatherWithFallback(city: String) async -> CompleteWeather? {
        do {
            return try await WeatherService().fetchCompleteWeatherParallel(city: city)
        } catch {
            // Try fallback source
            return await fetchFromBackupSource(city: city)
        }
    }

    private func fetchFromBackupSource(city: String) async -> CompleteWeather? {
        // Implement backup weather source
        return nil
    }
}

