// === CUSTOM DECODING WITH ERROR HANDLING ===
struct Order: Codable {
    let id: String
    let customerId: Int
    let items: [OrderItem]
    let totalAmount: Double
    let status: OrderStatus
    let createdAt: Date

    enum OrderStatus: String, Codable {
        case pending, confirmed, shipped, delivered, cancelled
    }

    struct OrderItem: Codable {
        let productId: Int
        let quantity: Int
        let unitPrice: Double

        var totalPrice: Double {
            return Double(quantity) * unitPrice
        }
    }

    // Custom initializer for complex validation
    init(from decoder: Decoder) throws {
        let container = try decoder.container(keyedBy: CodingKeys.self)

        id = try container.decode(String.self, forKey: .id)
        customerId = try container.decode(Int.self, forKey: .customerId)
        items = try container.decode([OrderItem].self, forKey: .items)
        totalAmount = try container.decode(Double.self, forKey: .totalAmount)
        status = try container.decode(OrderStatus.self, forKey: .status)

        // Custom date parsing
        let dateString = try container.decode(String.self, forKey: .createdAt)
        let formatter = ISO8601DateFormatter()
        formatter.formatOptions = [.withInternetDateTime, .withFractionalSeconds]

        if let date = formatter.date(from: dateString) {
            createdAt = date
        } else {
            throw DecodingError.dataCorruptedError(
                forKey: .createdAt,
                in: container,
                debugDescription: "Invalid date format"
            )
        }

        // Validate total amount matches items
        let calculatedTotal = items.reduce(0) { $0 + $1.totalPrice }
        guard abs(calculatedTotal - totalAmount) < 0.01 else {
            throw DecodingError.dataCorruptedError(
                forKey: .totalAmount,
                in: container,
                debugDescription: "Total amount doesn't match items total"
            )
        }
    }
}

// === PRACTICAL USAGE EXAMPLES ===
class JSONManager {
    static let shared = JSONManager()

    private let decoder: JSONDecoder
    private let encoder: JSONEncoder

    private init() {
        decoder = JSONDecoder()
        decoder.keyDecodingStrategy = .convertFromSnakeCase
        decoder.dateDecodingStrategy = .iso8601

        encoder = JSONEncoder()
        encoder.keyEncodingStrategy = .convertToSnakeCase
        encoder.dateEncodingStrategy = .iso8601
        encoder.outputFormatting = .prettyPrinted
    }

    // Generic decoding method
    func decode<T: Codable>(_ type: T.Type, from data: Data) throws -> T {
        return try decoder.decode(type, from: data)
    }

    // Generic encoding method
    func encode<T: Codable>(_ value: T) throws -> Data {
        return try encoder.encode(value)
    }

    // Safe decoding with error handling
    func safeDecode<T: Codable>(_ type: T.Type, from data: Data) -> Result<T, Error> {
        do {
            let decoded = try decoder.decode(type, from: data)
            return .success(decoded)
        } catch {
            return .failure(error)
        }
    }
}

