import Foundation

// === PART 1: UserDefaults Manager ===
class SettingsManager {
    private let defaults = UserDefaults.standard

    // User settings keys
    private enum SettingKeys {
        static let username = "username"
        static let readingTheme = "readingTheme"
        static let fontSize = "fontSize"
        static let readingProgress = "readingProgress"
        static let booksMetadata = "booksMetadata"
    }

    // Save settings
    func saveUsername(_ username: String) {
        defaults.set(username, forKey: SettingKeys.username)
    }

    func saveReadingTheme(_ theme: String) {
        defaults.set(theme, forKey: SettingKeys.readingTheme)
    }

    func saveFontSize(_ size: Int) {
        defaults.set(size, forKey: SettingKeys.fontSize)
    }

    func saveReadingProgress(_ progress: Double) {
        defaults.set(progress, forKey: SettingKeys.readingProgress)
    }

    // Load settings
    func loadUsername() -> String {
        return defaults.string(forKey: SettingKeys.username) ?? "Reader"
    }

    func loadReadingTheme() -> String {
        return defaults.string(forKey: SettingKeys.readingTheme) ?? "light"
    }

    func loadFontSize() -> Int {
        return defaults.integer(forKey: SettingKeys.fontSize)
    }

    func loadReadingProgress() -> Double {
        return defaults.double(forKey: SettingKeys.readingProgress)
    }

    // Reset all settings
    func resetAllSettings() {
        let dictionary = defaults.dictionaryRepresentation()
        dictionary.keys.forEach { key in
            defaults.removeObject(forKey: key)
        }
    }
}

// === PART 2: File System Operations ===
class BookFileManager {
    private let fileManager = FileManager.default

    var booksDirectoryURL: URL {
        let documents = fileManager.urls(for: .documentDirectory, in: .userDomainMask).first!
        return documents.appendingPathComponent("Books")
    }

    var backupDirectoryURL: URL {
        let documents = fileManager.urls(for: .documentDirectory, in: .userDomainMask).first!
        return documents.appendingPathComponent("Backups")
    }

    init() {
        createDirectoriesIfNeeded()
    }

    private func createDirectoriesIfNeeded() {
        try? fileManager.createDirectory(at: booksDirectoryURL, withIntermediateDirectories: true)
        try? fileManager.createDirectory(at: backupDirectoryURL, withIntermediateDirectories: true)
    }

    // Save book content to file
    func saveBookContent(_ content: String, forBook book: Book) throws {
        let fileURL = booksDirectoryURL.appendingPathComponent("\(book.id).txt")
        try content.write(to: fileURL, atomically: true, encoding: .utf8)
    }

    // Load book content from file
    func loadBookContent(forBook book: Book) throws -> String {
        let fileURL = booksDirectoryURL.appendingPathComponent("\(book.id).txt")
        return try String(contentsOf: fileURL, encoding: .utf8)
    }

    // List all saved books
    func listAllBooks() -> [String] {
        guard let files = try? fileManager.contentsOfDirectory(atPath: booksDirectoryURL.path) else {
            return []
        }
        return files.filter { $0.hasSuffix(".txt") }
    }

    // Delete specific book
    func deleteBook(_ book: Book) throws {
        let fileURL = booksDirectoryURL.appendingPathComponent("\(book.id).txt")
        if fileManager.fileExists(atPath: fileURL.path) {
            try fileManager.removeItem(at: fileURL)
        }
    }

    // Get file size
    func getFileSize(forBook book: Book) -> Int? {
        let fileURL = booksDirectoryURL.appendingPathComponent("\(book.id).txt")
        guard let attributes = try? fileManager.attributesOfItem(atPath: fileURL.path),
              let size = attributes[.size] as? Int else {
            return nil
        }
        return size
    }
}

// === PART 3: Codable Integration ===
struct Book: Codable, Identifiable {
    let id: UUID
    var title: String
    var author: String
    var content: String
    var lastReadPosition: Int
    var isFavorite: Bool
    var dateAdded: Date

    init(title: String, author: String, content: String = "") {
        self.id = UUID()
        self.title = title
        self.author = author
        self.content = content
        self.lastReadPosition = 0
        self.isFavorite = false
        self.dateAdded = Date()
    }
}

class BookDataManager {
    private let defaults = UserDefaults.standard
    private let fileManager = BookFileManager()

    private let booksKey = "savedBooks"

    // Save books metadata
    func saveBooks(_ books: [Book]) {
        if let encoded = try? JSONEncoder().encode(books) {
            defaults.set(encoded, forKey: booksKey)
        }
    }

    // Load books metadata
    func loadBooks() -> [Book] {
        guard let data = defaults.data(forKey: booksKey),
              let books = try? JSONDecoder().decode([Book].self, from: data) else {
            return []
        }
        return books
    }

    // Save book with content
    func saveBookWithContent(_ book: Book) throws {
        var currentBooks = loadBooks()

        // Update or add book
        if let index = currentBooks.firstIndex(where: { $0.id == book.id }) {
            currentBooks[index] = book
        } else {
            currentBooks.append(book)
        }

        saveBooks(currentBooks)
        try fileManager.saveBookContent(book.content, forBook: book)
    }

    // Load book with content
    func loadBookWithContent(_ book: Book) throws -> Book {
        var loadedBook = book
        let content = try fileManager.loadBookContent(forBook: book)
        loadedBook.content = content
        return loadedBook
    }
}

// === PART 4: Advanced Features ===
extension BookFileManager {
    // Create backup
    func createBackup() throws {
        let books = listAllBooks()
        let timestamp = Date().timeIntervalSince1970
        let backupFolder = backupDirectoryURL.appendingPathComponent("backup_\(Int(timestamp))")

        try fileManager.createDirectory(at: backupFolder, withIntermediateDirectories: true)

        for bookFile in books {
            let sourceURL = booksDirectoryURL.appendingPathComponent(bookFile)
            let destURL = backupFolder.appendingPathComponent(bookFile)
            try fileManager.copyItem(at: sourceURL, to: destURL)
        }
    }

    // Get storage statistics
    func getStorageStatistics() -> (bookCount: Int, totalSize: Int) {
        let books = listAllBooks()
        var totalSize = 0

        for bookFile in books {
            let fileURL = booksDirectoryURL.appendingPathComponent(bookFile)
            if let attributes = try? fileManager.attributesOfItem(atPath: fileURL.path),
               let size = attributes[.size] as? Int {
                totalSize += size
            }
        }

        return (books.count, totalSize)
    }
}

// === PART 5: Error Handling ===
enum StorageError: Error, LocalizedError {
    case fileNotFound
    case insufficientSpace
    case invalidData
    case backupFailed

    var errorDescription: String? {
        switch self {
        case .fileNotFound:
            return "The requested file was not found."
        case .insufficientSpace:
            return "There is not enough space to complete the operation."
        case .invalidData:
            return "The data is invalid or corrupted."
        case .backupFailed:
            return "Backup operation failed."
        }
    }
}

