import SwiftUI

// === 1. BASIC ANIMATABLE TYPES ===
struct AnimatedColor: Animatable {
    var red: Double
    var green: Double
    var blue: Double

    var color: Color {
        Color(red: red, green: green, blue: blue)
    }

    var animatableData: AnimatablePair<Double, AnimatablePair<Double, Double>> {
        get {
            AnimatablePair(red, AnimatablePair(green, blue))
        }
        set {
            red = newValue.first
            green = newValue.second.first
            blue = newValue.second.second
        }
    }
}

// === 2. GEOMETRY EFFECT EXAMPLES ===
struct WaveEffect: GeometryEffect {
    var phase: CGFloat
    var strength: CGFloat
    var frequency: CGFloat

    var animatableData: CGFloat {
        get { phase }
        set { phase = newValue }
    }

    func effectValue(size: CGSize) -> ProjectionTransform {
        // Create wave distortion
        let transform = CGAffineTransform(
            translationX: sin(phase * frequency) * strength,
            y: 0
        )
        return ProjectionTransform(transform)
    }
}

struct MorphEffect: GeometryEffect {
    var progress: CGFloat
    var controlPoints: [CGPoint]

    var animatableData: CGFloat {
        get { progress }
        set { progress = newValue }
    }

    func effectValue(size: CGSize) -> ProjectionTransform {
        // BÃ©zier curve morphing
        let t = progress
        let oneMinusT = 1 - t

        // Quadratic BÃ©zier interpolation
        let x = oneMinusT * oneMinusT * controlPoints[0].x +
                2 * oneMinusT * t * controlPoints[1].x +
                t * t * controlPoints[2].x

        let y = oneMinusT * oneMinusT * controlPoints[0].y +
                2 * oneMinusT * t * controlPoints[1].y +
                t * t * controlPoints[2].y

        return ProjectionTransform(CGAffineTransform(translationX: x, y: y))
    }
}

// === 3. ADVANCED 3D TRANSFORMS ===
struct CardFlipEffect: GeometryEffect {
    var rotation: Double
    var perspective: CGFloat = 0.001

    var animatableData: Double {
        get { rotation }
        set { rotation = newValue }
    }

    func effectValue(size: CGSize) -> ProjectionTransform {
        var transform = CATransform3DIdentity
        transform.m34 = perspective

        // Rotate around Y-axis for card flip
        transform = CATransform3DRotate(transform, rotation, 0, 1, 0)

        return ProjectionTransform(transform)
    }
}

// === 4. COMPLEX ANIMATABLE MODIFIER ===
struct ParticleEffect: AnimatableModifier {
    var emissionRate: CGFloat
    var particleSize: CGFloat
    var spread: CGFloat

    var animatableData: AnimatablePair<CGFloat, AnimatablePair<CGFloat, CGFloat>> {
        get {
            AnimatablePair(emissionRate, AnimatablePair(particleSize, spread))
        }
        set {
            emissionRate = newValue.first
            particleSize = newValue.second.first
            spread = newValue.second.second
        }
    }

    func body(content: Content) -> some View {
        content
            .overlay(
                ParticleOverlay(
                    emissionRate: emissionRate,
                    particleSize: particleSize,
                    spread: spread
                )
            )
    }
}

struct ParticleOverlay: View {
    let emissionRate: CGFloat
    let particleSize: CGFloat
    let spread: CGFloat

    var body: some View {
        GeometryReader { geometry in
            ForEach(0..<Int(emissionRate * 10), id: \.self) { index in
                Circle()
                    .fill(Color.blue)
                    .frame(width: particleSize, height: particleSize)
                    .offset(
                        x: CGFloat.random(in: -spread...spread) * geometry.size.width,
                        y: CGFloat.random(in: -spread...spread) * geometry.size.height
                    )
                    .opacity(Double.random(in: 0.2...0.8))
            }
