import SwiftUI

// === PART 1: Animatable Custom Types ===
struct AnimatedGradient: Animatable {
    var startColor: Color
    var endColor: Color
    var progress: CGFloat

    var animatableData: AnimatablePair<CGFloat, AnimatablePair<Double, Double>> {
        get {
            // Convert colors to animatable data (simplified)
            let startValues = colorToValues(startColor)
            let endValues = colorToValues(endColor)
            return AnimatablePair(progress, AnimatablePair(startValues, endValues))
        }
        set {
            progress = newValue.first
            // In real implementation, you'd convert back to colors
        }
    }

    private func colorToValues(_ color: Color) -> Double {
        // Simplified - in real app, extract RGB components
        return 0.5
    }
}

// === PART 2: GeometryEffect Transformations ===
struct BounceEffect: GeometryEffect {
    var amplitude: CGFloat
    var frequency: CGFloat
    var time: CGFloat

    var animatableData: AnimatablePair<CGFloat, AnimatablePair<CGFloat, CGFloat>> {
        get { AnimatablePair(amplitude, AnimatablePair(frequency, time)) }
        set {
            amplitude = newValue.first
            frequency = newValue.second.first
            time = newValue.second.second
        }
    }

    func effectValue(size: CGSize) -> ProjectionTransform {
        let bounce = amplitude * sin(time * frequency * 2 * .pi)
        let decay = amplitude * exp(-time * 0.5) // Damping
        let offset = bounce * decay

        return ProjectionTransform(CGAffineTransform(translationX: 0, y: offset))
    }
}

struct WobbleEffect: GeometryEffect {
    var intensity: CGFloat
    var speed: CGFloat
    var time: CGFloat

    var animatableData: AnimatablePair<CGFloat, AnimatablePair<CGFloat, CGFloat>> {
        get { AnimatablePair(intensity, AnimatablePair(speed, time)) }
        set {
            intensity = newValue.first
            speed = newValue.second.first
            time = newValue.second.second
        }
    }

    func effectValue(size: CGSize) -> ProjectionTransform {
        let rotation = intensity * sin(time * speed * 2 * .pi) * 0.1
        let transform = CGAffineTransform(rotationAngle: rotation)
        return ProjectionTransform(transform)
    }
}

struct LiquidEffect: GeometryEffect {
    var phase: CGFloat
    var amplitude: CGFloat
    var frequency: CGFloat

    var animatableData: AnimatablePair<CGFloat, AnimatablePair<CGFloat, CGFloat>> {
        get { AnimatablePair(phase, AnimatablePair(amplitude, frequency)) }
        set {
            phase = newValue.first
            amplitude = newValue.second.first
            frequency = newValue.second.second
        }
    }

    func effectValue(size: CGSize) -> ProjectionTransform {
        let distortion = amplitude * sin(phase * frequency)
        // Complex liquid distortion would use more advanced math
        let transform = CGAffineTransform(
            a: 1 + distortion * 0.1, b: distortion * 0.05,
            c: distortion * 0.05, d: 1 + distortion * 0.1,
            tx: 0, ty: 0
        )
        return ProjectionTransform(transform)
    }
}

// === PART 3: Advanced 3D Animations ===
struct CubeRotationEffect: GeometryEffect {
    var rotation: Double
    var perspective: CGFloat = 0.001

    var animatableData: Double {
        get { rotation }
        set { rotation = newValue }
    }

    func effectValue(size: CGSize) -> ProjectionTransform {
        var transform = CATransform3DIdentity
        transform.m34 = perspective

        // Rotate on multiple axes for cube-like effect
        transform = CATransform3DRotate(transform, rotation, 1, 1, 0)

        return ProjectionTransform(transform)
    }
}

struct PageCurlEffect: GeometryEffect {
    var progress: CGFloat
    var radius: CGFloat = 100

    var animatableData: CGFloat {
        get { progress }
        set { progress = newValue }
    }

    func effectValue(size: CGSize) -> ProjectionTransform {
        // Simplified page curl - real implementation would be more complex
        let angle = progress * .pi / 2
        let curvature = sin(angle) * radius

        var transform = CATransform3DIdentity
        transform.m34 = -0.002
        transform = CATransform3DTranslate(transform, curvature, 0, 0)
        transform = CATransform3DRotate(transform, -angle, 0, 1, 0)

        return ProjectionTransform(transform)
    }
}

// === PART 4: Complex AnimatableModifier ===
struct SparkleEffect: AnimatableModifier {
    var intensity: CGFloat
    var speed: CGFloat

    var animatableData: AnimatablePair<CGFloat, CGFloat> {
        get { AnimatablePair(intensity, speed) }
        set {
            intensity = newValue.first
            speed = newValue.second
        }
    }

    func body(content: Content) -> some View {
        content.overlay(
            SparkleOverlay(intensity: intensity, speed: speed)
        )
    }
}

struct SparkleOverlay: View {
    let intensity: CGFloat
    let speed: CGFloat

    var body: some View {
        GeometryReader { geometry in
            ForEach(0..<Int(intensity * 5), id: \.self) { index in
                Circle()
                    .fill(Color.white)
                    .frame(width: 3, height: 3)
                    .position(
                        x: CGFloat.random(in: 0...geometry.size.width),
                        y: CGFloat.random(in: 0...geometry.size.height)
                    )
                    .opacity(Double.random(in: 0.3...0.9))
                    .scaleEffect(CGFloat.random(in: 0.5...1.5))
            }
        }
    }
}

// === PART 5: Demo View ===
struct CustomAnimationsDemo: View {
    @State private var isAnimating = false
    @State private var bounceTime: CGFloat = 0
    @State private var wobbleTime: CGFloat = 0
    @State private var liquidPhase: CGFloat = 0
    @State private var cubeRotation: Double = 0
    @State private var pageCurlProgress: CGFloat = 0
    @State private var sparkleIntensity: CGFloat = 0

    var body: some View {
        VStack(spacing: 40) {
            Text("Custom Animations Demo")
                .font(.title)
                .bold()

            // Bounce Effect
            Rectangle()
                .fill(Color.blue)
                .frame(width: 80, height: 80)
                .modifier(BounceEffect(amplitude: 20, frequency: 2, time: bounceTime))

            // Wobble Effect
            Circle()
                .fill(Color.green)
                .frame(width: 80, height: 80)
                .modifier(WobbleEffect(intensity: 1, speed: 2, time: wobbleTime))

            // Cube Rotation
            Rectangle()
                .fill(Color.orange)
                .frame(width: 80, height: 80)
                .modifier(CubeRotationEffect(rotation: cubeRotation))

            // Sparkle Effect
            Text("Sparkle")
                .font(.title2)
                .modifier(SparkleEffect(intensity: sparkleIntensity, speed: 2))

            Button("Toggle Animation") {
                withAnimation(.spring(response: 0.6, dampingFraction: 0.8)) {
                    isAnimating.toggle()

                    if isAnimating {
                        // Start all animations
                        withAnimation(Animation.easeInOut(duration: 1).repeatForever(autoreverses: true)) {
                            bounceTime = 1
                            wobbleTime = 1
                            liquidPhase = 2 * .pi
                        }

                        withAnimation(Animation.linear(duration: 4).repeatForever(autoreverses: false)) {
                            cubeRotation = 2 * .pi
                        }

                        sparkleIntensity = 3
                    } else {
                        // Stop animations
                        bounceTime = 0
                        wobbleTime = 0
                        liquidPhase = 0
                        cubeRotation = 0
                        sparkleIntensity = 0
                    }
                }
            }
            .padding()
            .background(Color.blue)
            .foregroundColor(.white)
            .cornerRadius(10)
        }
        .padding()
    }
}

