import SwiftUI

// === BASIC PATH OPERATIONS ===
struct SimpleTriangle: Shape {
    func path(in rect: CGRect) -> Path {
        var path = Path()
        path.move(to: CGPoint(x: rect.midX, y: rect.minY))
        path.addLine(to: CGPoint(x: rect.minX, y: rect.maxY))
        path.addLine(to: CGPoint(x: rect.maxX, y: rect.maxY))
        path.closeSubpath()
        return path
    }
}

// === ADVANCED SHAPE: STAR ===
struct Star: Shape {
    var points: Int = 5
    var smoothness: Double = 0.5

    func path(in rect: CGRect) -> Path {
        var path = Path()

        let center = CGPoint(x: rect.width / 2, y: rect.height / 2)
        let outerRadius = min(rect.width, rect.height) / 2
        let innerRadius = outerRadius * smoothness

        for i in 0..<points * 2 {
            let angle = .pi * Double(i) / Double(points)
            let radius = i.isMultiple(of: 2) ? outerRadius : innerRadius

            let point = CGPoint(
                x: center.x + CGFloat(cos(angle)) * radius,
                y: center.y + CGFloat(sin(angle)) * radius
            )

            if i == 0 {
                path.move(to: point)
            } else {
                path.addLine(to: point)
            }
        }

        path.closeSubpath()
        return path
    }
}

// === ANIMATABLE SHAPE ===
struct AnimatableWave: Shape {
    var strength: Double
    var frequency: Double
    var phase: Double

    var animatableData: AnimatablePair<Double, AnimatablePair<Double, Double>> {
        get { AnimatablePair(strength, AnimatablePair(frequency, phase)) }
        set {
            strength = newValue.first
            frequency = newValue.second.first
            phase = newValue.second.second
        }
    }

    func path(in rect: CGRect) -> Path {
        let path = Path { path in
            let width = Double(rect.width)
            let height = Double(rect.height)

            path.move(to: CGPoint(x: 0, y: height))

            for x in stride(from: 0, through: width, by: 1) {
                let relativeX = x / width
                let y = strength * sin(frequency * relativeX + phase) + height / 2
                path.addLine(to: CGPoint(x: x, y: y))
            }

            path.addLine(to: CGPoint(x: width, y: height))
            path.closeSubpath()
        }

        return path
    }
}

