import SwiftUI

// === PART 1: Basic Custom Shapes ===
struct Hexagon: Shape {
    func path(in rect: CGRect) -> Path {
        var path = Path()
        let center = CGPoint(x: rect.midX, y: rect.midY)
        let radius = min(rect.width, rect.height) / 2

        for i in 0..<6 {
            let angle = Double(i) * .pi / 3
            let point = CGPoint(
                x: center.x + CGFloat(cos(angle)) * radius,
                y: center.y + CGFloat(sin(angle)) * radius
            )

            if i == 0 {
                path.move(to: point)
            } else {
                path.addLine(to: point)
            }
        }

        path.closeSubpath()
        return path
    }
}

struct SpeechBubble: Shape {
    var tailSize: CGFloat = 20

    func path(in rect: CGRect) -> Path {
        var path = Path()

        // Main bubble (rounded rect)
        let bubbleRect = CGRect(
            x: rect.minX,
            y: rect.minY,
            width: rect.width,
            height: rect.height - tailSize
        )

        path.addRoundedRect(
            in: bubbleRect,
            cornerSize: CGSize(width: 15, height: 15)
        )

        // Tail (triangle)
        let tailStart = CGPoint(x: rect.midX - tailSize, y: rect.maxY - tailSize)
        let tailTip = CGPoint(x: rect.midX, y: rect.maxY)
        let tailEnd = CGPoint(x: rect.midX + tailSize, y: rect.maxY - tailSize)

        path.move(to: tailStart)
        path.addLine(to: tailTip)
        path.addLine(to: tailEnd)
        path.closeSubpath()

        return path
    }
}

struct Gear: Shape {
    var teeth: Int = 8
    var toothDepth: Double = 0.3

    func path(in rect: CGRect) -> Path {
        var path = Path()
        let center = CGPoint(x: rect.midX, y: rect.midY)
        let outerRadius = min(rect.width, rect.height) / 2
        let innerRadius = outerRadius * 0.6

        for i in 0..<teeth {
            let angle = Double(i) * 2 * .pi / Double(teeth)
            let nextAngle = Double(i + 1) * 2 * .pi / Double(teeth)

            // Outer point (tooth tip)
            let outerPoint = CGPoint(
                x: center.x + CGFloat(cos(angle)) * outerRadius,
                y: center.y + CGFloat(sin(angle)) * outerRadius
            )

            // Inner point (between teeth)
            let midAngle = angle + .pi / Double(teeth)
            let innerPoint = CGPoint(
                x: center.x + CGFloat(cos(midAngle)) * innerRadius,
                y: center.y + CGFloat(sin(midAngle)) * innerRadius
            )

            if i == 0 {
                path.move(to: outerPoint)
            } else {
                path.addLine(to: outerPoint)
            }

            path.addLine(to: innerPoint)
        }

        path.closeSubpath()
        return path
    }
}

// === PART 2: Animatable Shapes ===
struct AnimatableGear: Shape {
    var teeth: Double

    var animatableData: Double {
        get { teeth }
        set { teeth = newValue }
    }

    func path(in rect: CGRect) -> Path {
        var path = Path()
        let center = CGPoint(x: rect.midX, y: rect.midY)
        let outerRadius = min(rect.width, rect.height) / 2
        let innerRadius = outerRadius * 0.6
        let actualTeeth = Int(teeth)

        for i in 0..<actualTeeth {
            let angle = Double(i) * 2 * .pi / Double(actualTeeth)
            let nextAngle = Double(i + 1) * 2 * .pi / Double(actualTeeth)

            let outerPoint = CGPoint(
                x: center.x + CGFloat(cos(angle)) * outerRadius,
                y: center.y + CGFloat(sin(angle)) * outerRadius
            )

            let midAngle = angle + .pi / Double(actualTeeth)
            let innerPoint = CGPoint(
                x: center.x + CGFloat(cos(midAngle)) * innerRadius,
                y: center.y + CGFloat(sin(midAngle)) * innerRadius
            )

            if i == 0 {
                path.move(to: outerPoint)
            } else {
                path.addLine(to: outerPoint)
            }

            path.addLine(to: innerPoint)
        }

        path.closeSubpath()
        return path
    }
}

struct PulsingCircle: Shape {
    var pulsePhase: Double

    var animatableData: Double {
        get { pulsePhase }
        set { pulsePhase = newValue }
    }

    func path(in rect: CGRect) -> Path {
        let lineWidth = 10 + 5 * sin(pulsePhase * 2 * .pi)
        let radius = min(rect.width, rect.height) / 2 - lineWidth / 2

        return Path { path in
            path.addArc(
                center: CGPoint(x: rect.midX, y: rect.midY),
                radius: radius,
                startAngle: .degrees(0),
                endAngle: .degrees(360),
                clockwise: false
            )
        }
        .strokedPath(StrokeStyle(lineWidth: lineWidth, lineCap: .round))
    }
}

// === PART 3: Advanced Path Operations ===
struct Spiral: Shape {
    var revolutions: Double = 3.0

    func path(in rect: CGRect) -> Path {
        var path = Path()
        let center = CGPoint(x: rect.midX, y: rect.midY)
        let maxRadius = min(rect.width, rect.height) / 2

        let points = 100
        for i in 0...points {
            let progress = Double(i) / Double(points)
            let angle = progress * 2 * .pi * revolutions
            let radius = maxRadius * progress

            let point = CGPoint(
                x: center.x + CGFloat(cos(angle)) * radius,
                y: center.y + CGFloat(sin(angle)) * radius
            )

            if i == 0 {
                path.move(to: point)
            } else {
                path.addLine(to: point)
            }
        }

        return path
    }
}

// === PART 4: Gradient and Effects ===
struct GlassMorphismCard: View {
    var body: some View {
        RoundedRectangle(cornerRadius: 20)
            .fill(
                LinearGradient(
                    colors: [
                        .white.opacity(0.3),
                        .white.opacity(0.1),
                        .white.opacity(0.05)
                    ],
                    startPoint: .topLeading,
                    endPoint: .bottomTrailing
                )
            )
            .background(
                RoundedRectangle(cornerRadius: 20)
                    .fill(.ultraThinMaterial)
            )
            .overlay(
                RoundedRectangle(cornerRadius: 20)
                    .stroke(.white.opacity(0.2), lineWidth: 1)
            )
            .shadow(color: .black.opacity(0.1), radius: 10, x: 5, y: 5)
    }
}

// === PART 5: Interactive Drawing ===
struct DrawingCanvas: View {
    @State private var lines: [Line] = []
    @State private var deletedLines: [Line] = []
    @State private var currentLine = Line(points: [], color: .blue, lineWidth: 3)

    var body: some View {
        VStack {
            // Toolbar
            HStack {
                Button("Undo") {
                    if let lastLine = lines.popLast() {
                        deletedLines.append(lastLine)
                    }
                }
                .disabled(lines.isEmpty)

                Button("Redo") {
                    if let lastDeleted = deletedLines.popLast() {
                        lines.append(lastDeleted)
                    }
                }
                .disabled(deletedLines.isEmpty)

                Button("Clear") {
                    lines.removeAll()
                    deletedLines.removeAll()
                }
            }

            // Canvas
            Canvas { context, size in
                // Draw all saved lines
                for line in lines {
                    var path = Path()
                    path.addLines(line.points)

                    context.stroke(
                        path,
                        with: .color(line.color),
                        lineWidth: line.lineWidth
                    )
                }

                // Draw current line
                if !currentLine.points.isEmpty {
                    var path = Path()
                    path.addLines(currentLine.points)

                    context.stroke(
                        path,
                        with: .color(currentLine.color),
                        lineWidth: currentLine.lineWidth
                    )
                }
            }
            .gesture(
                DragGesture()
                    .onChanged { value in
                        let newPoint = value.location
                        currentLine.points.append(newPoint)
                    }
                    .onEnded { _ in
                        lines.append(currentLine)
                        currentLine = Line(points: [], color: currentLine.color, lineWidth: currentLine.lineWidth)
                        deletedLines.removeAll()
                    }
            )
            .frame(height: 300)
            .background(Color.white)
            .border(Color.gray)
        }
        .padding()
    }
}

struct Line {
    var points: [CGPoint]
    var color: Color
    var lineWidth: CGFloat
}

// === MAIN VIEW ===
struct DrawingChallengeView: View {
    @State private var gearTeeth: Double = 8
    @State private var pulsePhase: Double = 0

    var body: some View {
        ScrollView {
            VStack(spacing: 30) {
                // Basic shapes
                HStack(spacing: 20) {
                    Hexagon()
                        .fill(LinearGradient(colors: [.blue, .purple], startPoint: .top, endPoint: .bottom))
                        .frame(width: 80, height: 80)

                    SpeechBubble()
                        .fill(Color.green)
