                        withAnimation(.easeInOut(duration: 1).repeatForever(autoreverses: true)) {
                            pulsePhase = 1
                        }
                    }

                // Spiral
                Spiral(revolutions: 5)
                    .stroke(Color.purple, lineWidth: 2)
