import SwiftUI

// === BASIC GESTURE SETUP ===
struct BasicGesturesView: View {
    @State private var scale: CGFloat = 1.0
    @State private var angle: Angle = .zero
    @State private var offset: CGSize = .zero
    @State private var isTapped = false

    var body: some View {
        VStack(spacing: 30) {
            // Draggable Card
            RoundedRectangle(cornerRadius: 20)
                .fill(isTapped ? Color.blue : Color.red)
                .frame(width: 200, height: 150)
                .scaleEffect(scale)
                .rotationEffect(angle)
                .offset(offset)
                .gesture(createDragGesture())
                .simultaneousGesture(createTapGesture())

            // Rotatable Image
            Image(systemName: "star.fill")
                .font(.system(size: 60))
                .foregroundColor(.yellow)
                .rotationEffect(angle)
                .gesture(createRotationGesture())

            // Scalable Shape
            Circle()
                .fill(Color.green)
                .frame(width: 100, height: 100)
                .scaleEffect(scale)
                .gesture(createMagnificationGesture())

            // Reset Button
            Button("Reset") {
                withAnimation(.spring()) {
                    scale = 1.0
                    angle = .zero
                    offset = .zero
                    isTapped = false
                }
            }
        }
    }

    private func createTapGesture() -> some Gesture {
        TapGesture()
            .onEnded {
                withAnimation {
                    isTapped.toggle()
                }
            }
    }

    private func createDragGesture() -> some Gesture {
        DragGesture()
            .onChanged { value in
                offset = value.translation
            }
            .onEnded { value in
                // Add some spring physics to the end
                withAnimation(.interactiveSpring()) {
                    offset = value.predictedEndTranslation
                }
            }
    }

    private func createRotationGesture() -> some Gesture {
        RotationGesture()
            .onChanged { angle in
                self.angle = angle
            }
            .onEnded { angle in
                // Snap to nearest 45 degrees
                withAnimation {
                    let degrees = angle.degrees
                    let snapped = round(degrees / 45) * 45
                    self.angle = Angle(degrees: snapped)
                }
            }
    }

    private func createMagnificationGesture() -> some Gesture {
        MagnificationGesture()
            .onChanged { scale in
                self.scale = scale
            }
            .onEnded { scale in
                // Apply scale limits with bounce animation
                withAnimation(.spring()) {
                    self.scale = min(max(scale, 0.5), 3.0)
                }
            }
    }
}

// === ADVANCED GESTURE COMBINATIONS ===
struct AdvancedGesturesView: View {
    @State private var position: CGPoint = .zero
    @State private var finalScale: CGFloat = 1.0
    @State private var temporaryScale: CGFloat = 1.0
    @GestureState private var isLongPressing = false

    var body: some View {
        VStack {
            Text("Advanced Gesture Demo")
                .font(.title2)
                .padding()

            RoundedRectangle(cornerRadius: 25)
                .fill(isLongPressing ? Color.orange : Color.purple)
                .frame(width: 200, height: 200)
                .scaleEffect(finalScale * temporaryScale)
                .position(position)
                .gesture(createCombinedGesture())
                .gesture(createLongPressGesture())
                .overlay(
                    Text(isLongPressing ? "LONG PRESSING" : "Drag + Scale")
                        .foregroundColor(.white)
                        .fontWeight(.bold)
                )
        }
    }

    private func createCombinedGesture() -> some Gesture {
        SimultaneousGesture(
            DragGesture()
                .onChanged { value in
                    position = value.location
                },
            MagnificationGesture()
                .onChanged { scale in
                    temporaryScale = scale
                }
                .onEnded { scale in
                    finalScale *= scale
                    temporaryScale = 1.0
                    // Enforce scale limits
                    finalScale = min(max(finalScale, 0.3), 5.0)
                }
        )
    }

    private func createLongPressGesture() -> some Gesture {
        LongPressGesture(minimumDuration: 0.5)
            .updating($isLongPressing) { currentState, gestureState, transaction in
                gestureState = currentState
            }
    }
}

// === CUSTOM GESTURE RECOGNITION ===
enum SwipeDirection {
    case left, right, up, down, none
}

struct SwipeDetectionView: View {
    @State private var swipeDirection: SwipeDirection = .none
    @State private var swipeOffset: CGSize = .zero

    var body: some View {
        VStack {
            Text("Swipe Detection")
                .font(.headline)

            RoundedRectangle(cornerRadius: 15)
                .fill(swipeColor)
                .frame(width: 250, height: 150)
                .overlay(
                    Text(swipeDirectionText)
                        .foregroundColor(.white)
                        .fontWeight(.bold)
                )
                .offset(swipeOffset)
                .gesture(createSwipeGesture())
        }
    }

    private var swipeColor: Color {
        switch swipeDirection {
        case .left: return .red
        case .right: return .green
        case .up: return .blue
        case .down: return .orange
        case .none: return .gray
        }
    }

    private var swipeDirectionText: String {
        switch swipeDirection {
        case .left: return "SWIPE LEFT"
        case .right: return "SWIPE RIGHT"
        case .up: return "SWIPE UP"
        case .down: return "SWIPE DOWN"
        case .none: return "SWIPE ME"
        }
    }

    private func createSwipeGesture() -> some Gesture {
        DragGesture(minimumDistance: 20)
            .onChanged { value in
                swipeOffset = value.translation
                swipeDirection = detectSwipeDirection(value)
            }
            .onEnded { value in
                withAnimation(.spring()) {
                    swipeOffset = .zero
                    // Keep direction for a moment, then reset
                    DispatchQueue.main.asyncAfter(deadline: .now() + 0.5) {
                        swipeDirection = .none
                    }
                }
            }
    }

    private func detectSwipeDirection(_ value: DragGesture.Value) -> SwipeDirection {
        let horizontal = value.translation.width
        let vertical = value.translation.height

        if abs(horizontal) > abs(vertical) {
            return horizontal > 0 ? .right : .left
        } else {
            return vertical > 0 ? .down : .up
        }
    }
}

