import SwiftUI

struct PhotoEditorView: View {
    @State private var imagePosition: CGPoint = CGPoint(x: 200, y: 300)
    @State private var imageScale: CGFloat = 1.0
    @State private var imageRotation: Angle = .zero
    @State private var hasBorder = false
    @State private var showControls = false
    @State private var currentFilter = 0
    @State private var drawingPoints: [CGPoint] = []
    @State private var isDrawing = false

    let filters = ["Normal", "Sepia", "Black & White", "Vivid"]

    var body: some View {
        ZStack {
            Color.black.ignoresSafeArea()

            VStack {
                Text("Photo Editor")
                    .font(.title)
                    .foregroundColor(.white)

                Text("Filter: \(filters[currentFilter])")
                    .foregroundColor(.white)
                    .padding(.bottom)

                Spacer()

                // Image container with all gestures
                ZStack {
                    Rectangle()
                        .fill(Color.gray.opacity(0.3))
                        .border(hasBorder ? Color.white : Color.clear, width: 2)

                    Image(systemName: "photo")
                        .font(.system(size: 100))
                        .foregroundColor(.white)
                        .scaleEffect(imageScale)
                        .rotationEffect(imageRotation)
                        .position(imagePosition)
                        .gesture(createCombinedGesture())
                        .simultaneousGesture(createTapGestures())
                        .simultaneousGesture(createSwipeGesture())

                    // Drawing overlay
                    if isDrawing {
                        Path { path in
                            guard let firstPoint = drawingPoints.first else { return }
                            path.move(to: firstPoint)

                            for point in drawingPoints.dropFirst() {
                                path.addLine(to: point)
                            }
                        }
                        .stroke(Color.red, lineWidth: 3)
                    }
                }
                .frame(width: 350, height: 450)
                .gesture(createDrawGesture())

                Spacer()

                // Controls (shown based on gesture)
                if showControls {
                    HStack(spacing: 20) {
                        Button("Reset") {
                            resetAll()
                        }
                        Button("Rotate 90Â°") {
                            withAnimation {
                                imageRotation += Angle(degrees: 90)
                            }
                        }
                        Button("Scale 2x") {
                            withAnimation {
                                imageScale *= 2.0
                            }
                        }
                        Button(isDrawing ? "Stop Draw" : "Start Draw") {
                            isDrawing.toggle()
                            if !isDrawing {
                                drawingPoints.removeAll()
                            }
                        }
                    }
                    .foregroundColor(.white)
                    .padding()
                    .background(Color.white.opacity(0.2))
                    .cornerRadius(10)
                }
            }
        }
    }

    // PART 1: Basic Gestures
    private func createTapGestures() -> some Gesture {
        SimultaneousGesture(
            TapGesture()
                .onEnded {
                    withAnimation {
                        hasBorder.toggle()
                    }
                },
            TapGesture(count: 2)
                .onEnded {
                    resetPosition()
                }
        )
    }

    private func createCombinedGesture() -> some Gesture {
        SimultaneousGesture(
            DragGesture()
                .onChanged { value in
                    imagePosition = value.location
                },
            SimultaneousGesture(
                RotationGesture()
                    .onChanged { angle in
                        imageRotation = angle
                    },
                MagnificationGesture()
                    .onChanged { scale in
                        imageScale = scale
                    }
                    .onEnded { scale in
                        imageScale = min(max(scale, 0.5), 3.0)
                    }
            )
        )
    }

    // PART 2: Advanced Gestures
    private func createSwipeGesture() -> some Gesture {
        DragGesture(minimumDistance: 50)
            .onEnded { value in
                let horizontal = value.translation.width

                if horizontal > 0 {
                    // Swipe right - next filter
                    withAnimation {
                        currentFilter = (currentFilter + 1) % filters.count
                    }
                } else {
                    // Swipe left - previous filter
                    withAnimation {
                        currentFilter = (currentFilter - 1 + filters.count) % filters.count
                    }
                }

                // Haptic feedback
                let impact = UIImpactFeedbackGenerator(style: .medium)
                impact.impactOccurred()
            }
    }

    private func createDrawGesture() -> some Gesture {
        DragGesture(minimumDistance: 0)
            .onChanged { value in
                if isDrawing {
                    drawingPoints.append(value.location)
                }
            }
    }

    // PART 3: Utility Functions
    private func resetPosition() {
        withAnimation(.spring()) {
            imagePosition = CGPoint(x: 175, y: 225)
        }
    }

    private func resetAll() {
        withAnimation(.spring()) {
            imagePosition = CGPoint(x: 175, y: 225)
            imageScale = 1.0
            imageRotation = .zero
            hasBorder = false
            showControls = false
            drawingPoints.removeAll()
            isDrawing = false
        }
    }
}

