import SwiftUI

// === DATA MODELS ===
struct DrawingPath: Identifiable {
    let id = UUID()
    var points: [CGPoint] = []
    var color: Color = .black
    var lineWidth: CGFloat = 3.0
    var tool: DrawingTool = .pen
}

enum DrawingTool {
    case pen, eraser, highlighter, shape(ShapeType)
}

enum ShapeType {
    case rectangle, circle, triangle, star
}

// === CUSTOM SHAPES ===
struct Star: Shape {
    let points: Int = 5

    func path(in rect: CGRect) -> Path {
        var path = Path()
        let center = CGPoint(x: rect.midX, y: rect.midY)
        let radius = min(rect.width, rect.height) / 2

        for i in 0..<points * 2 {
            let angle = .pi * Double(i) / Double(points)
            let currentRadius = i.isMultiple(of: 2) ? radius : radius / 2
            let point = CGPoint(
                x: center.x + currentRadius * cos(angle),
                y: center.y + currentRadius * sin(angle)
            )

            if i == 0 {
                path.move(to: point)
            } else {
                path.addLine(to: point)
            }
        }
        path.closeSubpath()
        return path
    }
}

// === DRAWING CANVAS ===
struct DrawingCanvas: View {
    @Binding var paths: [DrawingPath]
    @Binding var currentTool: DrawingTool
    @Binding var currentColor: Color
    @Binding var lineWidth: CGFloat

    var body: some View {
        GeometryReader { geometry in
            ZStack {
                Color.white

                ForEach(paths) { path in
                    if path.tool == .pen || path.tool == .highlighter {
                        Path { pathBuilder in
                            guard let firstPoint = path.points.first else { return }
                            pathBuilder.move(to: firstPoint)

                            for point in path.points.dropFirst() {
                                pathBuilder.addLine(to: point)
                            }
                        }
                        .stroke(path.color, style: StrokeStyle(
                            lineWidth: path.lineWidth,
                            lineCap: .round,
                            lineJoin: .round
                        ))
                        .opacity(path.tool == .highlighter ? 0.5 : 1.0)
                    }
                }
            }
            .gesture(drawingGesture(in: geometry.size))
        }
    }

    private func drawingGesture(in size: CGSize) -> some Gesture {
        DragGesture(minimumDistance: 0)
            .onChanged { value in
                let point = value.location

                switch currentTool {
                case .pen, .highlighter:
                    if paths.isEmpty {
                        paths.append(DrawingPath(
                            color: currentColor,
                            lineWidth: lineWidth,
                            tool: currentTool
                        ))
                    }
                    if let lastIndex = paths.indices.last {
                        paths[lastIndex].points.append(point)
                    }

                case .eraser:
                    paths.removeAll { path in
                        path.points.contains { pathPoint in
                            let distance = hypot(pathPoint.x - point.x, pathPoint.y - point.y)
                            return distance < lineWidth * 2
                        }
                    }

                case .shape:
                    break
                }
            }
            .onEnded { _ in
                if currentTool == .pen || currentTool == .highlighter {
                    paths.append(DrawingPath(
                        color: currentColor,
                        lineWidth: lineWidth,
                        tool: currentTool
                    ))
                }
            }
    }
}

// === TOOLBAR ===
struct DrawingToolbar: View {
    @Binding var selectedTool: DrawingTool
    @Binding var selectedColor: Color
    @Binding var lineWidth: CGFloat

    let tools: [DrawingTool] = [.pen, .eraser, .highlighter]
    let colors: [Color] = [.black, .red, .blue, .green, .yellow, .purple, .orange, .pink]

    var body: some View {
        VStack(spacing: 16) {
            // Tools
            HStack {
                ForEach(tools, id: \.self) { tool in
                    Button {
                        selectedTool = tool
                    } label: {
                        Image(systemName: iconName(for: tool))
                            .font(.title2)
                            .foregroundColor(selectedTool == tool ? .white : .blue)
                            .padding(12)
                            .background(selectedTool == tool ? Color.blue : Color.clear)
                            .clipShape(Circle())
                            .overlay(
                                Circle()
                                    .stroke(Color.blue, lineWidth: 2)
                            )
                    }
                }
            }

            // Colors
            ScrollView(.horizontal, showsIndicators: false) {
                HStack {
                    ForEach(colors, id: \.self) { color in
                        Button {
                            selectedColor = color
                        } label: {
                            Circle()
                                .fill(color)
                                .frame(width: 35, height: 35)
                                .overlay(
                                    Circle()
                                        .stroke(selectedColor == color ? Color.white : Color.clear, lineWidth: 3)
                                        .shadow(radius: 2)
                                )
                        }
                    }
                }
                .padding(.horizontal)
            }

            // Line width
            VStack {
                Text("Line Width: \(Int(lineWidth))")
                    .font(.headline)
                Slider(value: $lineWidth, in: 1...20, step: 1)
                    .padding(.horizontal)
            }
        }
        .padding()
        .background(Color.gray.opacity(0.1))
        .cornerRadius(12)
    }

    private func iconName(for tool: DrawingTool) -> String {
        switch tool {
        case .pen: return "pencil"
        case .eraser: return "eraser.fill"
        case .highlighter: return "highlighter"
        case .shape: return "square"
        }
    }
}

// === MAIN APP ===
struct DrawingApp: View {
    @State private var paths: [DrawingPath] = []
    @State private var currentTool: DrawingTool = .pen
    @State private var currentColor: Color = .black
    @State private var lineWidth: CGFloat = 3.0
    @State private var history: [[DrawingPath]] = []

    var body: some View {
        VStack(spacing: 0) {
            // Header
            HStack {
                Text("Drawing App")
                    .font(.title2)
                    .fontWeight(.bold)

                Spacer()

                // Undo button
                Button("Undo") {
                    undo()
                }
                .disabled(history.isEmpty)
                .foregroundColor(history.isEmpty ? .gray : .blue)

                // Clear button
                Button("Clear") {
                    saveState()
                    paths.removeAll()
                }
                .foregroundColor(.red)
            }
            .padding()
            .background(Color.white)

            // Drawing canvas
            DrawingCanvas(
                paths: $paths,
                currentTool: $currentTool,
                currentColor: $currentColor,
                lineWidth: $lineWidth
            )
            .onChange(of: paths) { _ in
                if paths.count % 10 == 0 { // Save state periodically
                    saveState()
                }
            }

            // Toolbar
            DrawingToolbar(
                selectedTool: $currentTool,
                selectedColor: $currentColor,
                lineWidth: $lineWidth
            )
        }
        .background(Color.gray.opacity(0.2))
    }

    private func saveState() {
        history.append(paths)
        if history.count > 50 { // Limit history size
            history.removeFirst()
        }
    }

    private func undo() {
        guard !history.isEmpty else { return }
        paths = history.removeLast()
    }
}

