// === SERVICE PROTOCOLS ===
protocol NetworkServiceProtocol {
    func request<T: Decodable>(_ endpoint: Endpoint) async throws -> T
}

protocol UserServiceProtocol {
    func fetchUsers() async throws -> [User]
    func fetchUser(byId id: UUID) async throws -> User
    func createUser(_ user: User) async throws -> User
}

protocol AnalyticsServiceProtocol {
    func trackEvent(_ event: String, parameters: [String: Any]?)
}

// === SERVICE IMPLEMENTATIONS ===
class NetworkService: NetworkServiceProtocol {
    private let baseURL: URL
    private let session: URLSession

    init(baseURL: URL, session: URLSession = .shared) {
        self.baseURL = baseURL
        self.session = session
    }

    func request<T: Decodable>(_ endpoint: Endpoint) async throws -> T {
        let url = baseURL.appendingPathComponent(endpoint.path)
        var request = URLRequest(url: url)
        request.httpMethod = endpoint.method.rawValue

        let (data, response) = try await session.data(for: request)

        guard let httpResponse = response as? HTTPURLResponse,
              (200...299).contains(httpResponse.statusCode) else {
            throw NetworkError.invalidResponse
        }

        return try JSONDecoder().decode(T.self, from: data)
    }
}

class UserService: UserServiceProtocol {
    private let networkService: NetworkServiceProtocol

    init(networkService: NetworkServiceProtocol) {
        self.networkService = networkService
    }

    func fetchUsers() async throws -> [User] {
        return try await networkService.request(Endpoint.users)
    }

    func fetchUser(byId id: UUID) async throws -> User {
        return try await networkService.request(Endpoint.user(id: id))
    }

    func createUser(_ user: User) async throws -> User {
        // Implementation for creating user
        return user
    }
}

// === DEPENDENCY CONTAINER ===
class DependencyContainer {
    private var factories: [String: () -> Any] = [:]

    func register<T>(_ type: T.Type, _ factory: @escaping () -> T) {
        let key = String(describing: type)
        factories[key] = factory
    }

    func resolve<T>() -> T {
        let key = String(describing: T.self)
        guard let factory = factories[key]?() as? T else {
            fatalError("No factory registered for \(key)")
        }
        return factory
    }
}

// === ADVANCED VIEWMODEL ===
@MainActor
class UserListViewModel: ObservableObject {
    @Published var users: [User] = []
    @Published var isLoading = false
    @Published var error: String?

    private let userService: UserServiceProtocol
    private let analyticsService: AnalyticsServiceProtocol

    init(userService: UserServiceProtocol, analyticsService: AnalyticsServiceProtocol) {
        self.userService = userService
        self.analyticsService = analyticsService
    }

    func loadUsers() async {
        isLoading = true
        error = nil
        analyticsService.trackEvent("user_list_load_started")

        do {
            users = try await userService.fetchUsers()
            analyticsService.trackEvent("user_list_load_success", parameters: ["count": users.count])
        } catch {
            self.error = error.localizedDescription
            analyticsService.trackEvent("user_list_load_failed", parameters: ["error": error.localizedDescription])
        }

        isLoading = false
    }

    func deleteUser(at offsets: IndexSet) {
        offsets.forEach { index in
            let user = users[index]
            users.remove(at: index)
            analyticsService.trackEvent("user_deleted", parameters: ["user_id": user.id.uuidString])
        }
    }
}

// === USAGE IN SWIFTUI ===
struct UserListView: View {
    @StateObject private var viewModel: UserListViewModel

    init(viewModel: UserListViewModel) {
        _viewModel = StateObject(wrappedValue: viewModel)
    }

    var body: some View {
        NavigationView {
            Group {
                if viewModel.isLoading {
                    ProgressView("Loading users...")
                } else if let error = viewModel.error {
                    ErrorView(error: error, retryAction: {
                        Task { await viewModel.loadUsers() }
                    })
                } else {
