    func createTask(_ task: Task) async throws -> Task
    func updateTask(_ task: Task) async throws -> Task
    func deleteTask(id: UUID) async throws
}

class NetworkTaskService: TaskServiceProtocol {
    private let networkService: NetworkServiceProtocol

    init(networkService: NetworkServiceProtocol) {
        self.networkService = networkService
    }

    func fetchTasks() async throws -> [Task] {
        return try await networkService.request(Endpoint.tasks)
    }

    func createTask(_ task: Task) async throws -> Task {
        return try await networkService.request(Endpoint.createTask(task))
    }

    func updateTask(_ task: Task) async throws -> Task {
        return try await networkService.request(Endpoint.updateTask(task))
    }

    func deleteTask(id: UUID) async throws {
        let _: EmptyResponse = try await networkService.request(Endpoint.deleteTask(id: id))
    }
}

// === PART 2: Dependency Injection ===
class DependencyContainer {
    private var factories: [String: () -> Any] = [:]

    func register<T>(_ type: T.Type, _ factory: @escaping () -> T) {
        let key = String(describing: type)
        factories[key] = factory
    }

    func resolve<T>() -> T {
        let key = String(describing: T.self)
        guard let factory = factories[key]?() as? T else {
            fatalError("No dependency registered for \(key)")
        }
        return factory
    }
}

// === PART 3: Advanced ViewModel ===
@MainActor
class TaskListViewModel: ObservableObject {
    @Published var tasks: [Task] = []
    @Published var isLoading = false
    @Published var error: String?

    private let taskService: TaskServiceProtocol
    private let analyticsService: AnalyticsServiceProtocol

    init(taskService: TaskServiceProtocol, analyticsService: AnalyticsServiceProtocol) {
        self.taskService = taskService
        self.analyticsService = analyticsService
    }

    func loadTasks() async {
        isLoading = true
        error = nil

        do {
            tasks = try await taskService.fetchTasks()
            analyticsService.trackEvent("tasks_loaded", parameters: ["count": tasks.count])
        } catch {
            self.error = error.localizedDescription
            analyticsService.trackEvent("tasks_load_failed", parameters: ["error": error.localizedDescription])
        }

        isLoading = false
    }

    func createTask(title: String, description: String) async {
        let newTask = Task(id: UUID(), title: title, description: description, isCompleted: false)

        do {
            let createdTask = try await taskService.createTask(newTask)
            tasks.append(createdTask)
            analyticsService.trackEvent("task_created", parameters: ["task_id": createdTask.id.uuidString])
        } catch {
            self.error = "Failed to create task: \(error.localizedDescription)"
        }
    }
}

// === PART 4: Coordinator Pattern ===
class AppCoordinator: ObservableObject {
    @Published var path = NavigationPath()
    private let container: DependencyContainer

    init(container: DependencyContainer) {
        self.container = container
    }

    func showTaskDetail(_ task: Task) {
        path.append(AppRoute.taskDetail(task))
    }

    func showCreateTask() {
        path.append(AppRoute.createTask)
    }
}

// === PART 5: Testing ===
class TaskListViewModelTests: XCTestCase {
    var viewModel: TaskListViewModel!
    var mockTaskService: MockTaskService!
    var mockAnalytics: MockAnalyticsService!

    override func setUp() {
        mockTaskService = MockTaskService()
        mockAnalytics = MockAnalyticsService()
        viewModel = TaskListViewModel(taskService: mockTaskService, analyticsService: mockAnalytics)
    }

    func testLoadTasksSuccess() async {
        // Given
        let expectedTasks = [Task(id: UUID(), title: "Test", description: "Test", isCompleted: false)]
        mockTaskService.tasksToReturn = expectedTasks

        // When
        await viewModel.loadTasks()

        // Then
        XCTAssertEqual(viewModel.tasks, expectedTasks)
        XCTAssertFalse(viewModel.isLoading)
        XCTAssertNil(viewModel.error)
    }
}

