class GameManager: ObservableObject {
    @Published var score: Int = 0
    @Published var multiplier: Double = 1.0
    @Published var isPowerUpActive: Bool = false
    @Published var playerName: String = ""

    private var cancellables = Set<AnyCancellable>()

    init() {
        setupGameLogic()
    }

    private func setupGameLogic() {
        // Transform score with multiplier
        $score
            .combineLatest($multiplier)
            .map { score, multiplier in
                return Int(Double(score) * multiplier)
            }
            .sink { [weak self] multipliedScore in
                print("Display score: " + String(multipliedScore))
