            .filter { $0 == true }
            .flatMap { _ in
                Timer.publish(every: 1.0, on: .main, in: .common)
                    .autoconnect()
                    .prefix(10) // Only take 10 values (10 seconds)
            }
            .sink { [weak self] _ in
                self?.score += 5
            }
            .store(in: &cancellables)

        // Input validation
        $playerName
            .debounce(for: .milliseconds(500), scheduler: RunLoop.main)
            .removeDuplicates()
            .map { name in
                return name.count >= 3 && name.count <= 15
            }
            .sink { isValid in
                print("Name valid: " + String(isValid))
            }
            .store(in: &cancellables)
    }

    func addScore(_ points: Int) {
        score += points
    }

    func activatePowerUp() {
        multiplier = 2.0
        isPowerUpActive = true

        // Auto-deactivate after 10 seconds
        DispatchQueue.main.asyncAfter(deadline: .now() + 10) {
            self.multiplier = 1.0
