        guard let url = URL(string: baseURL + "/leaderboard/top") else {
            return Fail(error: URLError(.badURL))
                .eraseToAnyPublisher()
        }

        return URLSession.shared.dataTaskPublisher(for: url)
            .map(\.data)
            .decode(type: [HighScore].self, decoder: JSONDecoder())
            .catch { error in
                // Return empty array on error
                return Just([])
                    .setFailureType(to: Error.self)
            }
            .eraseToAnyPublisher()
    }

    func submitScore(_ score: Int, player: String) -> AnyPublisher<Bool, Error> {
        guard let url = URL(string: baseURL + "/scores") else {
            return Fail(error: URLError(.badURL))
                .eraseToAnyPublisher()
        }

        var request = URLRequest(url: url)
        request.httpMethod = "POST"
        request.setValue("application/json", forHTTPHeaderField: "Content-Type")

        let scoreData = ["player": player, "score": score] as [String: Any]
        request.httpBody = try? JSONSerialization.data(withJSONObject: scoreData)

        return URLSession.shared.dataTaskPublisher(for: request)
            .map { response in
                return (response.response as? HTTPURLResponse)?.statusCode == 200
            }
            .mapError { $0 as Error }
            .eraseToAnyPublisher()
    }
}

