import Combine
import Foundation

// === PART 1: Basic Publishers & Subscribers ===
class Player {
    @Published var health: Int = 100
    @Published var score: Int = 0
    @Published var level: Int = 1
    @Published var name: String = ""
}

class GameManager: ObservableObject {
    @Published var players: [Player] = []
    private var cancellables = Set<AnyCancellable>()

    let gameEvent = PassthroughSubject<GameEvent, Never>()

    enum GameEvent {
        case playerJoined(Player)
        case scoreChanged(Int, Player)
        case levelUp(Player)
        case gameOver
    }

    init() {
        setupSubscribers()
    }

    private func setupSubscribers() {
        // React to score changes
        $players
            .flatMap { players in
                players.publisher
                    .flatMap { player in
                        player.$score.map { (player, $0) }
                    }
            }
            .sink { [weak self] player, newScore in
                self?.gameEvent.send(.scoreChanged(newScore, player))

                if newScore >= player.level * 1000 {
                    player.level += 1
                    self?.gameEvent.send(.levelUp(player))
                }
            }
            .store(in: &cancellables)
    }
}

// === PART 2: Operator Chains ===
class ComboSystem {
    @Published var score: Int = 0
    @Published var multiplier: Double = 1.0
    private var cancellables = Set<AnyCancellable>()

    let actionPerformed = PassthroughSubject<Void, Never>()

    init() {
        setupComboSystem()
    }

    private func setupComboSystem() {
        // Combo system: multiplier increases with rapid actions, resets after inactivity
        actionPerformed
            .map { _ in 1 }
            .scan(0) { $0 + $1 }
            .combineLatest(
                actionPerformed
                    .debounce(for: .seconds(3), scheduler: RunLoop.main)
                    .map { _ in 0 }
            )
            .map { actionCount, _ in
                return min(Double(actionCount) * 0.1 + 1.0, 3.0)
            }
            .assign(to: &$multiplier)

        // Apply multiplier to score
        $multiplier
            .sink { [weak self] multiplier in
                print("Current multiplier: " + String(multiplier) + "x")
            }
            .store(in: &cancellables)
    }
}

// === PART 3: Error Handling & Networking ===
class RobustLeaderboardService {
    private let baseURL = "https://api.game.com"

    func fetchLeaderboard() -> AnyPublisher<[LeaderboardEntry], Error> {
        guard let url = URL(string: baseURL + "/leaderboard") else {
            return Fail(error: URLError(.badURL))
                .eraseToAnyPublisher()
        }

        return URLSession.shared.dataTaskPublisher(for: url)
            .tryMap { data, response in
                guard let httpResponse = response as? HTTPURLResponse,
                      httpResponse.statusCode == 200 else {
                    throw URLError(.badServerResponse)
                }
                return data
            }
            .decode(type: [LeaderboardEntry].self, decoder: JSONDecoder())
            .retry(2) // Retry twice on failure
            .catch { error in
                // Fallback to cached data
                return self.loadCachedLeaderboard()
                    .setFailureType(to: Error.self)
            }
            .eraseToAnyPublisher()
    }

    private func loadCachedLeaderboard() -> AnyPublisher<[LeaderboardEntry], Never> {
        // Simulate loading from cache
        return Just([])
            .eraseToAnyPublisher()
    }
}

struct LeaderboardEntry: Codable {
    let playerName: String
    let score: Int
    let rank: Int
}

// === PART 4: Advanced Patterns ===
class GameStateMachine {
    enum State {
        case idle, playing, paused, gameOver
    }

    let currentState = CurrentValueSubject<State, Never>(.idle)
    private var cancellables = Set<AnyCancellable>()

    let playerHealth = PassthroughSubject<Int, Never>()
    let gameEvents = PassthroughSubject<String, Never>()

    init() {
        setupStateTransitions()
    }

    private func setupStateTransitions() {
        // Transition to playing when health > 0
        playerHealth
            .combineLatest(currentState)
            .filter { $0.1 == .idle && $0.0 > 0 }
            .map { _ in State.playing }
            .subscribe(currentState)
            .store(in: &cancellables)

        // Game over when health reaches 0
        playerHealth
            .filter { $0 <= 0 }
            .map { _ in State.gameOver }
            .subscribe(currentState)
            .store(in: &cancellables)

        // Log state changes
        currentState
            .sink { state in
                print("Game state changed to: " + String(describing: state))
            }
            .store(in: &cancellables)
    }
}

// === PART 5: Performance & Testing ===
class OptimizedGameSystem {
    @Published var entities: [GameEntity] = []
    private var cancellables = Set<AnyCancellable>()

    init() {
        setupOptimizedProcessing()
    }

    private func setupOptimizedProcessing() {
        // Process entities with backpressure handling
        $entities
            .flatMap(maxPublishers: .max(1)) { entities in
                // Limit concurrent processing
                entities.publisher
            }
            .buffer(size: 50, prefetch: .keepFull, whenFull: .dropOldest)
            .sink { entity in
                // Process entity
                self.processEntity(entity)
            }
            .store(in: &cancellables)
    }

    private func processEntity(_ entity: GameEntity) {
        // Entity processing logic
    }
}

