class TaskListViewModel: ObservableObject {
    @Published var tasks: [Task] = []
    @Published var isLoading = false
    @Published var error: String?

    private let taskService: TaskServiceProtocol
    private let analytics: AnalyticsProtocol

    init(taskService: TaskServiceProtocol, analytics: AnalyticsProtocol) {
        self.taskService = taskService
        self.analytics = analytics
    }

    @MainActor
    func loadTasks() async {
        isLoading = true
        error = nil
        analytics.trackEvent("tasks_loading", parameters: nil)

        do {
            tasks = try await taskService.fetchTasks()
            analytics.trackEvent("tasks_loaded", parameters: ["count": tasks.count])
        } catch {
            self.error = error.localizedDescription
            analytics.trackEvent("tasks_load_failed", parameters: ["error": error.localizedDescription])
